// Watch for modelValue changes
watch(() => props.modelValue, async (newValue) => {
  console.log('[TutorialArrow] modelValue changed:', newValue);

  if (newValue && mounted.value) {
    await updatePosition();
    if (targetElement.value) {
      console.log('[TutorialArrow] Setting isShowing to true');
      isShowing.value = true;
      setupObservers();
    } else {
      // Target not found, retry up to 3 times with increasing delays
      console.log('[TutorialArrow] Target not found, retrying...');
      let retries = 0;
      const maxRetries = 3;

      const retryInterval = setInterval(async () => {
        retries++;
        await updatePosition();

        if (targetElement.value) {
          console.log('[TutorialArrow] Target found on retry', retries);
          clearInterval(retryInterval);
          isShowing.value = true;
          setupObservers();
        } else if (retries >= maxRetries) {
          console.error('[TutorialArrow] Failed to find target after', maxRetries, 'retries');
          clearInterval(retryInterval);
        }
      }, 300 * retries + 200); // Progressive backoff: 200ms, 500ms, 800ms
    }
  } else {
    isShowing.value = false;
  }
}, { immediate: true });

