    63→const arrowStyles = ref<CSSProperties>({});
    64→const autoHideTimer = ref<number | null>(null);
    65→const resizeObserver = ref<ResizeObserver | null>(null);
    66→
    67→const shouldBounce = computed(() => props.bounce && isShowing.value);
    68→
    69→/**
    70→ * Find the target element and calculate arrow position
    71→ */
    72→const updatePosition = async () => {
    73→  if (!props.targetSelector || !mounted.value) return;
    74→
    75→  await nextTick();
    76→
    77→  // Find the target element
    78→  const element = document.querySelector(props.targetSelector) as HTMLElement;
    79→
    80→  if (!element) {
    81→    console.warn(`[TutorialArrow] Target element not found: ${props.targetSelector}`);
    82→    console.warn(`[TutorialArrow] Total buttons on page:`, document.querySelectorAll('button').length);
    83→    console.warn(`[TutorialArrow] Dashed border buttons:`, document.querySelectorAll('button.border-dashed').length);
    84→    targetElement.value = null;
    85→    return;
    86→  }
    87→
    88→  console.log('[TutorialArrow] ✅ Target element found:', element);
    89→  targetElement.value = element;
    90→  const rect = element.getBoundingClientRect();
    91→
    92→  console.log('[TutorialArrow] Element position:', { top: rect.top, left: rect.left, width: rect.width });
    93→
    94→  // Position the arrow above the target element, centered horizontally
    95→  arrowStyles.value = {
    96→    position: 'fixed',
    97→    top: `${rect.top - props.verticalOffset}px`,
    98→    left: `${rect.left + (rect.width / 2)}px`,
    99→    transform: 'translateX(-50%)',
   100→    zIndex: 2000,
   101→    pointerEvents: 'none'
   102→  };
   103→
   104→  console.log('[TutorialArrow] Arrow styles applied:', arrowStyles.value);
   105→};
   106→
   107→/**
   108→ * Set up observers to track window resize and element changes
   109→ */
   110→const setupObservers = () => {
   111→  // Watch for window resize
   112→  window.addEventListener('resize', updatePosition);
   113→  window.addEventListener('scroll', updatePosition, true); // Capture phase for all scrolls
   114→
   115→  // Watch for element size changes
   116→  if (targetElement.value) {
   117→    resizeObserver.value = new ResizeObserver(updatePosition);
   118→    resizeObserver.value.observe(targetElement.value);
   119→  }
   120→};
   121→
   122→/**
   123→ * Clean up observers
   124→ */
   125→const cleanupObservers = () => {
   126→  window.removeEventListener('resize', updatePosition);
   127→  window.removeEventListener('scroll', updatePosition, true);
   128→
   129→  if (resizeObserver.value) {
   130→    resizeObserver.value.disconnect();
   131→    resizeObserver.value = null;
   132→  }
   133→};
   134→
   135→/**
   136→ * Start auto-hide timer
   137→ */
   138→const startAutoHideTimer = () => {
   139→  if (props.autoHideDuration > 0) {
   140→    autoHideTimer.value = window.setTimeout(() => {
   141→      hide();
   142→    }, props.autoHideDuration);
   143→  }
   144→};
   145→
   146→/**
   147→ * Clear auto-hide timer
   148→ */
   149→const clearAutoHideTimer = () => {
   150→  if (autoHideTimer.value !== null) {
   151→    clearTimeout(autoHideTimer.value);
   152→    autoHideTimer.value = null;
   153→  }
   154→};
   155→
   156→/**
   157→ * Hide the tutorial arrow
   158→ */
   159→const hide = () => {
   160→  console.log('[TutorialArrow] Hiding tutorial arrow');
   161→  isShowing.value = false;
   162→  emit('update:modelValue', false);
   163→  emit('hidden');
   164→  cleanupObservers();
   165→  clearAutoHideTimer();
   166→};
   167→
   168→// Watch for modelValue changes
   169→watch(() => props.modelValue, async (newValue) => {
   170→  console.log('[TutorialArrow] modelValue changed to:', newValue);
   171→
   172→  if (newValue && mounted.value) {
   173→    console.log('[TutorialArrow] Attempting to show tutorial...');
   174→
   175→    // Try to find and position immediately
   176→    await updatePosition();
   177→
   178→    if (targetElement.value) {
   179→      console.log('[TutorialArrow] ✅ Showing tutorial immediately');
   180→      isShowing.value = true;
   181→      setupObservers();
   182→      startAutoHideTimer();
   183→    } else {
   184→      // Target not found, retry up to 5 times with increasing delays
   185→      console.log('[TutorialArrow] ⏳ Target not found, setting up retry logic...');
   186→      let retries = 0;
   187→      const maxRetries = 5;
   188→
   189→      const attemptShow = async () => {
   190→        retries++;
   191→        console.log(`[TutorialArrow] Retry attempt ${retries}/${maxRetries}`);
   192→
   193→        await updatePosition();
   194→
   195→        if (targetElement.value) {
   196→          console.log('[TutorialArrow] ✅ Target found on retry', retries);
   197→          isShowing.value = true;
   198→          setupObservers();
   199→          startAutoHideTimer();
   200→        } else if (retries < maxRetries) {
   201→          // Keep retrying
   202→          setTimeout(attemptShow, 400 + (retries * 200)); // 400ms, 600ms, 800ms, 1000ms, 1200ms
   203→        } else {
   204→          console.error('[TutorialArrow] ❌ Failed to find target after', maxRetries, 'retries');
   205→          console.error('[TutorialArrow] Selector:', props.targetSelector);
   206→        }
   207→      };
   208→
   209→      // Start first retry
   210→      setTimeout(attemptShow, 400);
   211→    }
   212→  } else if (!newValue) {
   213→    console.log('[TutorialArrow] Hiding tutorial');
   214→    isShowing.value = false;
   215→    cleanupObservers();
   216→    clearAutoHideTimer();
   217→  }
   218→}, { immediate: true });
   219→
   220→// Watch for target selector changes
   221→watch(() => props.targetSelector, async () => {
   222→  console.log('[TutorialArrow] Target selector changed:', props.targetSelector);
   223→  if (isShowing.value) {
   224→    await updatePosition();
   225→  }
   226→});
   227→
   228→// Clean up on unmount
   229→onUnmounted(() => {
   230→  cleanupObservers();
   231→  clearAutoHideTimer();
   232→});
   233→
   234→defineExpose({
   235→  hide,
   236→  updatePosition
   237→});
   238→</script>
   239→
