   242→  pointer-events: none;
   243→}
   244→
   245→.tutorial-arrow-content {
   246→  display: flex;
   247→  flex-direction: column;
   248→  align-items: center;
   249→  gap: 0.5rem;
   250→  pointer-events: auto;
   251→}
   252→
   253→.tutorial-arrow-message {
   254→  display: flex;
   255→  align-items: center;
   256→  gap: 0.75rem;
   257→  padding: 0.75rem 1.25rem;
   258→  background: linear-gradient(135deg, #9333ea 0%, #ec4899 100%);
   259→  color: white;
   260→  border-radius: 0.75rem;
   261→  box-shadow: 0 10px 25px -5px rgba(147, 51, 234, 0.4), 0 8px 10px -6px rgba(147, 51, 234, 0.3);
   262→  font-size: 0.95rem;
   263→  white-space: nowrap;
   264→  max-width: 300px;
   265→}
   266→
   267→.tutorial-arrow-pointer {
   268→  display: flex;
   269→  align-items: center;
   270→  justify-content: center;
   271→  animation: arrowPulse 2s ease-in-out infinite;
   272→}
   273→
   274→/* Bounce animation for the entire arrow */
   275→.tutorial-arrow-bounce {
   276→  animation: tutorialBounce 2s ease-in-out infinite;
   277→}
   278→
   279→@keyframes tutorialBounce {
   280→  0%, 100% {
   281→    transform: translateY(0);
   282→  }
   283→  50% {
   284→    transform: translateY(-10px);
   285→  }
   286→}
   287→
   288→@keyframes arrowPulse {
   289→  0%, 100% {
   290→    opacity: 1;
   291→    transform: translateY(0) scale(1);
   292→  }
   293→  50% {
