import { computed, reactive, ref, watch, unref, onUnmounted, nextTick, getCurrentInstance, type Ref, type ComputedRef } from 'vue'
import {
  useElementVisibility,
  useDocumentVisibility,
  usePreferredReducedMotion,
  useRafFn,
  useEventBus,
  useMounted,
  useBattery,
  useFps,
  useIdle,
  usePageLeave,
  useAnimate,
  useCycleList,
  useScroll,
  type EventBusKey,
  type MaybeRefOrGetter,
  type UseAnimateKeyframes,
  type UseAnimateOptions,
} from '@vueuse/core'

// Import existing presets and constants
import { animationPresets, type AnimationPreset } from './useAnimateElement'
import { ANIMATION_DURATION, EASING, FILL_MODE } from '@/constants/animations'

// Export preset system for convenient animation configuration
export { preset, presets } from './useAnimationPresets'

/**
 * Web Animation Builder API options
 */
export interface CreateAnimationOptions {
  preset?: keyof typeof animationPresets
  keyframes?: UseAnimateKeyframes
  duration?: number
  easing?: string
  delay?: number
  fill?: FillMode
  onStart?: () => void
  onComplete?: () => void
  onCancel?: () => void
}

/**
 * Web Animation Builder API return type
 */
export interface CreateAnimationReturn {
  play: () => Promise<void>
  pause: () => void
  cancel: () => void
  reverse: () => void
  seek: (time: number) => void
  animation: Ref<Animation | undefined>
  cleanup: () => void
}

/**
 * Web Animation Builder API
 *
 * Simplified API for creating and controlling Web Animations with proper lifecycle management.
 *
 * @example
 * ```ts
 * const fadeIn = createAnimation(elementRef, {
 *   preset: 'fadeIn',
 *   duration: 500,
 *   onComplete: () => console.log('Done!')
 * })
 *
 * await fadeIn.play()
 * fadeIn.seek(250) // Jump to 50%
 * fadeIn.reverse() // Play backward
 * ```
 */
// Issue #5 Fix: WeakMap for listener tracking to prevent memory leaks
const createAnimationFinishListeners = new WeakMap<Animation, () => void>()
const createAnimationCancelListeners = new WeakMap<Animation, () => void>()

export function createAnimation(
  target: MaybeRefOrGetter<HTMLElement | undefined>,
  options: CreateAnimationOptions
): CreateAnimationReturn {
  const targetEl = computed(() => unref(target))
  const animRef = ref<Animation>()

  const play = async () => {
    const el = targetEl.value
    if (!el) return

    // Cancel existing animation and remove listeners
    if (animRef.value) {
      const currentAnim = animRef.value

      // Remove listeners BEFORE cancel to avoid race condition
      const finishListener = createAnimationFinishListeners.get(currentAnim)
      if (finishListener) {
        currentAnim.removeEventListener('finish', finishListener)
        createAnimationFinishListeners.delete(currentAnim)
      }
      const cancelListener = createAnimationCancelListeners.get(currentAnim)
      if (cancelListener) {
        currentAnim.removeEventListener('cancel', cancelListener)
        createAnimationCancelListeners.delete(currentAnim)
      }

      currentAnim.cancel()
      animRef.value = undefined
    }

    const preset = options.preset || 'fadeIn'
    const presetConfig = animationPresets[preset]

    // Fallback for unknown presets
    const keyframes = options.keyframes || presetConfig?.keyframes || [
      { opacity: 1 },
      { opacity: 0 }
    ]
    const duration = options.duration || ANIMATION_DURATION.NORMAL

    options.onStart?.()

    const anim = el.animate(keyframes as Keyframe[], {
      duration,
      easing: options.easing || EASING.EASE_OUT,
      delay: options.delay || 0,
      fill: options.fill || FILL_MODE.FORWARDS as FillMode
    })

    animRef.value = anim

    // Attach listeners with { once: true } to auto-cleanup
    if (options.onComplete) {
      const listener = () => options.onComplete!()
      anim.addEventListener('finish', listener, { once: true })
      createAnimationFinishListeners.set(anim, listener)
    }

    if (options.onCancel) {
      const listener = () => options.onCancel!()
      anim.addEventListener('cancel', listener, { once: true })
      createAnimationCancelListeners.set(anim, listener)
    }

    try {
      await anim.finished
    } catch {
      // Animation was cancelled, ignore
    }
  }

  const pause = () => animRef.value?.pause()

  const cancel = () => animRef.value?.cancel()

  const reverse = () => animRef.value?.reverse()

  const seek = (time: number) => {
    if (animRef.value) {
      animRef.value.currentTime = time
    }
  }

  const cleanup = () => {
    if (animRef.value) {
      const finishListener = createAnimationFinishListeners.get(animRef.value)
      if (finishListener) {
        animRef.value.removeEventListener('finish', finishListener)
        createAnimationFinishListeners.delete(animRef.value)
      }
      const cancelListener = createAnimationCancelListeners.get(animRef.value)
      if (cancelListener) {
        animRef.value.removeEventListener('cancel', cancelListener)
        createAnimationCancelListeners.delete(animRef.value)
      }
      animRef.value.cancel()
      animRef.value = undefined
    }
  }

  // Note: Don't use onUnmounted here - createAnimation may be called outside component context
  // Caller is responsible for calling cleanup() manually

  return { play, pause, cancel, reverse, seek, animation: animRef, cleanup }
}

/**
 * Helper function to calculate stagger delays
 */
function calculateStaggerDelays(count: number, options: UseAnimationStaggerOptions): number[] {
  const { delay, from = 'start', ease = 'linear', grid } = options
  const delays: number[] = []

  if (grid) {
    // 2D grid stagger
    const [cols, rows] = grid
    for (let i = 0; i < count; i++) {
      const col = i % cols
      const row = Math.floor(i / cols)
      const distance = from === 'center'
        ? Math.sqrt(Math.pow(col - cols / 2, 2) + Math.pow(row - rows / 2, 2))
        : from === 'end'
          ? (cols - col) + (rows - row)
          : col + row

      delays.push(distance * delay)
    }
  } else {
    // 1D linear stagger
    for (let i = 0; i < count; i++) {
      const position = from === 'start'
        ? i
        : from === 'end'
          ? count - 1 - i
          : Math.abs(i - count / 2)

      let normalizedDelay = position * delay

      // Apply easing
      if (ease !== 'linear') {
        const t = position / (count - 1 || 1)
        let easedT = t

        if (ease === 'ease-in') {
          easedT = t * t
        } else if (ease === 'ease-out') {
          easedT = 1 - Math.pow(1 - t, 2)
        } else if (ease.startsWith('cubic-bezier')) {
          // Simple approximation for cubic-bezier - would need full implementation
          easedT = t
        }

        normalizedDelay = easedT * delay * count
      }

      delays.push(normalizedDelay)
    }
  }

  return delays
}

/**
 * Animation state cache for shared state management
 */
const stateCache = new Map<string, any>()
const initialStateCache = new Map<string, any>()

/**
 * Reference counting for state cache cleanup
 */
const stateConsumers = new Map<string, Set<symbol>>()

/**
 * Issue #6 Fix: Atomic state cache cleanup helper
 * Uses nextTick to ensure cleanup happens after all synchronous operations
 */
function cleanupStateName(stateName: string, consumers: Set<symbol>) {
  // Use nextTick to defer cleanup and double-check atomically
  nextTick(() => {
    if (consumers.size === 0) {
      stateCache.delete(stateName)
      initialStateCache.delete(stateName)
      stateConsumers.delete(stateName)
    }
  })
}

/**
 * Event bus registry for shared event coordination
 */
const busRegistry = new Map<string, Map<string, ReturnType<typeof useEventBus>>>()

/**
 * Global environment cache (singleton)
 */
let cachedEnvironment: {
  prefersReducedMotion: Ref<'reduce' | 'no-preference'>
  batteryInfo: ReturnType<typeof useBattery>
  fps: Ref<number> | null
  idle: { idle: Ref<boolean>; lastActive: Ref<number>; reset: () => void } | null
  isPageLeft: Ref<boolean> | null
} | null = null

/**
 * Autoplay configuration
 */
export interface UseAnimationAutoplayOptions {
  threshold?: number
  enabled?: Ref<boolean>
  includeNavigation?: boolean
}

/**
 * Event system configuration
 */
export interface UseAnimationEventsOptions {
  busName?: string
  emitOnStart?: string
  emitOnStop?: string
  listenForStop?: string
}

/**
 * Environment respect configuration
 */
export interface UseAnimationEnvironmentOptions {
  reducedMotion?: boolean
  battery?: boolean
  fps?: boolean
  idle?: boolean
  pageLeave?: boolean
}

/**
 * Web Animations API configuration
 */
export interface UseAnimationWebAnimationOptions {
  preset?: keyof typeof animationPresets
  keyframes?: UseAnimateKeyframes
  duration?: number
  easing?: string
}

/**
 * Timed sequence configuration (for RAF-based element activation)
 */
export interface UseAnimationSequenceOptions<T = any> {
  elements: T[] | Ref<T[]>
  interval: number | Ref<number> // Duration per element (ms)
  mode?: 'sequence' | 'loop' | 'stagger' | 'unison'
  onActivate: (element: T, index: number, context: {
    cycleIndex: number
    elapsedInCycle: number
    totalElapsed: number
  }) => void
  onCycleComplete?: () => void
}

/**
 * Scroll-triggered animation configuration
 */
export interface UseAnimationScrollOptions {
  container?: Ref<HTMLElement | undefined>
  threshold?: [number, number] // Start/end scroll % [0, 1]
  mode?: 'toggle' | 'scrub'
  offset?: { start?: number; end?: number }
  onProgress?: (progress: number) => void
}

/**
 * Stagger animation configuration
 */
export interface UseAnimationStaggerOptions {
  children: string | HTMLElement[] | Ref<HTMLElement[]>
  delay: number
  from?: 'start' | 'end' | 'center'
  ease?: 'linear' | 'ease-in' | 'ease-out' | string
  grid?: [number, number]
  resetOnRestart?: boolean // Whether to reset elements to initial state before restarting (default: true)
}

/**
 * Motion path configuration
 */
export interface UseAnimationMotionPathOptions {
  type: 'circular' | 'elliptical' | 'custom'
  radius?: number | { x: number; y: number }
  startAngle?: number
  duration: number
  direction?: 'clockwise' | 'counterclockwise'
  path?: string // SVG path for custom motion
  autoRotate?: boolean // Whether to rotate element along the path (default: false)
}

/**
 * Animation timeline step
 */
export interface UseAnimationTimelineStep {
  at: number | string
  target?: Ref<HTMLElement | undefined>
  action: 'play' | 'pause' | 'emit' | 'callback'
  animation?: UseAnimationWebAnimationOptions
  event?: string
  callback?: () => void
}

/**
 * Animation timeline configuration
 */
export interface UseAnimationTimelineOptions {
  steps: UseAnimationTimelineStep[]
  loop?: boolean
  duration?: number
}

/**
 * Enter/Exit animation configuration
 */
export interface UseAnimationEnterExitOptions {
  trigger: Ref<boolean>
  enter?: UseAnimationWebAnimationOptions
  exit?: UseAnimationWebAnimationOptions
  skipInitial?: boolean
  onEnterStart?: () => void
  onEnterComplete?: () => void
  onExitStart?: () => void
  onExitComplete?: () => void
}

/**
 * Lifecycle hook system configuration
 */
export interface UseAnimationLifecycleOptions {
  beforePlay?: () => void | Promise<void>
  afterPlay?: () => void
  beforePause?: () => void
  afterPause?: () => void
  beforeStop?: () => void
  afterStop?: () => void
  beforeResume?: () => void
  afterResume?: () => void
  onCancel?: () => void
  onProgress?: (progress: number) => void
}

/**
 * Main useAnimation options
 */
export interface UseAnimationOptions<T = any> {
  // Auto-play configuration
  autoplay?: boolean | UseAnimationAutoplayOptions

  // Event system (replaces useAnimationBus)
  events?: UseAnimationEventsOptions

  // State management (replaces useAnimationState)
  stateName?: string
  initialState?: Record<string, any>

  // Environment (replaces useAnimationEnvironment)
  respectEnvironment?: boolean | UseAnimationEnvironmentOptions

  // Priority system (simplified two-tier: critical vs normal)
  critical?: boolean // If true, never auto-pause (for essential UI feedback like spinners, button states)

  // Web Animations (replaces useAnimateElement)
  animation?: UseAnimationWebAnimationOptions

  // Timed sequence (NEW - replaces useTimedSequence)
  sequence?: UseAnimationSequenceOptions<T>

  // Scroll-triggered animations
  scroll?: UseAnimationScrollOptions

  // Stagger animations
  stagger?: UseAnimationStaggerOptions

  // Motion path animations
  motionPath?: UseAnimationMotionPathOptions

  // Animation timeline
  timeline?: UseAnimationTimelineOptions

  // Enter/Exit pattern
  enterExit?: UseAnimationEnterExitOptions

  // Lifecycle hooks
  lifecycle?: UseAnimationLifecycleOptions

  // Callbacks (deprecated in favor of lifecycle hooks, but kept for backward compatibility)
  onStart?: () => void
  onStop?: () => void
  onComplete?: () => void
}

/**
 * Animation quality level
 */
export type AnimationQuality = 'none' | 'low' | 'medium' | 'high'

/**
 * useAnimation return type
 */
export interface UseAnimationReturn<TState = any, TElement = any> {
  // Playback controls
  play: () => void
  pause: () => void
  stop: () => void
  resume: () => void

  // State access
  isPlaying: Ref<boolean>
  state: TState

  // Event emitter
  emit: <K extends string>(event: K, payload?: any) => void
  on: <K extends string>(event: K, handler: (payload: any) => void) => () => void

  // Environment info
  canAnimate: ComputedRef<boolean>
  animationQuality: ComputedRef<AnimationQuality>
  shouldPauseAnimations: ComputedRef<boolean>

  // Animation instance (if Web Animations enabled)
  animate?: Ref<Animation | undefined>
  animateIn?: (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
  animateOut?: (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>

  // Sequence controls (if sequence enabled)
  sequence?: {
    currentIndex: Ref<number>
    currentElement: ComputedRef<TElement | undefined>
    cycleIndex: Ref<number>
    next: () => void
    prev: () => void
    goTo: (index: number) => void
  }

  // Scroll controls (if scroll enabled)
  scroll?: {
    progress: ComputedRef<number>
    isScrolling: Ref<boolean>
  }

  // Stagger controls (if stagger enabled)
  stagger?: {
    elements: Ref<HTMLElement[]>
    triggerStagger: () => void
    activeTimeouts?: Ref<Array<ReturnType<typeof setTimeout>>>
    activeAnimations?: Ref<Animation[]>
  }

  // Motion path controls (if motionPath enabled)
  motionPath?: {
    progress: Ref<number>
    setProgress: (progress: number) => void
  }

  // CSS variable utilities
  setCSSVar: (name: string, value: string | number) => void
  getCSSVar: (name: string) => string | undefined
  syncCSSVars: (vars: Record<string, Ref<string | number> | ComputedRef<string | number>>) => () => void

  // Cleanup
  cleanup: () => void
}

/**
 * Universal animation orchestrator that consolidates:
 * - useAnimationBus (event coordination)
 * - useAnimationState (state management)
 * - useAnimationEnvironment (environment detection)
 * - useAnimateElement (Web Animations API)
 *
 * @example Using animation presets
 * ```typescript
 * import { useAnimation, preset } from '@composables/useAnimation'
 *
 * // Use preset as-is
 * const orbitAnim = useAnimation(iconRef, preset('orbitingIcon'))
 *
 * // With overrides
 * const customOrbit = useAnimation(iconRef, preset('orbitingIcon', {
 *   motionPath: { radius: 120, duration: 3000 }
 * }))
 *
 * // Stagger animation preset
 * const stagger = useAnimation(containerRef, preset('staggerFade', {
 *   stagger: { children: '.custom-item', delay: 150 }
 * }))
 *
 * // Combine preset with other options
 * const enhanced = useAnimation(ref, preset('fadeInUp', {
 *   autoplay: { threshold: 0.5 },
 *   lifecycle: {
 *     afterPlay: () => console.log('Animation complete!')
 *   }
 * }))
 * ```
 *
 * @example Manual configuration (without presets)
 * ```typescript
 * const customAnim = useAnimation(ref, {
 *   animation: {
 *     keyframes: [
 *       { opacity: 0, transform: 'scale(0.8)' },
 *       { opacity: 1, transform: 'scale(1)' }
 *     ],
 *     duration: 600
 *   },
 *   autoplay: true
 * })
 * ```
 */
export function useAnimation<TState = any, TElement = any>(
  target: MaybeRefOrGetter<HTMLElement | undefined>,
  options: UseAnimationOptions<TElement> = {}
): UseAnimationReturn<TState, TElement> {
  const {
    autoplay = false,
    events,
    stateName,
    initialState,
    respectEnvironment = true,
    critical = false,
    animation,
    sequence,
    scroll,
    stagger,
    motionPath,
    timeline,
    enterExit,
    lifecycle,
    onStart,
    onStop,
    onComplete,
  } = options

  const isMounted = useMounted()

  // Create unique consumer ID for reference counting
  const consumerId = Symbol('animation-consumer')

  // Pause state (separate from isPlaying to avoid breaking timeline timing)
  const isPaused = ref(false)

  // Track active progress interval for cleanup
  const activeProgressInterval = ref<number | null>(null)

  // Parse autoplay options
  const autoplayOptions: UseAnimationAutoplayOptions =
    typeof autoplay === 'boolean'
      ? { threshold: 0.25, enabled: ref(autoplay) }
      : { threshold: autoplay.threshold || 0.25, enabled: autoplay.enabled || ref(true) }

  // Parse environment options
  const envOptions: UseAnimationEnvironmentOptions =
    typeof respectEnvironment === 'boolean'
      ? { reducedMotion: true, battery: true, fps: true, idle: true, pageLeave: true }
      : {
          reducedMotion: respectEnvironment.reducedMotion !== false,
          battery: respectEnvironment.battery !== false,
          fps: respectEnvironment.fps !== false,
          idle: respectEnvironment.idle !== false, // Changed from === true to !== false (opt-out instead of opt-in)
          pageLeave: respectEnvironment.pageLeave !== false,
        }

  // ===============================
  // ENVIRONMENT DETECTION (replaces useAnimationEnvironment)
  // ===============================
  if (!cachedEnvironment) {
    const prefersReducedMotion = usePreferredReducedMotion()
    const batteryInfo = useBattery()
    const fps = envOptions.fps ? useFps({ every: 10 }) : null
    const idle = envOptions.idle
      ? useIdle(5 * 60 * 1000) // 5 minutes
      : null
    const isPageLeft = envOptions.pageLeave ? usePageLeave() : null

    cachedEnvironment = {
      prefersReducedMotion,
      batteryInfo,
      fps,
      idle,
      isPageLeft,
    }
  }

  const env = cachedEnvironment

  // Computed environment flags
  const reducedMotion = computed(
    () => envOptions.reducedMotion && isMounted.value && env.prefersReducedMotion.value === 'reduce'
  )

  const batteryOptimization = computed(() => {
    if (!envOptions.battery) return false
    const level = env.batteryInfo.level.value
    // Changed threshold from 0.2 to 0.2 (20%) for earlier auto-pause
    return typeof level === 'number' ? level < 0.2 && !env.batteryInfo.charging.value : false
  })

  const lowFps = computed(() => envOptions.fps && env.fps ? env.fps.value < 30 : false)

  const userIdle = computed(() => envOptions.idle && env.idle ? env.idle.idle.value : false)

  const pageLeft = computed(() => envOptions.pageLeave && env.isPageLeft ? env.isPageLeft.value : false)

  const canAnimate = computed(
    () => !reducedMotion.value && !batteryOptimization.value && !lowFps.value
  )

  const shouldPauseAnimations = computed(
    () => userIdle.value || pageLeft.value || (batteryOptimization.value && env.batteryInfo.level.value < 0.1)
  )

  const animationQuality = computed<AnimationQuality>(() => {
    if (reducedMotion.value) return 'none'
    if (batteryOptimization.value) return 'low'
    if (lowFps.value) return 'medium'
    return 'high'
  })

  // ===============================
  // STATE MANAGEMENT (replaces useAnimationState)
  // ===============================
  let state: TState

  if (stateName) {
    // Register consumer for reference counting
    if (!stateConsumers.has(stateName)) {
      stateConsumers.set(stateName, new Set())
    }
    stateConsumers.get(stateName)!.add(consumerId)

    // Get or create shared state
    if (!stateCache.has(stateName)) {
      const resolved = initialState || {}
      const cloned = structuredClone(resolved)
      stateCache.set(stateName, reactive({ isPlaying: false, ...cloned }))
      initialStateCache.set(stateName, structuredClone(cloned))
    }
    state = stateCache.get(stateName) as TState
  } else {
    // Local state
    state = reactive({ isPlaying: false, ...(initialState || {}) }) as TState
  }

  const isPlaying = computed({
    get: () => (state as any).isPlaying,
    set: (value: boolean) => {
      ;(state as any).isPlaying = value
    },
  })

  // ===============================
  // EVENT SYSTEM (replaces useAnimationBus)
  // ===============================
  const busName = events?.busName || 'default'

  if (!busRegistry.has(busName)) {
    busRegistry.set(busName, new Map())
  }
  const eventBuses = busRegistry.get(busName)!

  const getEventBus = (eventName: string) => {
    const namespacedKey = `${busName}:${eventName}` as unknown as EventBusKey<any>
    if (!eventBuses.has(eventName)) {
      eventBuses.set(eventName, useEventBus(namespacedKey))
    }
    return eventBuses.get(eventName)!
  }

  const emit = <K extends string>(event: K, payload?: any) => {
    const bus = getEventBus(event)
    bus.emit(payload)
  }

  const on = <K extends string>(event: K, handler: (payload: any) => void) => {
    const bus = getEventBus(event)
    return bus.on(handler as any)
  }

  // Listen for stop events
  const stopListeners: Array<() => void> = []
  if (events?.listenForStop) {
    const unsubscribe = on(events.listenForStop, () => {
      stop()
    })
    stopListeners.push(unsubscribe)
  }

  // ===============================
  // AUTO-PLAY LOGIC (replaces manual visibility watchers)
  // ===============================
  const targetElement = computed(() => unref(target))
  const elementVisible = autoplay
    ? useElementVisibility(targetElement, { threshold: autoplayOptions.threshold })
    : ref(false)
  const documentVisible = autoplay ? useDocumentVisibility() : ref('visible')

  // Navigation tracking (Astro-specific)
  // Issue #3 Fix: Store cleanup function for use outside component context
  const currentPath = ref(typeof window !== 'undefined' ? window.location.pathname : '')
  const navigationCleanup = ref<(() => void) | null>(null)

  if (autoplay && autoplayOptions.includeNavigation && typeof window !== 'undefined') {
    const handleNavigationChange = () => {
      const newPath = window.location.pathname
      if (newPath !== currentPath.value) {
        currentPath.value = newPath
        stop()
      }
    }

    window.addEventListener('popstate', handleNavigationChange)
    if (typeof document !== 'undefined') {
      document.addEventListener('astro:before-preparation', handleNavigationChange)
      document.addEventListener('astro:page-load', handleNavigationChange)
    }

    // Store cleanup function for manual cleanup
    navigationCleanup.value = () => {
      window.removeEventListener('popstate', handleNavigationChange)
      if (typeof document !== 'undefined') {
        document.removeEventListener('astro:before-preparation', handleNavigationChange)
        document.removeEventListener('astro:page-load', handleNavigationChange)
      }
    }

    onUnmounted(() => {
      navigationCleanup.value?.()
      navigationCleanup.value = null
    })
  }

  const shouldAutoplay = computed(() => {
    if (!autoplay) return false
    if (!autoplayOptions.enabled?.value) return false
    if (documentVisible.value !== 'visible') return false
    if (!canAnimate.value) return false
    return elementVisible.value
  })

  // ===============================
  // WEB ANIMATIONS API (replaces useAnimateElement)
  // ===============================
  let webAnimation:
    | {
        animate: Ref<Animation | undefined>
        play: () => void
        pause: () => void
        cancel: () => void
        animateIn: (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
        animateOut: (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => Promise<void | Animation | undefined>
      }
    | undefined

  // Skip container animation if stagger is configured (animation config used for children only)
  if (animation && !stagger) {
    const preset = animation.preset || 'fadeIn'
    const presetConfig = animationPresets[preset]
    const keyframes = animation.keyframes || presetConfig.keyframes
    const baseOptions =
      typeof presetConfig.options === 'object' ? presetConfig.options : { duration: presetConfig.options }

    const baseDuration = animation.duration || (typeof baseOptions.duration === 'number' ? baseOptions.duration : ANIMATION_DURATION.NORMAL)

    const effectiveDuration = computed(() => {
      switch (animationQuality.value) {
        case 'none':
          return 0
        case 'low':
          return baseDuration * 0.3
        case 'medium':
          return baseDuration * 0.6
        case 'high':
          return baseDuration
      }
    })

    const animationOptions = computed(
      () =>
        ({
          ...baseOptions,
          duration: effectiveDuration.value,
          ...(animation.easing && { easing: animation.easing }),
          immediate: false,
          // onReady fires immediately, not when animation finishes - removed
        }) as UseAnimateOptions
    )

    const webAnimAPI = useAnimate(target, keyframes, animationOptions.value)

    const animateIn = async (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => {
      if (!isMounted.value || !webAnimAPI.isSupported.value) return

      if (animationQuality.value === 'none' || !canAnimate.value) {
        const element = unref(target)
        if (element && Array.isArray(keyframes) && keyframes.length > 0) {
          const finalFrame = keyframes[keyframes.length - 1] as Record<string, any>
          Object.assign(element.style, finalFrame)
        }
        onComplete?.()
        return
      }

      if (customKeyframes && webAnimAPI.animate.value) {
        webAnimAPI.cancel()
        const element = unref(target)
        if (element) {
          const newAnimation = element.animate(customKeyframes as Keyframe[], {
            ...animationOptions.value,
            ...customOptions,
          })
          if (onComplete) newAnimation.addEventListener('finish', onComplete)
          return newAnimation
        }
      } else {
        webAnimAPI.play()
      }
    }

    const animateOut = async (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => {
      if (!isMounted.value || !webAnimAPI.isSupported.value) return

      if (animationQuality.value === 'none' || !canAnimate.value) {
        const element = unref(target)
        if (element) {
          const defaultOutKeyframes = [
            { opacity: 1, transform: 'scale(1)' },
            { opacity: 0, transform: 'scale(0.8)' },
          ]
          const finalFrame = (outKeyframes?.[outKeyframes.length - 1] || defaultOutKeyframes[1]) as Record<string, any>
          Object.assign(element.style, finalFrame)
        }
        onComplete?.()
        return
      }

      const defaultOutKeyframes = [
        { opacity: 1, transform: 'scale(1)' },
        { opacity: 0, transform: 'scale(0.8)' },
      ]
      const defaultOutDuration =
        animationQuality.value === 'low'
          ? ANIMATION_DURATION.FAST * 0.3
          : animationQuality.value === 'medium'
            ? ANIMATION_DURATION.FAST * 0.6
            : ANIMATION_DURATION.FAST
      const defaultOutOptions = {
        duration: defaultOutDuration,
        easing: EASING.EASE_OUT,
        fill: FILL_MODE.FORWARDS as FillMode,
      }
      const element = unref(target)
      if (element) {
        const anim = element.animate((outKeyframes || defaultOutKeyframes) as Keyframe[], {
          ...defaultOutOptions,
          ...outOptions,
        })
        if (onComplete) anim.addEventListener('finish', onComplete)
        return anim
      }
    }

    webAnimation = {
      animate: webAnimAPI.animate,
      play: webAnimAPI.play,
      pause: webAnimAPI.pause,
      cancel: webAnimAPI.cancel,
      animateIn,
      animateOut,
    }
  }

  // ===============================
  // FINISH LISTENER TRACKING
  // ===============================
  const finishListeners = new WeakMap<Animation, () => void>()

  // ===============================
  // PLAYBACK CONTROLS
  // ===============================
  const play = async () => {
    // Lifecycle: beforePlay
    if (lifecycle?.beforePlay) {
      await lifecycle.beforePlay()
    }

    // Always set isPlaying to allow timeline/sequence to run
    // (they coordinate child callbacks, not Web Animations)
    isPlaying.value = true
    onStart?.()
    if (events?.emitOnStart) emit(events.emitOnStart)

    // Only skip Web Animations if environment conditions don't allow
    // Timeline and sequence still run to coordinate child components
    if (canAnimate.value && animationQuality.value !== 'none') {
      webAnimation?.play()

      // Attach onComplete listener using addEventListener for proper replay support
      const anim = webAnimation?.animate.value
      if (anim && onComplete) {
        // Remove previous listener if exists
        const prevListener = finishListeners.get(anim)
        if (prevListener) {
          anim.removeEventListener('finish', prevListener)
        }

        // Add new listener
        const finishHandler = () => {
          if (isPlaying.value) {
            onComplete()
          }
        }

        anim.addEventListener('finish', finishHandler)
        finishListeners.set(anim, finishHandler)
      }
    } else {
      // Environment doesn't allow Web Animations, but timeline/sequence still run
      // This allows child component coordination to work even in degraded environments
      if (onComplete) {
        // If there's no Web Animation, complete immediately
        onComplete()
      }
    }

    // Lifecycle: afterPlay
    lifecycle?.afterPlay?.()
  }

  const pause = () => {
    // Lifecycle: beforePause
    lifecycle?.beforePause?.()

    isPaused.value = true
    webAnimation?.pause()

    // Explicitly pause all RAF systems
    if (timedSequence) pauseSequence()
    if (motionPathAnimation) pauseMotion()
    if (timeline) pauseTimeline()

    // Lifecycle: afterPause
    lifecycle?.afterPause?.()
  }

  const stop = () => {
    // Lifecycle: beforeStop
    lifecycle?.beforeStop?.()

    isPlaying.value = false
    isPaused.value = false // Reset pause state on stop
    onStop?.()
    if (events?.emitOnStop) emit(events.emitOnStop)

    // Lifecycle: onCancel (if animation was cancelled)
    lifecycle?.onCancel?.()

    webAnimation?.cancel()

    // Clear stagger timeouts and animations to prevent animations on stopped/unmounted components
    if (staggerAnimation?.activeTimeouts) {
      staggerAnimation.activeTimeouts.value.forEach(id => clearTimeout(id))
      staggerAnimation.activeTimeouts.value = []
    }
    if (staggerAnimation && 'activeAnimations' in staggerAnimation) {
      (staggerAnimation as any).activeAnimations.value.forEach((anim: Animation) => anim.cancel())
      ;(staggerAnimation as any).activeAnimations.value = []
    }

    // Lifecycle: afterStop
    lifecycle?.afterStop?.()
  }

  const resume = () => {
    // Lifecycle: beforeResume
    lifecycle?.beforeResume?.()

    isPaused.value = false
    if (isPlaying.value) {
      webAnimation?.play()

      // Resume RAF systems
      if (timedSequence) resumeSequence()
      if (motionPathAnimation) resumeMotion()
      if (timeline) resumeTimeline()
    }

    // Lifecycle: afterResume
    lifecycle?.afterResume?.()
  }

  // ===============================
  // LIFECYCLE: PROGRESS TRACKING
  // ===============================
  // Issue #1 Fix: Use WeakMap for event listeners and proper cleanup
  const progressEventListeners = new WeakMap<Animation, { finish: () => void; cancel: () => void }>()
  let progressWatchStop: (() => void) | undefined

  if (lifecycle?.onProgress && webAnimation?.animate) {
    progressWatchStop = watch(() => webAnimation.animate.value, (anim, oldAnim) => {
      // Remove old animation listeners BEFORE clearing interval
      if (oldAnim) {
        const oldListeners = progressEventListeners.get(oldAnim)
        if (oldListeners) {
          oldAnim.removeEventListener('finish', oldListeners.finish)
          oldAnim.removeEventListener('cancel', oldListeners.cancel)
          progressEventListeners.delete(oldAnim)
        }
      }

      // Cleanup previous interval
      if (activeProgressInterval.value !== null) {
        clearInterval(activeProgressInterval.value)
        activeProgressInterval.value = null
      }

      if (!anim) return

      // Poll for progress updates
      const progressInterval = setInterval(() => {
        if (!anim || anim.playState === 'finished' || anim.playState === 'idle') {
          if (activeProgressInterval.value !== null) {
            clearInterval(activeProgressInterval.value)
            activeProgressInterval.value = null
          }
          return
        }

        const timing = anim.effect?.getTiming()
        if (timing && typeof timing.duration === 'number' && anim.currentTime !== null) {
          const progress = Math.max(0, Math.min(1, anim.currentTime / timing.duration))
          lifecycle.onProgress!(progress)
        }
      }, 16) // ~60fps

      activeProgressInterval.value = progressInterval

      // Cleanup on animation finish/cancel using WeakMap
      const finishHandler = () => {
        if (activeProgressInterval.value !== null) {
          clearInterval(activeProgressInterval.value)
          activeProgressInterval.value = null
        }
        lifecycle.onProgress!(1) // Final progress
      }
      const cancelHandler = () => {
        if (activeProgressInterval.value !== null) {
          clearInterval(activeProgressInterval.value)
          activeProgressInterval.value = null
        }
      }

      anim.addEventListener('finish', finishHandler, { once: true })
      anim.addEventListener('cancel', cancelHandler, { once: true })
      progressEventListeners.set(anim, { finish: finishHandler, cancel: cancelHandler })
    }, { immediate: true })
  }

  // ===============================
  // WATCHERS
  // ===============================

  // Auto-play watcher
  if (autoplay) {
    watch(shouldAutoplay, (should) => {
      if (should && !isPlaying.value) {
        play()
      } else if (!should && isPlaying.value) {
        stop()
      }
    }, { immediate: true })
  }

  // Auto-pause on environment changes (unless animation is critical)
  watch(shouldPauseAnimations, (shouldPause) => {
    // Skip auto-pause for critical animations (e.g., loading spinners, essential UI feedback)
    if (critical) return

    if (shouldPause && isPlaying.value) {
      pause()
    } else if (!shouldPause && isPlaying.value) {
      resume()
    }
  })

  // ===============================
  // TIMED SEQUENCE (NEW - replaces useTimedSequence)
  // ===============================

  // Declare RAF control functions at higher scope for pause/resume access
  let pauseSequence: (() => void) | undefined
  let resumeSequence: (() => void) | undefined
  let pauseMotion: (() => void) | undefined
  let resumeMotion: (() => void) | undefined
  let pauseTimeline: (() => void) | undefined
  let resumeTimeline: (() => void) | undefined

  let timedSequence:
    | {
        currentIndex: Ref<number>
        currentElement: ComputedRef<TElement | undefined>
        cycleIndex: Ref<number>
        next: () => void
        prev: () => void
        goTo: (index: number) => void
      }
    | undefined

  if (sequence) {
    const elements = computed(() => (Array.isArray(sequence.elements) ? sequence.elements : sequence.elements.value))
    const interval = computed(() => (typeof sequence.interval === 'number' ? sequence.interval : sequence.interval.value))
    const mode = sequence.mode || 'loop'

    // Use VueUse's useCycleList for element cycling
    const { state: currentElement, index: currentIndex, next, prev, go: goTo } = useCycleList(elements)

    const cycleIndex = ref(0)
    const startTime = ref<number | null>(null)
    const currentCycleStartTime = ref(0)

    // RAF loop for timed activation
    const rafControls = useRafFn(
      (args) => {
        if (!isPlaying.value || isPaused.value) {
          rafControls.pause()
          return
        }

        const now = args.timestamp

        // Initialize on first RAF callback instead of relying on watch
        if (startTime.value === null) {
          startTime.value = now
          currentCycleStartTime.value = now
        }

        const totalElapsed = now - startTime.value
        const elapsedSinceCycle = now - currentCycleStartTime.value
        const elementsArray = elements.value

        if (!elementsArray.length) {
          rafControls.pause()
          return
        }

        if (mode === 'loop' || mode === 'sequence') {
          // Sequential activation: one element at a time
          if (elapsedSinceCycle >= interval.value) {
            const element = elementsArray[currentIndex.value]
            const elapsedInCycle = elapsedSinceCycle

            // Activate current element
            sequence.onActivate(element, currentIndex.value, {
              cycleIndex: cycleIndex.value,
              elapsedInCycle,
              totalElapsed,
            })

            // Move to next element
            if (currentIndex.value === elementsArray.length - 1) {
              // Cycle complete
              cycleIndex.value++
              sequence.onCycleComplete?.()

              if (mode === 'sequence') {
                // Stop after one sequence
                stop()
                return
              }
            }

            next()
            currentCycleStartTime.value = now
          }
        } else if (mode === 'stagger') {
          // Stagger: activate elements with delays based on their index
          const timePerElement = interval.value / elementsArray.length

          elementsArray.forEach((element, index) => {
            const activationTime = index * timePerElement
            const nextActivationTime = (index + 1) * timePerElement

            if (elapsedSinceCycle >= activationTime && elapsedSinceCycle < nextActivationTime) {
              sequence.onActivate(element, index, {
                cycleIndex: cycleIndex.value,
                elapsedInCycle: elapsedSinceCycle - activationTime,
                totalElapsed,
              })
            }
          })

          // Check for cycle complete
          if (elapsedSinceCycle >= interval.value) {
            cycleIndex.value++
            sequence.onCycleComplete?.()
            currentCycleStartTime.value = now
          }
        } else if (mode === 'unison') {
          // Unison: activate all elements at once
          if (elapsedSinceCycle < 50) {
            // Activate within first frame
            elementsArray.forEach((element, index) => {
              sequence.onActivate(element, index, {
                cycleIndex: cycleIndex.value,
                elapsedInCycle: elapsedSinceCycle,
                totalElapsed,
              })
            })
          }

          // Check for cycle complete
          if (elapsedSinceCycle >= interval.value) {
            cycleIndex.value++
            sequence.onCycleComplete?.()
            currentCycleStartTime.value = now
          }
        }
      },
      { immediate: false }
    )

    // Assign pause/resume to outer scope for use in pause()/resume() functions
    pauseSequence = rafControls.pause
    resumeSequence = rafControls.resume

    // Auto-start/stop sequence with animation
    watch(isPlaying, (playing) => {
      if (playing) {
        // Reset state for new cycle (initialization happens in RAF)
        rafControls.resume()
      } else {
        rafControls.pause()
        startTime.value = null
      }
    })

    timedSequence = {
      currentIndex,
      currentElement,
      cycleIndex,
      next,
      prev,
      goTo,
    }
  }

  // ===============================
  // SCROLL-TRIGGERED ANIMATIONS
  // ===============================
  let scrollAnimation:
    | {
        progress: ComputedRef<number>
        isScrolling: Ref<boolean>
      }
    | undefined

  if (scroll) {
    const scrollContainer = scroll.container || ref(typeof window !== 'undefined' ? window : undefined)
    const { y: scrollY, isScrolling } = useScroll(scrollContainer as any)

    const scrollProgress = computed(() => {
      if (!isMounted.value) return 0

      const container = unref(scrollContainer)
      if (!container) return 0

      const scrollHeight = container === window
        ? document.documentElement.scrollHeight - window.innerHeight
        : container.scrollHeight - container.clientHeight

      if (scrollHeight <= 0) return 0

      const rawProgress = scrollY.value / scrollHeight
      const [thresholdStart, thresholdEnd] = scroll.threshold || [0, 1]

      // Apply offsets
      const offsetStart = scroll.offset?.start || 0
      const offsetEnd = scroll.offset?.end || 0
      const adjustedProgress = (rawProgress * scrollHeight - offsetStart) / (scrollHeight - offsetStart - offsetEnd)

      // Clamp between threshold values with division-by-zero protection
      const denominator = thresholdEnd - thresholdStart
      const normalizedProgress = denominator > 0
        ? (adjustedProgress - thresholdStart) / denominator
        : 0 // Default to 0 if thresholds are equal

      const clampedProgress = Math.max(0, Math.min(1, normalizedProgress))

      // Safety check for NaN
      return isNaN(clampedProgress) ? 0 : clampedProgress
    })

    // Watch progress and trigger callbacks
    watch(scrollProgress, (progress) => {
      if (scroll.mode === 'toggle') {
        const [thresholdStart] = scroll.threshold || [0, 1]
        if (progress >= thresholdStart && !isPlaying.value) {
          play()
        } else if (progress < thresholdStart && isPlaying.value) {
          stop()
        }
      }

      // Single call for all modes (toggle, scrub, or undefined)
      scroll.onProgress?.(progress)
    })

    scrollAnimation = {
      progress: scrollProgress,
      isScrolling,
    }
  }

  // ===============================
  // STAGGER ANIMATIONS
  // ===============================
  let staggerAnimation:
    | {
        elements: Ref<HTMLElement[]>
        triggerStagger: () => void
        activeTimeouts: Ref<Array<ReturnType<typeof setTimeout>>>
        activeAnimations: Ref<Animation[]>
      }
    | undefined

  if (stagger) {
    const staggerElements = ref<HTMLElement[]>([])
    // Issue #4 Fix: Use ReturnType<typeof setTimeout> for cross-platform compatibility
    const activeTimeouts = ref<Array<ReturnType<typeof setTimeout>>>([])
    const activeAnimations = ref<Animation[]>([]) // Track animations for cleanup
    const initialElementStates = new WeakMap<HTMLElement, { opacity: string; transform: string }>() // Store initial states

    const updateStaggerElements = () => {
      if (!isMounted.value) return

      const targetEl = unref(target)
      if (!targetEl) return

      if (typeof stagger.children === 'string') {
        staggerElements.value = Array.from(targetEl.querySelectorAll(stagger.children))
      } else if (Array.isArray(stagger.children)) {
        staggerElements.value = stagger.children
      } else {
        staggerElements.value = unref(stagger.children) || []
      }
    }

    const triggerStagger = async () => {
      // Clear any existing timeouts to prevent overlapping animations
      activeTimeouts.value.forEach(id => clearTimeout(id))
      activeTimeouts.value = []

      // Clear any existing animations
      activeAnimations.value.forEach(anim => anim.cancel())
      activeAnimations.value = []

      // Wait for DOM to be ready
      await nextTick()
      updateStaggerElements()

      const elements = staggerElements.value

      if (!elements.length) {
        return
      }

      // Reset elements to initial state if resetOnRestart is enabled (default: true)
      const shouldReset = stagger.resetOnRestart !== false
      if (shouldReset) {
        elements.forEach(el => {
          // Capture initial state on first run
          if (!initialElementStates.has(el)) {
            const computedStyle = getComputedStyle(el)
            initialElementStates.set(el, {
              opacity: computedStyle.opacity,
              transform: computedStyle.transform
            })
          }

          // Reset to initial state
          const initialState = initialElementStates.get(el)
          if (initialState) {
            el.style.opacity = initialState.opacity
            el.style.transform = initialState.transform
          }
        })

        // Small delay to ensure style reset applies before animation starts
        await new Promise(resolve => setTimeout(resolve, 16)) // ~1 frame at 60fps
      }

      const delays = calculateStaggerDelays(elements.length, stagger)

      elements.forEach((el, index) => {
        const delay = delays[index]

        const timeoutId = setTimeout(() => {
          // Skip animation if quality is none or cannot animate
          if (!canAnimate.value || animationQuality.value === 'none') {
            // Apply final state immediately without animation
            if (animation) {
              const preset = animation.preset || 'fadeIn'
              const presetConfig = animationPresets[preset]
              const keyframes = animation.keyframes || presetConfig.keyframes
              if (Array.isArray(keyframes) && keyframes.length > 0) {
                const finalFrame = keyframes[keyframes.length - 1] as Record<string, any>
                Object.assign(el.style, finalFrame)
              }
            }
            return
          }

          if (animation) {
            const preset = animation.preset || 'fadeIn'
            const presetConfig = animationPresets[preset]
            const keyframes = animation.keyframes || presetConfig.keyframes
            const duration = animation.duration || ANIMATION_DURATION.NORMAL
            const effectiveDuration = duration * (
              animationQuality.value === 'low' ? 0.3 :
              animationQuality.value === 'medium' ? 0.6 : 1
            )
            const easing = animation.easing || EASING.EASE_OUT

            // Use Web Animations API for consistency and proper control
            const anim = el.animate(keyframes as Keyframe[], {
              duration: Math.max(1, effectiveDuration), // Ensure minimum 1ms duration
              easing: easing,
              fill: 'forwards' as FillMode
            })

            activeAnimations.value.push(anim)

            // Add completion callback for last element
            if (index === elements.length - 1) {
              anim.addEventListener('finish', () => {
                emit('stagger-complete')
              })
            }
          }
        }, delay)

        // Track timeout ID for cleanup
        activeTimeouts.value.push(timeoutId)
      })
    }

    // Auto-trigger on mount or play
    watch([isMounted, isPlaying], ([mounted, playing]) => {
      if (mounted && playing && autoplay) {
        nextTick(() => triggerStagger())
      }
    }, { immediate: true })

    staggerAnimation = {
      elements: staggerElements,
      triggerStagger,
      activeTimeouts,
      activeAnimations
    }
  }

  // ===============================
  // MOTION PATH ANIMATIONS
  // ===============================
  let motionPathAnimation:
    | {
        progress: Ref<number>
        setProgress: (progress: number) => void
      }
    | undefined

  if (motionPath) {
    const motionProgress = ref(0)
    const motionStartTime = ref<number | null>(null)

    const setProgress = (progress: number) => {
      motionProgress.value = Math.max(0, Math.min(1, progress))
      updateMotionPathPosition()
    }

    const updateMotionPathPosition = () => {
      const targetEl = unref(target)
      if (!targetEl) return

      const progress = motionProgress.value

      if (motionPath.type === 'circular') {
        const radius = typeof motionPath.radius === 'number'
          ? motionPath.radius
          : motionPath.radius?.x || 100

        const startAngle = (motionPath.startAngle || 0) * (Math.PI / 180)
        const direction = motionPath.direction === 'counterclockwise' ? -1 : 1
        const angle = startAngle + (direction * 2 * Math.PI * progress)

        const x = Math.cos(angle) * radius
        const y = Math.sin(angle) * radius

        // Set CSS custom properties for flexibility
        setCSSVar('--motion-x', `${x}px`)
        setCSSVar('--motion-y', `${y}px`)

        if (motionPath.autoRotate) {
          const rotation = -angle * (180 / Math.PI)
          setCSSVar('--motion-rotation', `${rotation}deg`)
        } else {
          setCSSVar('--motion-rotation', '0deg')
        }

      } else if (motionPath.type === 'elliptical') {
        const radiusX = typeof motionPath.radius === 'object'
          ? motionPath.radius.x
          : motionPath.radius || 100
        const radiusY = typeof motionPath.radius === 'object'
          ? motionPath.radius.y
          : motionPath.radius || 60

        const startAngle = (motionPath.startAngle || 0) * (Math.PI / 180)
        const direction = motionPath.direction === 'counterclockwise' ? -1 : 1
        const angle = startAngle + (direction * 2 * Math.PI * progress)

        const x = Math.cos(angle) * radiusX
        const y = Math.sin(angle) * radiusY

        // Set CSS custom properties for flexibility
        setCSSVar('--motion-x', `${x}px`)
        setCSSVar('--motion-y', `${y}px`)

        if (motionPath.autoRotate) {
          const rotation = -angle * (180 / Math.PI)
          setCSSVar('--motion-rotation', `${rotation}deg`)
        } else {
          setCSSVar('--motion-rotation', '0deg')
        }

      } else if (motionPath.type === 'custom' && motionPath.path) {
        // For custom SVG paths - would need more complex implementation
        // This is a placeholder for now
        console.warn('Custom motion paths not yet fully implemented')
      }
    }

    // Animation loop for motion path
    const motionRafControls = useRafFn(
      (args) => {
        if (!isPlaying.value || isPaused.value) {
          motionRafControls.pause()
          return
        }

        const now = args.timestamp
        if (motionStartTime.value === null) {
          motionStartTime.value = now
        }

        const elapsed = now - motionStartTime.value
        motionProgress.value = (elapsed % motionPath.duration) / motionPath.duration

        updateMotionPathPosition()
      },
      { immediate: false }
    )

    // Assign pause/resume to outer scope for use in pause()/resume() functions
    pauseMotion = motionRafControls.pause
    resumeMotion = motionRafControls.resume

    watch(isPlaying, (playing) => {
      if (playing) {
        // Reset state for new cycle (initialization happens in RAF)
        motionRafControls.resume()
      } else {
        motionRafControls.pause()
        motionStartTime.value = null
      }
    })

    motionPathAnimation = {
      progress: motionProgress,
      setProgress,
    }
  }

  // ===============================
  // ANIMATION TIMELINE
  // ===============================
  if (timeline) {
    const timelineStartTime = ref<number | null>(null)
    const executedSteps = new Set<number>()

    const timelineRafControls = useRafFn(
      (args) => {
        if (!isPlaying.value || isPaused.value) {
          timelineRafControls.pause()
          return
        }

        const now = args.timestamp

        // Initialize on first RAF callback instead of relying on watch
        if (timelineStartTime.value === null) {
          timelineStartTime.value = now
        }

        const elapsed = now - timelineStartTime.value
        const duration = timeline.duration || Infinity

        timeline.steps.forEach((step, index) => {
          if (executedSteps.has(index)) return

          const stepTime = typeof step.at === 'number' ? step.at : 0 // Handle string labels later

          if (elapsed >= stepTime) {
            executeTimelineStep(step)
            executedSteps.add(index)
          }
        })

        if (timeline.loop && elapsed >= duration) {
          // Reset for loop
          timelineStartTime.value = now
          executedSteps.clear()
        }
      },
      { immediate: false }
    )

    // Assign pause/resume to outer scope for use in pause()/resume() functions
    pauseTimeline = timelineRafControls.pause
    resumeTimeline = timelineRafControls.resume

    const executeTimelineStep = (step: UseAnimationTimelineStep) => {
      switch (step.action) {
        case 'play':
          if (step.target) {
            // Animate a different target
            const targetEl = unref(step.target)
            if (targetEl && step.animation) {
              const preset = step.animation.preset || 'fadeIn'
              const presetConfig = animationPresets[preset]
              const keyframes = step.animation.keyframes || presetConfig.keyframes
              const duration = step.animation.duration || ANIMATION_DURATION.NORMAL

              targetEl.animate(keyframes as Keyframe[], {
                duration,
                easing: step.animation.easing || EASING.EASE_OUT,
                fill: FILL_MODE.FORWARDS as FillMode,
              })
            }
          } else {
            play()
          }
          break
        case 'pause':
          pause()
          break
        case 'emit':
          if (step.event) {
            emit(step.event)
          }
          break
        case 'callback':
          step.callback?.()
          break
      }
    }

    watch(isPlaying, (playing) => {
      if (playing) {
        // Reset state for new cycle
        executedSteps.clear()
        timelineRafControls.resume()
      } else {
        timelineRafControls.pause()
        timelineStartTime.value = null
      }
    })
  }

  // ===============================
  // ENTER/EXIT PATTERN
  // ===============================
  if (enterExit) {
    const hasEntered = ref(false)

    // Set initial state based on trigger value
    watch(isMounted, (mounted) => {
      if (mounted && !enterExit.trigger.value && enterExit.exit) {
        const targetEl = unref(target)
        if (targetEl) {
          const preset = enterExit.exit.preset || 'fadeOut'
          const presetConfig = animationPresets[preset]
          const keyframes = enterExit.exit.keyframes || presetConfig.keyframes

          // Apply final exit state immediately without animation
          if (Array.isArray(keyframes) && keyframes.length > 0) {
            const finalFrame = keyframes[keyframes.length - 1] as Record<string, any>
            Object.assign(targetEl.style, finalFrame)
          }
        }
      }
    }, { immediate: true })

    watch(enterExit.trigger, async (shouldEnter, wasEntered) => {
      if (!isMounted.value) return

      if (shouldEnter && !wasEntered) {
        // Enter animation
        enterExit.onEnterStart?.()

        if (!enterExit.skipInitial || hasEntered.value) {
          const targetEl = unref(target)
          if (targetEl && enterExit.enter) {
            const preset = enterExit.enter.preset || 'fadeIn'
            const presetConfig = animationPresets[preset]
            const keyframes = enterExit.enter.keyframes || presetConfig.keyframes
            const duration = enterExit.enter.duration || ANIMATION_DURATION.NORMAL

            const anim = targetEl.animate(keyframes as Keyframe[], {
              duration,
              easing: enterExit.enter.easing || EASING.EASE_OUT,
              fill: FILL_MODE.FORWARDS as FillMode,
            })

            await anim.finished
          }
        }

        hasEntered.value = true
        enterExit.onEnterComplete?.()

      } else if (!shouldEnter && wasEntered) {
        // Exit animation
        enterExit.onExitStart?.()

        if (!enterExit.skipInitial) {
          const targetEl = unref(target)
          if (targetEl && enterExit.exit) {
            const preset = enterExit.exit.preset || 'fadeOut'
            const presetConfig = animationPresets[preset]
            const keyframes = enterExit.exit.keyframes || presetConfig.keyframes
            const duration = enterExit.exit.duration || ANIMATION_DURATION.FAST

            const anim = targetEl.animate(keyframes as Keyframe[], {
              duration,
              easing: enterExit.exit.easing || EASING.EASE_OUT,
              fill: FILL_MODE.FORWARDS as FillMode,
            })

            await anim.finished
          }
        }

        enterExit.onExitComplete?.()
      }
    }, { immediate: !enterExit.skipInitial })
  }

  // ===============================
  // CSS VARIABLE INTEGRATION
  // ===============================
  const cssVarWatchers: Array<() => void> = []
  const appliedCSSVars = new Set<string>()

  const setCSSVar = (name: string, value: string | number) => {
    const targetEl = unref(target)
    if (!targetEl) return

    const varName = name.startsWith('--') ? name : `--${name}`
    targetEl.style.setProperty(varName, String(value))
    appliedCSSVars.add(varName)
  }

  const getCSSVar = (name: string): string | undefined => {
    const targetEl = unref(target)
    if (!targetEl) return undefined

    const varName = name.startsWith('--') ? name : `--${name}`
    return getComputedStyle(targetEl).getPropertyValue(varName) || undefined
  }

  const syncCSSVars = (vars: Record<string, Ref<string | number> | ComputedRef<string | number>>): (() => void) => {
    const stopHandles = Object.entries(vars).map(([name, valueRef]) => {
      return watch(
        valueRef,
        (value) => {
          setCSSVar(name, value)
        },
        { immediate: true }
      )
    })

    cssVarWatchers.push(...stopHandles)

    return () => {
      stopHandles.forEach(stop => stop())
    }
  }

  // ===============================
  // CLEANUP
  // ===============================
  const cleanup = () => {
    // Issue #2 Fix: Set playback flags BEFORE stopping RAF to prevent race conditions
    isPlaying.value = false
    isPaused.value = false

    // Stop all RAF loops BEFORE state reset
    pauseSequence?.()
    pauseMotion?.()
    pauseTimeline?.()

    // Now call stop() to cleanup other resources
    stop()

    // Remove all CSS variables
    const targetEl = unref(target)
    if (targetEl) {
      appliedCSSVars.forEach(varName => {
        targetEl.style.removeProperty(varName)
      })
      appliedCSSVars.clear()
    }

    // Remove finish listeners
    const anim = webAnimation?.animate.value
    if (anim && finishListeners.has(anim)) {
      const listener = finishListeners.get(anim)!
      anim.removeEventListener('finish', listener)
      finishListeners.delete(anim)
    }

    // Cleanup event listeners
    stopListeners.forEach((unsubscribe) => unsubscribe())
    cssVarWatchers.forEach((unsubscribe) => unsubscribe())

    // Cancel animations
    webAnimation?.cancel()

    // Cleanup progress interval and watcher
    if (activeProgressInterval.value !== null) {
      clearInterval(activeProgressInterval.value)
      activeProgressInterval.value = null
    }
    progressWatchStop?.() // Stop the progress watch itself

    // Cleanup navigation listeners (Issue #3)
    navigationCleanup.value?.()
    navigationCleanup.value = null

    // Issue #4 Fix: Clear stagger timeouts in cleanup, not just stop()
    if (staggerAnimation?.activeTimeouts) {
      staggerAnimation.activeTimeouts.value.forEach(id => clearTimeout(id))
      staggerAnimation.activeTimeouts.value = []
    }
    if (staggerAnimation?.activeAnimations) {
      staggerAnimation.activeAnimations.value.forEach(anim => anim.cancel())
      staggerAnimation.activeAnimations.value = []
    }

    // Reset state refs
    if (motionPathAnimation) {
      motionPathAnimation.progress.value = 0
    }
    if (timedSequence) {
      timedSequence.currentIndex.value = 0
      timedSequence.cycleIndex.value = 0
    }

    // Unregister consumer and cleanup state cache if no consumers remain
    if (stateName && stateConsumers.has(stateName)) {
      const consumers = stateConsumers.get(stateName)!
      consumers.delete(consumerId)

      // Issue #6 Fix: Use nextTick for atomic cleanup
      cleanupStateName(stateName, consumers)
    }
  }

  // Only register onUnmounted if we're in a component context
  // getCurrentInstance returns null if called outside setup()
  if (getCurrentInstance()) {
    onUnmounted(cleanup)
  }
  // Note: If not in component context, caller must manually call cleanup()
  // This is expected for tests and utilities that create animations dynamically

  // ===============================
  // RETURN API
  // ===============================
  return {
    // Playback controls
    play,
    pause,
    stop,
    resume,

    // State
    isPlaying,
    state,

    // Events
    emit,
    on,

    // Environment
    canAnimate,
    animationQuality,
    shouldPauseAnimations,

    // Web Animations
    ...(webAnimation && {
      animate: webAnimation.animate,
      animateIn: webAnimation.animateIn,
      animateOut: webAnimation.animateOut,
    }),

    // Timed Sequence
    ...(timedSequence && {
      sequence: timedSequence,
    }),

    // Scroll
    ...(scrollAnimation && {
      scroll: scrollAnimation,
    }),

    // Stagger
    ...(staggerAnimation && {
      stagger: staggerAnimation,
    }),

    // Motion Path
    ...(motionPathAnimation && {
      motionPath: motionPathAnimation,
    }),

    // CSS Variables
    setCSSVar,
    getCSSVar,
    syncCSSVars,

    // Cleanup
    cleanup,
  }
}

