export interface UseAnimationLifecycleOptions {
  beforePlay?: () => void | Promise<void>
  afterPlay?: () => void
  beforePause?: () => void
  afterPause?: () => void
  beforeStop?: () => void
  afterStop?: () => void
  beforeResume?: () => void
