// Idea card floating with stagger
const cardAnimation = useAnimation(theaterRef, {
  respectEnvironment: true,
  stagger: {
    children: cardRefs,
    delay: 800, // Stagger start time between cards
    from: 'start'
  },
  animation: {
    keyframes: [
      { opacity: 0, transform: 'translate(-50%, 80px) scale(0.9)' },
      { opacity: 1, transform: 'translate(-50%, 0px) scale(1)', offset: 0.15 },
      { opacity: 1, transform: 'translate(-50%, -80px) scale(0.95)', offset: 0.7 },
      { opacity: 0, transform: 'translate(-50%, -120px) scale(0.9)' }
    ],
    duration: 2400,
    easing: 'ease-in-out'
  }
})

// Manual loop control to avoid autoplay conflicts
let loopTimeoutId: ReturnType<typeof setTimeout> | null = null

const startCardLoop = () => {
  if (!props.active) return

  // Clear any existing timeout
  if (loopTimeoutId !== null) {
    clearTimeout(loopTimeoutId)
  }

  // Start stagger animation
  cardAnimation.stagger?.triggerStagger()

  // Schedule next loop (stagger delay * 3 cards + animation duration)
  loopTimeoutId = setTimeout(() => {
    startCardLoop()
  }, (800 * 2) + 2400) // Last card starts at 1600ms, runs for 2400ms = 4000ms total
}

const stopCardLoop = () => {
  if (loopTimeoutId !== null) {
    clearTimeout(loopTimeoutId)
    loopTimeoutId = null
  }
}

// Watch for active state changes
watchEffect(() => {
  if (props.active && cardAnimation.canAnimate.value) {
    startCardLoop()
  } else {
    stopCardLoop()
  }
})

