// Light bulb glow animation
const bulbAnimation = useAnimation(theaterRef, {
  autoplay: { enabled: computed(() => props.active), threshold: 0 },
  respectEnvironment: true,
  stagger: {
    children: bulbRefs,
    delay: 500,
    from: 'start'
  },
  animation: {
    keyframes: [
      { opacity: 0.4, filter: 'drop-shadow(0 0 0px currentColor)' },
      { opacity: 1, filter: 'drop-shadow(0 0 8px currentColor)' },
      { opacity: 0.4, filter: 'drop-shadow(0 0 0px currentColor)' }
    ],
    duration: 2000,
    easing: 'ease-in-out'
  },
  lifecycle: {
    afterPlay: () => {
      // Restart animation for continuous loop
      setTimeout(() => {
        if (props.active) {
          bulbAnimation.stagger?.triggerStagger()
        }
      }, 2000)
    }
  }
})

