---
import Navbar from "@vue/global/Navbar.vue";
import Footer from "@vue/global/Footer.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import { SITE_DATA } from "@/constants/siteData";
import "../styles/typography.css";

interface Props {
  title: string;
  navVariant?: 'default' | 'landing';
  footerVariant?: 'default' | 'landing';
}

const siteData = SITE_DATA;

const { title } = Astro.props;
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />

    <!-- Inter font (industry standard for SaaS 2025) -->
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap" rel="stylesheet">

    <style is:global>
      html {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
        font-family: 'Inter', system-ui, sans-serif;
      }

      [data-theme="dark"] {
        --bg-color-default: #0D1117;
        --text-color-default: white;
        color: white;
        background-color: #0D1117;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body>
    <Navbar client:load transition:persist variant={Astro.props.navVariant || 'default'}/>
    <slot />
    <Footer
      client:load
      transition:persist
      footerVariant={Astro.props.footerVariant || (Astro.props.navVariant === 'landing' ? 'landing' : 'default')}
    />
    <CookieGuard client:load transition:persist />
    <SessionInitializer client:load />

    <!-- Teleport Layer -->
    <div id="teleport-layer" style="position: fixed; top: 0; left: 0; width: 100%; height: 100%; pointer-events: none; z-index: 9999;"></div>
  </body>
</html>

---
import { ClientRouter } from "astro:transitions";
import Themes from "astro-themes";
import "@fontsource/noto-color-emoji";
import "@fontsource/inter";
import "../../styles/global.css";
import "sweetalert2/src/sweetalert2.scss";

interface Props {
  title: string;
  description: string;
  image?: string;
  noindex?: boolean;
}

const {
  title,
  description,
  image = "/og-image.jpg",
  noindex = false,
} = Astro.props;

const canonicalURL = new URL(Astro.url.pathname, Astro.site);
const siteName = "Socialaize";
const fullTitle = `${title} - ${siteName}`;
---

<!-- Essential Meta Tags -->
<meta charset="UTF-8" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<meta name="generator" content={Astro.generator} />

<!-- SEO Meta Tags -->
<title>{fullTitle}</title>
<meta name="description" content={description} />
<link rel="canonical" href={canonicalURL} />
{noindex && <meta name="robots" content="noindex" />}

<!-- OpenGraph Meta Tags -->
<meta property="og:title" content={fullTitle} />
<meta property="og:description" content={description} />
<meta property="og:type" content="website" />
<meta property="og:url" content={canonicalURL} />
<meta property="og:image" content={new URL(image, Astro.url)} />
<meta property="og:site_name" content={siteName} />

<!-- Twitter Meta Tags -->
<meta name="twitter:card" content="summary_large_image" />
<meta name="twitter:title" content={fullTitle} />
<meta name="twitter:description" content={description} />
<meta name="twitter:image" content={new URL(image, Astro.url)} />

<!-- Favicon -->
<link rel="icon" type="image/webp" href="/favicon.webp" />

<!-- Theme Color -->
<meta
  name="theme-color"
  content="#ffffff"
  media="(prefers-color-scheme: light)"
/>
<meta
  name="theme-color"
  content="#333333"
  media="(prefers-color-scheme: dark)"
/>

<!-- Client Router for Transitions -->
<ClientRouter />

<!-- Themes Handler (light/dark mode) -->
<Themes />

