     1→<template>
     2→  <div
     3→    ref="iconRef"
     4→    class="social-icon"
     5→    :class="iconClasses"
     6→    :style="iconStyles"
     7→  >
     8→    <Icon
     9→      :icon="`simple-icons:${provider}`"
    10→      :class="iconColorClass"
    11→    />
    12→  </div>
    13→</template>
    14→
    15→<script setup lang="ts">
    16→import { computed, useTemplateRef, onUnmounted } from 'vue'
    17→import { useAnimate } from '@vueuse/core'
    18→import { Icon } from '@iconify/vue'
    19→
    20→interface Props {
    21→  provider: string
    22→  pulseIntensity?: number
    23→  pulseDelay?: number
    24→  rotation?: number
    25→}
    26→
    27→const props = withDefaults(defineProps<Props>(), {
    28→  pulseIntensity: 1.3,
    29→  pulseDelay: 0,
    30→  rotation: 0
    31→})
    32→
    33→const iconRef = useTemplateRef('iconRef')
    34→
    35→// Provider color mapping
    36→const providerColors: Record<string, string> = {
    37→  instagram: 'text-pink-400',
    38→  facebook: 'text-indigo-400',
    39→  threads: 'text-sky-400',
    40→  linkedin: 'text-blue-400',
    41→  tiktok: 'text-cyan-300',
    42→  youtube: 'text-red-500',
    43→  twitter: 'text-blue-400',
    44→  pinterest: 'text-red-400',
    45→  snapchat: 'text-yellow-400',
    46→  discord: 'text-purple-400',
    47→  telegram: 'text-blue-500',
    48→  whatsapp: 'text-green-400'
    49→}
    50→
    51→const iconColorClass = computed(() =>
    52→  `w-6 h-6 ${providerColors[props.provider] || 'text-gray-400'}`
    53→)
    54→
    55→const iconClasses = computed(() => [
    56→  'bg-white/90',
    57→  'dark:bg-white/10',
    58→  'border',
    59→  'border-black/8',
    60→  'dark:border-white/20',
    61→  'shadow-lg',
    62→  'dark:shadow-black/30'
    63→])
    64→
    65→const iconStyles = computed(() => ({
    66→  transform: `translateY(-50%) rotate(${props.rotation}deg)`
    67→}))
    68→
    69→// Base transform for composition
    70→const baseTransform = computed(() => `translateY(-50%) rotate(${props.rotation}deg)`)
    71→
    72→// Pulse animation keyframes with preserved base transform
    73→const pulseKeyframes = computed(() => [
    74→  {
    75→    transform: `${baseTransform.value} scale(1)`,
    76→    boxShadow: '0 2px 10px rgba(102, 126, 234, 0.25)'
    77→  },
    78→  {
    79→    transform: `${baseTransform.value} scale(${props.pulseIntensity})`,
    80→    boxShadow: '0 0 30px rgba(102, 126, 234, 0.65), 0 0 60px rgba(118, 75, 162, 0.45)'
    81→  },
    82→  {
    83→    transform: `${baseTransform.value} scale(1)`,
    84→    boxShadow: '0 2px 10px rgba(102, 126, 234, 0.25)'
    85→  }
    86→])
    87→
    88→let pulseAnimation: ReturnType<typeof useAnimate> | null = null
    89→let glowResetTimeout: ReturnType<typeof setTimeout> | null = null
    90→
    91→const withGlow = (enabled: boolean) => {
    92→  const el = iconRef.value
    93→  if (!el) return
    94→  el.classList.toggle('hover-glow', enabled)
    95→}
    96→
    97→const pulse = () => {
    98→  if (pulseAnimation) {
    99→    pulseAnimation.cancel()
   100→  }
   101→  if (glowResetTimeout) {
   102→    clearTimeout(glowResetTimeout)
   103→    glowResetTimeout = null
   104→  }
   105→
   106→  pulseAnimation = useAnimate(
   107→    iconRef,
   108→    pulseKeyframes.value,
   109→    {
   110→      duration: 600,
   111→      delay: 0,
   112→      easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
   113→      fill: 'forwards'
   114→    }
   115→  )
   116→
   117→  withGlow(true)
   118→  pulseAnimation.play()
   119→
   120→  glowResetTimeout = setTimeout(() => {
   121→    withGlow(false)
   122→    glowResetTimeout = null
   123→  }, 650)
   124→}
   125→const stop = () => {
   126→  if (pulseAnimation) {
   127→    pulseAnimation.cancel()
   128→    pulseAnimation = null
   129→  }
   130→  if (glowResetTimeout) {
   131→    clearTimeout(glowResetTimeout)
   132→    glowResetTimeout = null
   133→  }
   134→  withGlow(false)
   135→}
   136→
   137→onUnmounted(() => {
   138→  stop()
   139→})
   140→
   141→defineExpose({
   142→  pulse,
   143→  stop
   144→})
   145→</script>
