   148→.social-icon {
   149→  position: absolute;
   150→  right: -30px;
   151→  top: 50%;
   152→  width: 60px;
   153→  height: 60px;
   154→  border-radius: 50%;
   155→  display: flex;
   156→  justify-content: center;
   157→  align-items: center;
   158→  backdrop-filter: blur(5px);
   159→  z-index: 15;
   160→  transition: all 0.3s ease;
   161→  will-change: transform, box-shadow;
   162→  box-shadow: 0 2px 10px rgba(102, 126, 234, 0.25);
   163→}
   164→
   165→.social-icon.hover-glow,
   166→.social-icon:hover {
   167→  box-shadow:
   168→    0 0 30px rgba(102, 126, 234, 0.6),
   169→    0 0 60px rgba(118, 75, 162, 0.45);
   170→}
   171→
   172→/* Enhanced glow for dark mode */
   173→@media (prefers-color-scheme: dark) {
   174→  .social-icon {
   175→    box-shadow: 0 2px 10px rgba(102, 126, 234, 0.25);
   176→  }
   177→
   178→  .social-icon.pulsing {
   179→    box-shadow:
   180→      0 0 20px rgba(255, 255, 255, 0.6),
   181→      0 0 40px rgba(255, 255, 255, 0.3),
   182→      0 4px 16px rgba(0, 0, 0, 0.4);
   183→  }
   184→}
   185→
   186→/* Responsive adjustments */
   187→@media (max-width: 640px) {
   188→  .social-icon {
   189→    width: 50px;
   190→    height: 50px;
   191→    right: -25px;
   192→  }
   193→}
   194→
   195→@media (max-width: 480px) {
   196→  .social-icon {
   197→    width: 45px;
   198→    height: 45px;
   199→    right: -22px;
   200→  }
   201→}
   202→
   203→/* Reduced motion support */
   204→@media (prefers-reduced-motion: reduce) {
   205→  .social-icon {
   206→    transition: none !important;
   207→    animation: none !important;
   208→  }
   209→}
   210→</style>
