     7→<script setup lang="ts">
     8→import { useTemplateRef } from 'vue'
     9→import { useAnimation } from '@/components/composables/useAnimation'
    10→
    11→interface Props {
    12→  pulseIntensity?: number
    13→  cycleDuration?: number
    14→}
    15→
    16→const props = withDefaults(defineProps<Props>(), {
    17→  pulseIntensity: 1.1,
    18→  cycleDuration: 5
    19→})
    20→
    21→const orbRef = useTemplateRef('orbRef')
    22→
    23→// Unified animation with automatic event system integration
    24→const animation = useAnimation(orbRef, {
    25→  animation: {
    26→    keyframes: [
    27→      {
    28→        transform: 'scale(1)',
    29→        boxShadow: '0 0 20px rgba(102, 126, 234, 0.3), 0 0 40px rgba(118, 75, 162, 0.2)'
    30→      },
    31→      {
    32→        transform: `scale(${props.pulseIntensity})`,
    33→        boxShadow: '0 0 30px rgba(102, 126, 234, 0.5), 0 0 60px rgba(118, 75, 162, 0.35)'
    34→      },
    35→      {
    36→        transform: 'scale(1)',
    37→        boxShadow: '0 0 20px rgba(102, 126, 234, 0.3), 0 0 40px rgba(118, 75, 162, 0.2)'
    38→      }
    39→    ],
    40→    duration: props.cycleDuration * 1000,
    41→    easing: 'cubic-bezier(0.4, 0, 0.6, 1)'
    42→  },
    43→  respectEnvironment: true,
    44→  events: {
    45→    busName: 'conductor',
    46→    listenForStop: 'stop-all'
    47→  }
    48→})
    49→
    50→// Expose controls for parent component (maintains backward compatibility)
    51→const startPulse = () => animation.play()
    52→const stopPulse = () => animation.stop()
    53→
    54→// Listen to orchestrate-start event manually (not using autoplay)
    55→const unsubscribeOrchestrate = animation.on('orchestrate-start', () => {
    56→  startPulse()
    57→})
    58→
    59→// Cleanup
    60→const cleanup = () => {
    61→  unsubscribeOrchestrate()
    62→  animation.cleanup()
    63→}
    64→
    65→defineExpose({
    66→  startPulse,
    67→  stopPulse,
    68→  orbRef,
    69→  cleanup
    70→})
    71→</script>
    72→
    73→<style scoped>
    74→.ai-orb {
    75→  width: 100px;
    76→  height: 100px;
    77→  background: radial-gradient(circle,
    78→    rgba(102, 126, 234, 0.8) 0%,
    79→    rgba(118, 75, 162, 0.8) 100%);
    80→  border-radius: 50%;
    81→  position: relative;
    82→  z-index: 10;
    83→  display: flex;
    84→  align-items: center;
    85→  justify-content: center;
    86→  padding: 10px;
    87→  will-change: transform, box-shadow;
    88→  contain: layout style paint;
    89→}
    90→
    91→.ai-orb::before {
    92→  content: '';
    93→  position: absolute;
    94→  inset: 8px;
    95→  border-radius: 50%;
    96→  background: radial-gradient(circle at 30% 30%,
    97→    rgba(255, 255, 255, 0.3) 0%,
    98→    rgba(255, 255, 255, 0.1) 50%,
    99→    transparent 100%);
   100→  pointer-events: none;
   101→}
   102→
   103→.orb-image {
   104→  width: 80%;
   105→  height: 80%;
   106→  object-fit: contain;
   107→  position: relative;
   108→  z-index: 1;
   109→}
   110→
   111→/* Dark mode enhancements */
   112→@media (prefers-color-scheme: dark) {
