    77→<script setup lang="ts">
    78→import { onMounted, onUnmounted, nextTick } from "vue";
    79→import Typed from "typed.js";
    80→import { Icon } from "@iconify/vue";
    81→import AiConductorGraphicV2 from "../../ui/AiConductorGraphicV2.vue";
    82→import AiConductorGraphic from "../../ui/AiConductorGraphic.vue";
    83→import { usePerformance } from "@/components/composables/usePerformance";
    84→
    85→
    86→
    87→
    88→// Performance monitoring with all VueUse composables included
    89→const {
    90→  measureOperation,
    91→  optimizations,
    92→  batteryInfo,
    93→  networkInfo,
    94→  windowRef
    95→} = usePerformance();
    96→
    97→const typewriterStrings: string[] = ['Post once —', 'Schedule smarter —', 'Grow faster —'];
    98→
    99→let typedInstance: any = null;
   100→let cleanupRefs: {
   101→  timeouts: Set<ReturnType<typeof setTimeout>>;
   102→  callbacks: Set<number>;
   103→} = {
   104→  timeouts: new Set(),
   105→  callbacks: new Set()
   106→};
   107→
   108→onMounted(() => {
   109→  const initOperation = measureOperation('chatInitTime')
   110→
   111→  try {
   112→    // Check performance conditions for adaptive animations
   113→    const prefersReducedMotion = optimizations.reducedMotion
   114→
   115→    const lowBattery = optimizations.batteryOptimization
   116→
   117→    const slowNetwork = networkInfo.effectiveType &&
   118→      (networkInfo.effectiveType === 'slow-2g' ||
   119→       networkInfo.effectiveType === '2g')
   120→
   121→    const shouldOptimizeAnimations = prefersReducedMotion ||
   122→      (optimizations.batteryOptimization && lowBattery) ||
   123→      slowNetwork
   124→
   125→    // Adaptive typewriter configuration
   126→    const typewriterConfig = {
   127→      strings: typewriterStrings,
   128→      smartBackspace: false,
   129→      shuffle: false, // Disabled shuffle to prevent buggy behavior
   130→      typeSpeed: shouldOptimizeAnimations ? 80 : 40, // Smoother typing speed
   131→      backSpeed: shouldOptimizeAnimations ? 50 : 25, // Smoother backspace speed
   132→      backDelay: shouldOptimizeAnimations ? 1500 : 2500, // Longer pause before deleting
   133→      loop: !shouldOptimizeAnimations, // Disable loop for reduced motion
   134→      showCursor: true,
   135→      cursorChar: '|',
   136→    };
   137→
   138→    // Add progressive enhancement classes
   139→    if (windowRef.value) {
   140→      windowRef.value.document.documentElement.classList.add('js-enabled')
   141→      if (shouldOptimizeAnimations) {
   142→        windowRef.value.document.documentElement.classList.add('reduced-motion')
   143→      }
   144→    }
   145→
   146→    // Initialize typewriter with performance considerations
   147→    if (!shouldOptimizeAnimations) {
   148→      typedInstance = new Typed(".typewriter", typewriterConfig);
   149→    } else {
   150→      // For reduced performance scenarios, show static text
   151→      if (windowRef.value) {
   152→        const typewriterElement = windowRef.value.document.querySelector('.typewriter');
   153→        if (typewriterElement) {
   154→          typewriterElement.textContent = typewriterStrings[0].replace(' —', '');
   155→        }
   156→      }
   157→    }
   158→
   159→    // Use requestIdleCallback for non-critical operations
   160→    nextTick(() => {
   161→      if (windowRef.value && 'requestIdleCallback' in windowRef.value && !shouldOptimizeAnimations) {
   162→        const callbackId = (windowRef.value as any).requestIdleCallback(() => {
   163→          cleanupRefs.callbacks.delete(callbackId)
   164→          // Non-critical initialization code
   165→        });
   166→        cleanupRefs.callbacks.add(callbackId)
   167→      } else {
   168→        // Fallback for browsers without requestIdleCallback or when optimizing
   169→        const timeoutId = setTimeout(() => {
   170→          cleanupRefs.timeouts.delete(timeoutId)
   171→          // Simplified initialization
   172→        }, shouldOptimizeAnimations ? 0 : 100);
   173→        cleanupRefs.timeouts.add(timeoutId)
   174→      }
   175→    });
   176→
   177→    initOperation.end()
   178→  } catch (error) {
   179→    console.error('HeroSection: Error during typewriter initialization:', error)
   180→    initOperation.end()
   181→  }
   182→});
   183→
   184→onUnmounted(() => {
   185→  // Clean up typewriter instance
   186→  if (typedInstance) {
   187→    typedInstance.destroy();
   188→    typedInstance = null;
   189→  }
   190→
   191→  // Clear all timeouts
   192→  cleanupRefs.timeouts.forEach(timeout => {
   193→    clearTimeout(timeout);
   194→  });
   195→  cleanupRefs.timeouts.clear();
   196→
   197→  // Cancel idle callbacks
   198→  cleanupRefs.callbacks.forEach(callbackId => {
   199→    if (windowRef.value && 'cancelIdleCallback' in windowRef.value) {
   200→      (windowRef.value as any).cancelIdleCallback(callbackId);
   201→    }
   202→  });
   203→  cleanupRefs.callbacks.clear();
   204→});
   205→</script>
