   418→          type: 'success',
   419→          title: `Good ${metric.toUpperCase()}`,
   420→          description: `${metric.toUpperCase()} is ${value}ms (excellent!)`,
   421→          impact: 'low',
   422→          metric: metric as keyof PerformanceMetrics,
   423→          value,
   424→          threshold: threshold.good
   425→        });
   426→      }
   427→    } else if ('critical' in threshold) {
   428→      // Memory metric
   429→      if (value > threshold.critical) {
   430→        addInsight({
   431→          type: 'error',
   432→          title: `Critical Memory Usage`,
   433→          description: `Memory usage is ${(value * 100).toFixed(1)}% (critical: >${(threshold.critical * 100).toFixed(1)}%)`,
   434→          impact: 'high',
   435→          recommendation: `Reduce memory usage immediately`,
   436→          metric: metric as keyof PerformanceMetrics,
   437→          value,
   438→          threshold: threshold.critical
   439→        });
   440→      } else if (value > threshold.warning) {
   441→        addInsight({
   442→          type: 'warning',
   443→          title: `High Memory Usage`,
   444→          description: `Memory usage is ${(value * 100).toFixed(1)}% (warning: >${(threshold.warning * 100).toFixed(1)}%)`,
   445→          impact: 'medium',
   446→          recommendation: `Monitor memory usage closely`,
   447→          metric: metric as keyof PerformanceMetrics,
   448→          value,
   449→          threshold: threshold.warning
   450→        });
   451→      } else {
   452→        addInsight({
   453→          type: 'success',
   454→          title: `Good Memory Usage`,
   455→          description: `Memory usage is ${(value * 100).toFixed(1)}% (healthy!)`,
   456→          impact: 'low',
   457→          metric: metric as keyof PerformanceMetrics,
   458→          value,
   459→          threshold: threshold.warning
   460→        });
   461→      }
   462→    }
   463→  };
   464→
   465→  // Performance scoring
   466→  const calculatePerformanceScore = computed((): number => {
   467→    let score = 100;
   468→    let checks = 0;
   469→
   470→    // Core Web Vitals scoring
   471→    if (metrics.fcp) {
   472→      checks++;
   473→      if (metrics.fcp > thresholds.fcp.needs_improvement) score -= 25;
   474→      else if (metrics.fcp > thresholds.fcp.good) score -= 10;
   475→    }
   476→
   477→    if (metrics.lcp) {
   478→      checks++;
   479→      if (metrics.lcp > thresholds.lcp.needs_improvement) score -= 25;
   480→      else if (metrics.lcp > thresholds.lcp.good) score -= 10;
   481→    }
   482→
   483→    if (metrics.fid) {
   484→      checks++;
