   499→      if (memoryRatio > thresholds.memory.critical) score -= 30;
   500→      else if (memoryRatio > thresholds.memory.warning) score -= 15;
   501→    }
   502→
   503→    return Math.max(0, checks > 0 ? score : 0);
   504→  });
   505→
   506→  // Performance grade
   507→  const performanceGrade = computed((): string => {
   508→    const score = calculatePerformanceScore.value;
   509→    if (score >= 90) return 'A';
   510→    if (score >= 80) return 'B';
   511→    if (score >= 70) return 'C';
   512→    if (score >= 60) return 'D';
   513→    return 'F';
