   592→
   593→    if (batteryUpdateTimer) {
   594→      clearInterval(batteryUpdateTimer);
   595→      batteryUpdateTimer = null;
   596→    }
   597→
   598→    console.log('🔍 Performance monitoring stopped');
   599→  };
   600→
   601→  const measureOperation = (name: keyof PerformanceMetrics) => {
   602→    if (!isMounted.value || !windowRef.value) {
   603→      return { end: () => {} };
   604→    }
   605→
   606→    const startTime = windowRef.value.performance.now();
   607→    
   608→    return {
   609→      end: () => measureCustomMetric(name, startTime)
   610→    };
   611→  };
