import { ref, reactive, computed, watch, onMounted, onUnmounted, nextTick, readonly } from 'vue';
import { useMounted, useBattery, useNetwork, usePreferredReducedMotion } from '@vueuse/core';
import { useSsrGlobals } from './useSsrGlobals';

// TypeScript interfaces
export interface PerformanceMetrics {
  // Core Web Vitals
  fcp?: number; // First Contentful Paint
  lcp?: number; // Largest Contentful Paint
  fid?: number; // First Input Delay
  cls?: number; // Cumulative Layout Shift

  // Additional metrics
  ttfb?: number; // Time to First Byte
  fmp?: number; // First Meaningful Paint
  tbt?: number; // Total Blocking Time

  // Memory metrics
  jsHeapSizeLimit?: number;
  usedJSHeapSize?: number;
  totalJSHeapSize?: number;

  // Navigation metrics
  domContentLoaded?: number;
  loadComplete?: number;

  // Custom metrics
  chatInitTime?: number;
  notificationProcessTime?: number;
  realtimeConnectionTime?: number;
}

export interface PerformanceThresholds {
  fcp: { good: number; needs_improvement: number };
  lcp: { good: number; needs_improvement: number };
  fid: { good: number; needs_improvement: number };
  cls: { good: number; needs_improvement: number };
  memory: { warning: number; critical: number };
}

export interface BatteryInfo {
  charging: boolean;
  level: number;
  chargingTime?: number;
  dischargingTime?: number;
}

export interface NetworkInfo {
  effectiveType?: string;
  downlink?: number;
  rtt?: number;
  saveData?: boolean;
}

export interface PerformanceOptimizations {
  lazyLoading: boolean;
  imageOptimization: boolean;
  codesplitting: boolean;
  serviceWorker: boolean;
  memoryManagement: boolean;
  batteryOptimization: boolean;
  reducedMotion: boolean;
}

export interface PerformanceInsight {
  id: string;
  type: 'warning' | 'error' | 'suggestion' | 'success';
  title: string;
  description: string;
  impact: 'low' | 'medium' | 'high' | 'critical';
  recommendation?: string;
  metric?: keyof PerformanceMetrics;
  value?: number;
  threshold?: number;
  timestamp: Date;
}

export const usePerformance = () => {
  // SSR-safe globals and mounting state
  const isMounted = useMounted();
  const { windowRef } = useSsrGlobals();

  // VueUse composables (SSR-safe)
  const batteryInfo = useBattery();
  const networkInfo = useNetwork();
  const prefersReducedMotion = usePreferredReducedMotion();

  // State
  const metrics = reactive<PerformanceMetrics>({});
  const insights = ref<PerformanceInsight[]>([]);
  const isMonitoring = ref(false);
  const devicePixelRatio = computed(() => windowRef.value?.devicePixelRatio || 1);

  // Performance thresholds based on Google's Core Web Vitals
  const thresholds: PerformanceThresholds = {
    fcp: { good: 1800, needs_improvement: 3000 },
    lcp: { good: 2500, needs_improvement: 4000 },
    fid: { good: 100, needs_improvement: 300 },
    cls: { good: 0.1, needs_improvement: 0.25 },
    memory: { warning: 0.8, critical: 0.95 } // As percentage of available heap
  };

  // Performance optimization settings (reactive for SSR safety)
  const optimizations = reactive<PerformanceOptimizations>({
    lazyLoading: true,
    imageOptimization: true,
    codesplitting: true,
    serviceWorker: false,
    memoryManagement: true,
    batteryOptimization: false,
    reducedMotion: false
  });

  // Watch for changes and update optimizations
  watch([isMounted, windowRef, batteryInfo.level, batteryInfo.charging, prefersReducedMotion], () => {
    optimizations.serviceWorker = isMounted.value && windowRef.value ? 'serviceWorker' in windowRef.value.navigator : false;
    optimizations.batteryOptimization = batteryInfo.level.value ? batteryInfo.level.value < 0.2 && !batteryInfo.charging.value : false;
    // Only enable reduced motion if explicitly set by user AND we're mounted (not during SSR)
    optimizations.reducedMotion = isMounted.value && prefersReducedMotion.value === 'reduce';
  }, { immediate: true });

  // Observers and timers
  let performanceObserver: PerformanceObserver | null = null;
  let memoryMonitorTimer: NodeJS.Timeout | null = null;
  let batteryUpdateTimer: NodeJS.Timeout | null = null;

  // Core Web Vitals measurement
  const measureCoreWebVitals = (): void => {
    if (!isMounted.value || !windowRef.value || !('PerformanceObserver' in windowRef.value)) {
      console.warn('PerformanceObserver not supported or not mounted');
      return;
    }

    const window = windowRef.value;

    // First Contentful Paint
    const fcpObserver = new window.PerformanceObserver((list) => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1];

      if (lastEntry && lastEntry.startTime) {
        metrics.fcp = Math.round(lastEntry.startTime);
        checkThreshold('fcp', metrics.fcp);
      }
    });

    try {
      fcpObserver.observe({ entryTypes: ['paint'] });
    } catch (e) {
      console.warn('FCP observation failed:', e);
    }

    // Largest Contentful Paint
    const lcpObserver = new window.PerformanceObserver((list) => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1] as any;

      if (lastEntry && lastEntry.startTime) {
        metrics.lcp = Math.round(lastEntry.startTime);
        checkThreshold('lcp', metrics.lcp);
      }
    });

    try {
      lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });
    } catch (e) {
      console.warn('LCP observation failed:', e);
    }

    // First Input Delay
    const fidObserver = new window.PerformanceObserver((list) => {
      const entries = list.getEntries();
      const lastEntry = entries[entries.length - 1] as any;

      if (lastEntry && lastEntry.processingStart && lastEntry.startTime) {
        metrics.fid = Math.round(lastEntry.processingStart - lastEntry.startTime);
        checkThreshold('fid', metrics.fid);
      }
    });

    try {
      fidObserver.observe({ entryTypes: ['first-input'] });
    } catch (e) {
      console.warn('FID observation failed:', e);
    }

    // Cumulative Layout Shift
    let clsValue = 0;
    const clsObserver = new window.PerformanceObserver((list) => {
      for (const entry of list.getEntries() as any[]) {
        if (!entry.hadRecentInput) {
          clsValue += entry.value;
        }
      }

      metrics.cls = Math.round(clsValue * 1000) / 1000;
      checkThreshold('cls', metrics.cls);
    });

    try {
      clsObserver.observe({ entryTypes: ['layout-shift'] });
    } catch (e) {
      console.warn('CLS observation failed:', e);
    }

    // Navigation metrics
    const navObserver = new window.PerformanceObserver((list) => {
      const entries = list.getEntries();
      const navigation = entries[0] as PerformanceNavigationTiming;

      if (navigation) {
        metrics.ttfb = Math.round(navigation.responseStart - navigation.fetchStart);
        metrics.domContentLoaded = Math.round(navigation.domContentLoadedEventEnd - navigation.fetchStart);
        metrics.loadComplete = Math.round(navigation.loadEventEnd - navigation.fetchStart);
      }
    });

    try {
      navObserver.observe({ entryTypes: ['navigation'] });
    } catch (e) {
      console.warn('Navigation observation failed:', e);
    }
  };

  // Memory monitoring
  const measureMemoryUsage = (): void => {
    if (!isMounted.value || !windowRef.value) return;

    if ('memory' in windowRef.value.performance) {
      const memory = (windowRef.value.performance as any).memory;

      metrics.jsHeapSizeLimit = memory.jsHeapSizeLimit;
      metrics.usedJSHeapSize = memory.usedJSHeapSize;
      metrics.totalJSHeapSize = memory.totalJSHeapSize;

      // Check memory usage
      const memoryUsageRatio = memory.usedJSHeapSize / memory.jsHeapSizeLimit;

      if (memoryUsageRatio > thresholds.memory.critical) {
        addInsight({
          type: 'error',
          title: 'Critical Memory Usage',
          description: `Memory usage is at ${Math.round(memoryUsageRatio * 100)}% of available heap`,
          impact: 'critical',
          recommendation: 'Consider closing unused tabs or refreshing the page',
          metric: 'usedJSHeapSize',
          value: memory.usedJSHeapSize,
          threshold: memory.jsHeapSizeLimit * thresholds.memory.critical
        });
      } else if (memoryUsageRatio > thresholds.memory.warning) {
        addInsight({
          type: 'warning',
          title: 'High Memory Usage',
          description: `Memory usage is at ${Math.round(memoryUsageRatio * 100)}% of available heap`,
          impact: 'medium',
          recommendation: 'Monitor memory usage and consider optimizing heavy operations',
          metric: 'usedJSHeapSize',
          value: memory.usedJSHeapSize,
          threshold: memory.jsHeapSizeLimit * thresholds.memory.warning
        });
      }
    }
  };

  // Battery monitoring (now handled by VueUse useBattery)
  const monitorBattery = async (): Promise<void> => {
    // Battery monitoring is now handled by VueUse useBattery composable
    // We can watch for changes and add insights when needed
    watch([batteryInfo.level, batteryInfo.charging], ([level, charging]) => {
      if (level && level < 0.2 && !charging) {
        addInsight({
          type: 'suggestion',
          title: 'Battery Optimization Enabled',
          description: 'Reduced performance features to conserve battery',
          impact: 'low',
          recommendation: 'Connect to power for full functionality'
        });
      }
    });
  };

  // Network monitoring (now handled by VueUse useNetwork)
  const monitorNetwork = (): void => {
    // Network monitoring is now handled by VueUse useNetwork composable
    // We can watch for changes and add insights when needed
    watch(networkInfo.effectiveType, (effectiveType) => {
      if (effectiveType === 'slow-2g' || effectiveType === '2g') {
        addInsight({
          type: 'suggestion',
          title: 'Slow Connection Detected',
          description: 'Optimizing content delivery for slow network',
          impact: 'medium',
          recommendation: 'Image loading and animations have been optimized'
        });
      }
    });
  };

  // Custom metric measurement
  const measureCustomMetric = (name: keyof PerformanceMetrics, startTime: number): void => {
    if (!isMounted.value || !windowRef.value) return;

    const endTime = windowRef.value.performance.now();
    const duration = Math.round(endTime - startTime);

    metrics[name] = duration;

    // Add insights for slow operations
    const slowThresholds = {
      chatInitTime: 1000,
      notificationProcessTime: 200,
      realtimeConnectionTime: 3000
    };

    const threshold = slowThresholds[name as keyof typeof slowThresholds];
    if (threshold && duration > threshold) {
      addInsight({
        type: 'warning',
        title: `Slow ${name}`,
        description: `${name} took ${duration}ms (threshold: ${threshold}ms)`,
        impact: 'medium',
        recommendation: 'Consider optimizing this operation',
        metric: name,
        value: duration,
        threshold
      });
    }
  };

  // Resource monitoring
  const monitorResources = (): void => {
    if (!isMounted.value || !windowRef.value || !('PerformanceObserver' in windowRef.value)) return;

    const resourceObserver = new windowRef.value.PerformanceObserver((list) => {
      const entries = list.getEntries();

      for (const entry of entries) {
        const resource = entry as PerformanceResourceTiming;

        // Check for slow loading resources
        if (resource.duration > 2000) {
          addInsight({
            type: 'warning',
            title: 'Slow Resource Loading',
            description: `${resource.name} took ${Math.round(resource.duration)}ms to load`,
            impact: 'medium',
            recommendation: 'Consider optimizing or lazy loading this resource'
          });
        }

        // Check for large resources
        if (resource.transferSize && resource.transferSize > 1000000) { // 1MB
          addInsight({
            type: 'suggestion',
            title: 'Large Resource Detected',
            description: `${resource.name} is ${Math.round(resource.transferSize / 1024)}KB`,
            impact: 'low',
            recommendation: 'Consider compressing or splitting this resource'
          });
        }
      }
    });

    try {
      resourceObserver.observe({ entryTypes: ['resource'] });
    } catch (e) {
      console.warn('Resource observation failed:', e);
    }
  };

  // Performance insights
  const addInsight = (insight: Omit<PerformanceInsight, 'id' | 'timestamp'>): void => {
    const newInsight: PerformanceInsight = {
      id: crypto.randomUUID(),
      timestamp: new Date(),
      ...insight
    };

    insights.value.push(newInsight);

    // Keep only recent insights
    if (insights.value.length > 50) {
      insights.value = insights.value.slice(-30);
    }
  };

  const checkThreshold = (metric: keyof typeof thresholds, value: number): void => {
    const threshold = thresholds[metric];
    if (!threshold) return;

    // Handle different threshold structures
    if ('needs_improvement' in threshold) {
      // Standard metrics (fcp, lcp, fid, cls)
      if (value > threshold.needs_improvement) {
        addInsight({
          type: 'error',
          title: `Poor ${metric.toUpperCase()}`,
          description: `${metric.toUpperCase()} is ${value}ms (threshold: ${threshold.needs_improvement}ms)`,
          impact: 'high',
          recommendation: `Optimize ${metric.toUpperCase()} for better user experience`,
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.needs_improvement
        });
      } else if (value > threshold.good) {
        addInsight({
          type: 'warning',
          title: `${metric.toUpperCase()} Needs Improvement`,
          description: `${metric.toUpperCase()} is ${value}ms (target: <${threshold.good}ms)`,
          impact: 'medium',
          recommendation: `Consider optimizing ${metric.toUpperCase()}`,
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.good
        });
      } else {
        addInsight({
          type: 'success',
          title: `Good ${metric.toUpperCase()}`,
          description: `${metric.toUpperCase()} is ${value}ms (excellent!)`,
          impact: 'low',
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.good
        });
      }
    } else if ('critical' in threshold) {
      // Memory metric
      if (value > threshold.critical) {
        addInsight({
          type: 'error',
          title: `Critical Memory Usage`,
          description: `Memory usage is ${(value * 100).toFixed(1)}% (critical: >${(threshold.critical * 100).toFixed(1)}%)`,
          impact: 'high',
          recommendation: `Reduce memory usage immediately`,
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.critical
        });
      } else if (value > threshold.warning) {
        addInsight({
          type: 'warning',
          title: `High Memory Usage`,
          description: `Memory usage is ${(value * 100).toFixed(1)}% (warning: >${(threshold.warning * 100).toFixed(1)}%)`,
          impact: 'medium',
          recommendation: `Monitor memory usage closely`,
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.warning
        });
      } else {
        addInsight({
          type: 'success',
          title: `Good Memory Usage`,
          description: `Memory usage is ${(value * 100).toFixed(1)}% (healthy!)`,
          impact: 'low',
          metric: metric as keyof PerformanceMetrics,
          value,
          threshold: threshold.warning
        });
      }
    }
  };

  // Performance scoring
  const calculatePerformanceScore = computed((): number => {
    let score = 100;
    let checks = 0;

    // Core Web Vitals scoring
    if (metrics.fcp) {
      checks++;
      if (metrics.fcp > thresholds.fcp.needs_improvement) score -= 25;
      else if (metrics.fcp > thresholds.fcp.good) score -= 10;
    }

    if (metrics.lcp) {
      checks++;
      if (metrics.lcp > thresholds.lcp.needs_improvement) score -= 25;
      else if (metrics.lcp > thresholds.lcp.good) score -= 10;
    }

    if (metrics.fid) {
      checks++;
      if (metrics.fid > thresholds.fid.needs_improvement) score -= 25;
      else if (metrics.fid > thresholds.fid.good) score -= 10;
    }

    if (metrics.cls) {
      checks++;
      if (metrics.cls > thresholds.cls.needs_improvement) score -= 25;
      else if (metrics.cls > thresholds.cls.good) score -= 10;
    }

    // Memory usage scoring
    if (metrics.usedJSHeapSize && metrics.jsHeapSizeLimit) {
      checks++;
      const memoryRatio = metrics.usedJSHeapSize / metrics.jsHeapSizeLimit;
      if (memoryRatio > thresholds.memory.critical) score -= 30;
      else if (memoryRatio > thresholds.memory.warning) score -= 15;
    }

    return Math.max(0, checks > 0 ? score : 0);
  });

  // Performance grade
  const performanceGrade = computed((): string => {
    const score = calculatePerformanceScore.value;
    if (score >= 90) return 'A';
    if (score >= 80) return 'B';
    if (score >= 70) return 'C';
    if (score >= 60) return 'D';
    return 'F';
  });

  // Optimization recommendations
  const getOptimizationRecommendations = (): PerformanceInsight[] => {
    const recommendations: PerformanceInsight[] = [];

    // Check if service worker should be enabled
    if (!optimizations.serviceWorker && isMounted.value && windowRef.value && 'serviceWorker' in windowRef.value.navigator) {
      recommendations.push({
        id: 'service-worker',
        type: 'suggestion',
        title: 'Enable Service Worker',
        description: 'Service worker can improve loading performance and enable offline functionality',
        impact: 'medium',
        recommendation: 'Enable service worker for better caching and offline support',
        timestamp: new Date()
      });
    }

    // Check for large bundle sizes
    if (metrics.loadComplete && metrics.loadComplete > 3000) {
      recommendations.push({
        id: 'code-splitting',
        type: 'suggestion',
        title: 'Consider Code Splitting',
        description: 'Large initial bundle may be slowing down load time',
        impact: 'high',
        recommendation: 'Implement code splitting to reduce initial bundle size',
        timestamp: new Date()
      });
    }

    // Check for reduced motion preference
    if (prefersReducedMotion.value && !optimizations.reducedMotion) {
      recommendations.push({
        id: 'reduced-motion',
        type: 'suggestion',
        title: 'Enable Reduced Motion',
        description: 'User prefers reduced motion animations',
        impact: 'low',
        recommendation: 'Respect user motion preferences for better accessibility',
        timestamp: new Date()
      });
    }

    return recommendations;
  };

  // Performance API
  const startMonitoring = (): void => {
    if (isMonitoring.value) return;

    isMonitoring.value = true;

    measureCoreWebVitals();
    monitorResources();
    monitorBattery();
    monitorNetwork();

    // Start memory monitoring
    measureMemoryUsage();
    memoryMonitorTimer = setInterval(measureMemoryUsage, 10000); // Every 10 seconds

    console.log('🔍 Performance monitoring started');
  };

  const stopMonitoring = (): void => {
    isMonitoring.value = false;

    if (performanceObserver) {
      performanceObserver.disconnect();
      performanceObserver = null;
    }

    if (memoryMonitorTimer) {
      clearInterval(memoryMonitorTimer);
      memoryMonitorTimer = null;
    }

    if (batteryUpdateTimer) {
      clearInterval(batteryUpdateTimer);
      batteryUpdateTimer = null;
    }

    console.log('🔍 Performance monitoring stopped');
  };

  const measureOperation = (name: keyof PerformanceMetrics) => {
    if (!isMounted.value || !windowRef.value) {
      return { end: () => {} };
    }

    const startTime = windowRef.value.performance.now();

    return {
      end: () => measureCustomMetric(name, startTime)
    };
  };

  const clearInsights = (): void => {
    insights.value = [];
  };

  const exportMetrics = () => ({
    metrics: { ...metrics },
    score: calculatePerformanceScore.value,
    grade: performanceGrade.value,
    insights: insights.value,
    battery: batteryInfo.level.value,
    network: networkInfo.effectiveType.value,
    optimizations: { ...optimizations },
    timestamp: new Date().toISOString()
  });

  // Lifecycle
  onMounted(() => {
    // Auto-start monitoring
    nextTick(() => {
      startMonitoring();
    });

    // Listen for visibility changes to pause monitoring when hidden
    const handleVisibilityChange = () => {
      if (!windowRef.value) return;

      if (windowRef.value.document.hidden && isMonitoring.value) {
        stopMonitoring();
      } else if (!windowRef.value.document.hidden && !isMonitoring.value && optimizations.memoryManagement) {
        startMonitoring();
      }
    };

    if (windowRef.value) {
      windowRef.value.document.addEventListener('visibilitychange', handleVisibilityChange);
    }

    onUnmounted(() => {
      stopMonitoring();
      if (windowRef.value) {
        windowRef.value.document.removeEventListener('visibilitychange', handleVisibilityChange);
      }
    });
  });

  return {
    // State
    metrics: readonly(metrics),
    insights: readonly(insights),
    batteryInfo: readonly(batteryInfo),
    networkInfo: readonly(networkInfo),
    optimizations: readonly(optimizations),
    isMonitoring: readonly(isMonitoring),
    devicePixelRatio,
    isMounted,
    windowRef,
    prefersReducedMotion,

    // Computed
    performanceScore: calculatePerformanceScore,
    performanceGrade,

    // Methods
    startMonitoring,
    stopMonitoring,
    measureOperation,
    clearInsights,
    getOptimizationRecommendations,
    exportMetrics,

    // Thresholds
    thresholds: readonly(thresholds)
  };
};

export default usePerformance;

import { computed, unref } from 'vue'
import { useAnimate, useMounted, type MaybeComputedElementRef } from '@vueuse/core'
import type { UseAnimateKeyframes, UseAnimateOptions } from '@vueuse/core'
import { ANIMATION_DURATION, EASING, FILL_MODE } from '@/constants/animations'

export interface AnimationPreset {
  keyframes: UseAnimateKeyframes
  options: number | UseAnimateOptions
}

export interface UseAnimateElementOptions {
  preset?: keyof typeof animationPresets
  duration?: number
  easing?: string
  immediate?: boolean
  stagger?: number
  onComplete?: () => void
}

// Animation presets using Web Animations API keyframes
export const animationPresets = {
  slideUp: {
    keyframes: [
      { opacity: 0, transform: 'translateY(20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideDown: {
    keyframes: [
      { opacity: 0, transform: 'translateY(-20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  fadeIn: {
    keyframes: [
      { opacity: 0 },
      { opacity: 1 }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  scaleUp: {
    keyframes: [
      { opacity: 0, transform: 'scale(0.8)' },
      { opacity: 1, transform: 'scale(1.05)' },
      { opacity: 1, transform: 'scale(1)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  scaleDown: {
    keyframes: [
      { opacity: 1, transform: 'scale(1)' },
      { opacity: 0, transform: 'scale(0.8)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.EASE_OUT,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  staggerFadeIn: {
    keyframes: [
      { opacity: 0, transform: 'translateY(10px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  popIn: {
    keyframes: [
      { opacity: 0, transform: 'scale(0.5) rotate(-10deg)' },
      { opacity: 1, transform: 'scale(1.05) rotate(2deg)' },
      { opacity: 1, transform: 'scale(1) rotate(0deg)' }
    ],
    options: {
      duration: ANIMATION_DURATION.SLOW,
      easing: EASING.ELASTIC,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  rotate45: {
    keyframes: [
      { transform: 'rotate(0deg)' },
      { transform: 'rotate(45deg)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  rotateBack: {
    keyframes: [
      { transform: 'rotate(45deg)' },
      { transform: 'rotate(0deg)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideInRight: {
    keyframes: [
      { opacity: 0, transform: 'translateX(100%)' },
      { opacity: 1, transform: 'translateX(0%)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.SHARP,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideOutRight: {
    keyframes: [
      { opacity: 1, transform: 'translateX(0%)' },
      { opacity: 0, transform: 'translateX(100%)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SHARP,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideInLeft: {
    keyframes: [
      { opacity: 0, transform: 'translateX(-100%)' },
      { opacity: 1, transform: 'translateX(0%)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.SHARP,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideOutLeft: {
    keyframes: [
      { opacity: 1, transform: 'translateX(0%)' },
      { opacity: 0, transform: 'translateX(-100%)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SHARP,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,
} as const

export function useAnimateElement(
  target: MaybeComputedElementRef,
  options: UseAnimateElementOptions = {}
) {
  const isMounted = useMounted()
  const {
    preset = 'fadeIn',
    duration,
    easing,
    immediate = false,
    stagger = 0,
    onComplete
  } = options

  const presetConfig = animationPresets[preset]
  const keyframes = presetConfig.keyframes

  const baseOptions = typeof presetConfig.options === 'object' ? presetConfig.options : { duration: presetConfig.options }
  const animationOptions = {
    ...baseOptions,
    ...(duration && { duration }),
    ...(easing && { easing }),
    // Avoid SSR window issues by not forcing a custom window here
    immediate,
    onReady: onComplete
  } as UseAnimateOptions

  const {
    isSupported,
    animate,
    play,
    pause,
    reverse,
    finish,
    cancel,
    pending,
    playState,
    currentTime,
    playbackRate
  } = useAnimate(target, keyframes, animationOptions)

  const bounds = computed(() => {
    const element = unref(target) as HTMLElement | null
    return element?.getBoundingClientRect() || null
  })

  const animateIn = async (customKeyframes?: UseAnimateKeyframes, customOptions?: UseAnimateOptions) => {
    if (!isMounted.value || !isSupported.value) return
    if (customKeyframes && animate.value) {
      cancel()
      const element = unref(target) as HTMLElement | null
      if (element) {
        const newAnimation = element.animate(
          customKeyframes as Keyframe[],
          { ...animationOptions, ...customOptions }
        )
        if (onComplete) newAnimation.addEventListener('finish', onComplete)
        return newAnimation
      }
    } else {
      play()
    }
  }

  const animateOut = async (outKeyframes?: UseAnimateKeyframes, outOptions?: UseAnimateOptions) => {
    if (!isMounted.value || !isSupported.value) return
    const defaultOutKeyframes = [
      { opacity: 1, transform: 'scale(1)' },
      { opacity: 0, transform: 'scale(0.8)' }
    ]
    const defaultOutOptions = {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.EASE_OUT,
      fill: FILL_MODE.FORWARDS as FillMode
    }
    const element = unref(target) as HTMLElement | null
    if (element) {
      const animation = element.animate(
        (outKeyframes || defaultOutKeyframes) as Keyframe[],
        { ...defaultOutOptions, ...outOptions }
      )
      if (onComplete) animation.addEventListener('finish', onComplete)
      return animation
    }
  }

  const isAnimating = computed(() => pending.value || playState.value === 'running')

  const animateStaggered = async (
    elements: (HTMLElement | null)[],
    staggerDelay: number = stagger || 50,
    animationPreset: keyof typeof animationPresets = preset
  ) => {
    if (!isMounted.value) return []
    const presetConfig = animationPresets[animationPreset]
    const animations: Animation[] = []
    elements.forEach((element, index) => {
      if (!element) return
      const baseOptions = typeof presetConfig.options === 'object' ? presetConfig.options : { duration: presetConfig.options }
      const delayedOptions = { ...baseOptions, delay: index * staggerDelay }
      const animation = element.animate(
        presetConfig.keyframes as Keyframe[],
        delayedOptions
      )
      animations.push(animation)
    })
    return animations
  }

  return {
    animateIn,
    animateOut,
    animateStaggered,
    isSupported,
    animate,
    play,
    pause,
    reverse,
    finish,
    cancel,
    pending,
    playState,
    isAnimating,
    currentTime,
    playbackRate,
    bounds,
    presets: animationPresets
  }
}

export function createAnimation(
  target: MaybeComputedElementRef,
  preset: keyof typeof animationPresets,
  options?: Partial<UseAnimateElementOptions>
) {
  return useAnimateElement(target, { preset, ...options })
}

export function useStaggeredAnimation(
  targets: MaybeComputedElementRef[],
  preset: keyof typeof animationPresets = 'staggerFadeIn',
  staggerDelay: number = 50
) {
  const animations = targets.map(target =>
    useAnimateElement(target, { preset, immediate: false })
  )
  const playStaggered = async () => {
    for (let i = 0; i < animations.length; i++) {
      setTimeout(() => animations[i].play(), i * staggerDelay)
    }
  }
  const cancelAll = () => animations.forEach(anim => anim.cancel())
  return { animations, playStaggered, cancelAll }
}

/**
 * Shared animation constants for Tailwind CSS and VueUse composables
 *
 * These values ensure consistency between CSS-based animations (Tailwind)
 * and JavaScript-based animations (useAnimateElement composable).
 *
 * Research-based timing from UI_ENHANCEMENTS.md:
 * - INSTANT: 100ms - Quick feedback (hover acknowledgment)
 * - FAST: 200ms - Button hovers, simple selections
 * - NORMAL: 300ms - Standard transitions, most animations
 * - SLOW: 500ms - Page transitions (max recommended)
 * - CELEBRATION: 2000ms - Success celebrations, confetti
 */

export const ANIMATION_DURATION = {
  INSTANT: 100,
  FAST: 200,
  NORMAL: 300,
  SLOW: 500,
  CELEBRATION: 2000
} as const

/**
 * Easing functions for smooth, natural motion
 *
 * - EASE_OUT: Most common (decelerating, snappy end)
 * - EASE_IN_OUT: Smooth start and end (transformations)
 * - BOUNCE: Playful overshoot (success states, selections)
 */
export const EASING = {
  EASE_OUT: 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',
  EASE_IN_OUT: 'cubic-bezier(0.42, 0, 0.58, 1)',
  BOUNCE: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
  SMOOTH: 'cubic-bezier(0.16, 1, 0.3, 1)',
  SHARP: 'cubic-bezier(0.4, 0, 0.2, 1)',
  ELASTIC: 'cubic-bezier(0.68, -0.55, 0.265, 1.55)'
} as const

/**
 * Animation fill modes for Web Animations API
 */
export const FILL_MODE = {
  FORWARDS: 'forwards',
  BACKWARDS: 'backwards',
  BOTH: 'both',
  NONE: 'none'
} as const

/**
 * Timing presets for common animation patterns
 */
export const TIMING_PRESETS = {
  // Quick interactions
  HOVER: {
    duration: ANIMATION_DURATION.FAST,
    easing: EASING.EASE_OUT
  },
  // Standard transitions
  TRANSITION: {
    duration: ANIMATION_DURATION.NORMAL,
    easing: EASING.EASE_OUT
  },
  // Bouncy selections
  SELECTION: {
    duration: ANIMATION_DURATION.NORMAL,
    easing: EASING.BOUNCE
  },
  // Smooth slides
  SLIDE: {
    duration: ANIMATION_DURATION.NORMAL,
    easing: EASING.SHARP
  },
  // Playful pops
  POP: {
    duration: ANIMATION_DURATION.SLOW,
    easing: EASING.ELASTIC
  }
} as const

/**
 * Type exports for TypeScript
 */
export type AnimationDuration = typeof ANIMATION_DURATION[keyof typeof ANIMATION_DURATION]
export type AnimationEasing = typeof EASING[keyof typeof EASING]
export type AnimationFillMode = typeof FILL_MODE[keyof typeof FILL_MODE]

