// Performance monitoring with all VueUse composables included
const {
  measureOperation,
  optimizations,
  batteryInfo,
  networkInfo,
  windowRef
} = usePerformance();

const typewriterStrings: string[] = ['Post once —', 'Schedule smarter —', 'Grow faster —'];

let typedInstance: any = null;
let cleanupRefs: {
  timeouts: Set<ReturnType<typeof setTimeout>>;
  callbacks: Set<number>;
} = {
  timeouts: new Set(),
  callbacks: new Set()
};

onMounted(() => {
  const initOperation = measureOperation('chatInitTime')

  try {
    // Check performance conditions for adaptive animations
    const prefersReducedMotion = optimizations.reducedMotion

    const lowBattery = optimizations.batteryOptimization

    const slowNetwork = networkInfo.effectiveType &&
      (networkInfo.effectiveType === 'slow-2g' ||
       networkInfo.effectiveType === '2g')

    const shouldOptimizeAnimations = prefersReducedMotion ||
      (optimizations.batteryOptimization && lowBattery) ||
      slowNetwork

    // Adaptive typewriter configuration
    const typewriterConfig = {
      strings: typewriterStrings,
      smartBackspace: false,
      shuffle: false, // Disabled shuffle to prevent buggy behavior
      typeSpeed: shouldOptimizeAnimations ? 80 : 40, // Smoother typing speed
      backSpeed: shouldOptimizeAnimations ? 50 : 25, // Smoother backspace speed
      backDelay: shouldOptimizeAnimations ? 1500 : 2500, // Longer pause before deleting
      loop: !shouldOptimizeAnimations, // Disable loop for reduced motion
      showCursor: true,
      cursorChar: '|',
    };

    // Add progressive enhancement classes
    if (windowRef.value) {
      windowRef.value.document.documentElement.classList.add('js-enabled')
      if (shouldOptimizeAnimations) {
        windowRef.value.document.documentElement.classList.add('reduced-motion')
      }
    }

    // Initialize typewriter with performance considerations
    if (!shouldOptimizeAnimations) {
      typedInstance = new Typed(".typewriter", typewriterConfig);
    } else {
      // For reduced performance scenarios, show static text
      if (windowRef.value) {
        const typewriterElement = windowRef.value.document.querySelector('.typewriter');
        if (typewriterElement) {
          typewriterElement.textContent = typewriterStrings[0].replace(' —', '');
        }
      }
    }

    // Use requestIdleCallback for non-critical operations
    nextTick(() => {
      if (windowRef.value && 'requestIdleCallback' in windowRef.value && !shouldOptimizeAnimations) {
        const callbackId = (windowRef.value as any).requestIdleCallback(() => {
          cleanupRefs.callbacks.delete(callbackId)
          // Non-critical initialization code
        });
        cleanupRefs.callbacks.add(callbackId)
      } else {
        // Fallback for browsers without requestIdleCallback or when optimizing
        const timeoutId = setTimeout(() => {
          cleanupRefs.timeouts.delete(timeoutId)
          // Simplified initialization
        }, shouldOptimizeAnimations ? 0 : 100);
        cleanupRefs.timeouts.add(timeoutId)
      }
    });

    initOperation.end()
  } catch (error) {
    console.error('HeroSection: Error during typewriter initialization:', error)
    initOperation.end()
  }
});

onUnmounted(() => {
  // Clean up typewriter instance
  if (typedInstance) {
    typedInstance.destroy();
    typedInstance = null;
  }

  // Clear all timeouts
  cleanupRefs.timeouts.forEach(timeout => {
    clearTimeout(timeout);
  });
  cleanupRefs.timeouts.clear();

  // Cancel idle callbacks
  cleanupRefs.callbacks.forEach(callbackId => {
    if (windowRef.value && 'cancelIdleCallback' in windowRef.value) {
      (windowRef.value as any).cancelIdleCallback(callbackId);
    }
  });
  cleanupRefs.callbacks.clear();
});

