import { atom } from "nanostores";
import type { Models } from "appwrite";
import type { PostContainer } from "@appwrite/schemas/postContainer";
import { setPostModalOpen } from "./navigationStore";

export interface CreatePostModalState {
  isOpen: boolean;
  mode: "create" | "repost";
  repostData?: PostContainer;
  initialPostType?: string;
  initialAccounts?: string[];
  initialMediaFiles?: Models.File[] | File[];
  prefilledContent?: string;
  prefilledHashtags?: string[];
  scheduleType?: "now" | "later";
  prefilledScheduleDateTime?: string;
}

export const createPostModalState = atom<CreatePostModalState>({
  isOpen: false,
  mode: "create",
});

// Actions
export const openCreatePostModal = (options?: {
  postType?: string;
  accounts?: string[];
  mediaFiles?: Models.File[] | File[];
  content?: string;
  hashtags?: string[];
  scheduleDateTime?: string;
}) => {
  let definedScheduleType = "now";
  if (options?.scheduleDateTime) {
    const today = new Date().toISOString().slice(0,16);
    if (options?.scheduleDateTime <= today) {
      definedScheduleType = "later";
    }
  }
  createPostModalState.set({
    isOpen: true,
    mode: "create",
    initialPostType: options?.postType,
    initialAccounts: options?.accounts,
    initialMediaFiles: options?.mediaFiles,
    prefilledContent: options?.content,
    prefilledHashtags: options?.hashtags,
    scheduleType: definedScheduleType as "now" | "later",
    prefilledScheduleDateTime: options?.scheduleDateTime,
  });
  setPostModalOpen(true);
};

export const openRepostModal = (post: PostContainer, options?: {
  accounts?: string[];
  additionalContent?: string;
}) => {
  createPostModalState.set({
    isOpen: true,
    mode: "repost",
    repostData: post,
    initialAccounts: options?.accounts,
    prefilledContent: options?.additionalContent,
  });
  setPostModalOpen(true);
};

export const closeCreatePostModal = () => {
  createPostModalState.set({
    isOpen: false,
    mode: "create",
    repostData: undefined,
    initialPostType: undefined,
    initialAccounts: undefined,
    initialMediaFiles: undefined,
    prefilledContent: undefined,
    prefilledHashtags: undefined,
    prefilledScheduleDateTime: undefined,
  });
  setPostModalOpen(false);
};

export const toggleCreatePostModal = () => {
  const currentState = createPostModalState.get();
  if (currentState.isOpen) {
    closeCreatePostModal();
  } else {
    openCreatePostModal();
  }
};

