```typescript
import fs from 'fs';
import path from 'path';
import matter from 'gray-matter';
import { marked } from 'marked';

export interface DocMeta {
  title: string;
  description: string;
  category: string;
  order?: number;
  tags?: string[];
  relatedSections?: string[];
}

export interface Doc {
  id: string;
  slug: string;
  meta: DocMeta;
  content: string;
  html: string;
}

export async function loadDocs(): Promise<Doc[]> {
  const docsDir = path.join(process.cwd(), 'src/content/docs');
  const docs: Doc[] = [];

  function readDocsRecursively(dir: string, category: string = '') {
    const files = fs.readdirSync(dir);

    files.forEach(file => {
      const filePath = path.join(dir, file);
      const stat = fs.statSync(filePath);

      if (stat.isDirectory()) {
        readDocsRecursively(filePath, file);
      } else if (file.endsWith('.md')) {
        const fileContent = fs.readFileSync(filePath, 'utf-8');
        const { data, content } = matter(fileContent);

        const slug = path.relative(docsDir, filePath)
          .replace(/\.md$/, '')
          .replace(/\\/g, '/');

        docs.push({
          id: slug,
          slug,
          meta: data as DocMeta,
          content,
          html: marked(content),
        });
      }
    });
  }

  readDocsRecursively(docsDir);
  return docs;
}

export async function loadDoc(slug: string): Promise<Doc | null> {
  const docs = await loadDocs();
  return docs.find(doc => doc.slug === slug) || null;
}
```

