---
import Navbar from "@vue/global/Navbar.vue";
import SideMenu from "@vue/global/SideMenu.vue";
import Footer from "@vue/global/Footer.vue";
import GlobalActionWidget from "@vue/global/GlobalActionWidget.vue";
import GlobalActionWidgetWithOverrides from "@vue/global/GlobalActionWidgetWithOverrides.vue";
import SmartNotifications from "@vue/global/SmartNotifications.vue";
import DataProvider from "@components/providers/DataProvider.vue";
import CreatePostProvider from "@vue/providers/CreatePostProvider.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import { SITE_DATA } from "@/constants/siteData";

const siteData = SITE_DATA;

interface Props {
  title: string;
}

const sessionUser = await Astro.session?.get("authUser");

const { title } = Astro.props;

// Prevent caching of authenticated pages to avoid logout issues
Astro.response.headers.set('Cache-Control', 'private, no-cache, no-store, must-revalidate');
Astro.response.headers.set('Pragma', 'no-cache');
Astro.response.headers.set('Expires', '0');
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />
    <style is:global>
      html {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
        font-family: system-ui, sans-serif;
      }

      html,
      body,
      #body {
        height: 100% !important;
      }

      [data-theme="dark"] {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body
    id="body"
    class="relative min-h-screen flex flex-col overflow-hidden touch-optimized mobile-a11y"
  >
    <Navbar client:load transition:persist user={sessionUser} />
    <div class="flex-1 flex overflow-hidden">
      <SideMenu client:load />
      <div
        class="flex-1 flex flex-col overflow-y-auto bg-gray-100 dark:bg-gray-900 transition-colors duration-300"
      >
        <DataProvider client:load>
          <slot />
        </DataProvider>
        <Footer client:load transition:persist />
      </div>
    </div>

    <CookieGuard client:load transition:persist />
    <CreatePostProvider client:load />
    <SessionInitializer client:load />

    <!-- Teleport Layer: keep this as a single top-level target for modals/popovers -->
    <div id="teleport-layer"></div>

    <!-- Global Action Widget -->
    <GlobalActionWidgetWithOverrides
      client:load
      position="bottom-right"
      glass="subtle"
      show-chat-actions={true}
      transition:persist="global-action-widget"
    />

    <!-- Smart Notifications -->
    <SmartNotifications
      client:load
      position="top-right"
      max-visible={5}
      enable-ai-prioritization={true}
      enable-stacking-prevention={true}
      enable-smart-grouping={true}
      transition:persist
    />

  </body>
</html>

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: ["class", '[data-theme="dark"]'],
