import { computed } from 'vue';
import type { CollectionEntry } from 'astro:content';
import { marked } from 'marked';

const props = defineProps<{
  doc: CollectionEntry<'docs'> | null;
  allDocs: CollectionEntry<'docs'>[];
}>();

const renderedContent = computed(() => {
  if (!props.doc?.body) return '';
  return marked(props.doc.body);
});

const getRelatedTitle = (slug: string) => {
  const relatedDoc = props.allDocs.find(d => d.slug === slug);
  return relatedDoc?.data.title || slug.split('/').pop()?.replace(/-/g, ' ') || '';
};
</script>
