     1→<template>
     2→  <BaseStepContainer
     3→    step="accounts_workflows"
     4→    :title="stepTitle"
     5→    :can-go-back="true"
     6→    :can-go-next="true"
     7→    :can-proceed="canProceed"
     8→    :next-button-text="nextButtonText"
     9→    @next="handleNext"
    10→    @previous="handlePrevious"
    11→  >
    12→    <component
    13→      :is="currentSubstepComponent"
    14→      :platform-trigger="data?.platformTrigger"
    15→      :platform-targets="data?.platformTargets"
    16→      :trigger-connection="data?.triggerConnection"
    17→      :connected-target-accounts="data?.connectedTargetAccounts"
    18→      :current-target-index="currentTargetIndex"
    19→      :total-targets="data?.platformTargets?.length || 0"
    20→      :workflow-created="data?.workflowCreated"
    21→      @update="handleUpdate"
    22→      @next="handleNext"
    23→      @previous="handlePrevious"
    24→    />
    25→  </BaseStepContainer>
    26→</template>
    27→
    28→<script setup lang="ts">
    29→import { computed, defineAsyncComponent } from 'vue';
    30→import { useOnboardingFlow, isDynamicTargetStep, getTargetIndexFromSubstep } from '@composables/useOnboardingFlow';
    31→import { isSubstepComplete } from '@/types/onboarding';
    32→import BaseStepContainer from '../BaseStepContainer.vue';
    33→
    34→const { currentSubstep, data, next, previous, update } = useOnboardingFlow();
    35→
    36→const currentTargetIndex = computed(() => {
    37→  if (isDynamicTargetStep(currentSubstep.value)) {
    38→    return getTargetIndexFromSubstep(currentSubstep.value);
    39→  }
    40→  return data.value?.currentTargetIndex || 0;
    41→});
    42→
    43→const stepTitle = computed(() => {
    44→  const titles: Record<string, string> = {
    45→    accounts_workflow_start: 'Connect & Automate',
    46→    post_from_here_to_here: 'Choose Your Automation Flow',
    47→    connect_trigger_account: 'Connect Source Account',
    48→    connect_target_accounts: 'Connect Target Account',
    49→    generate_workflow: 'Generate Workflow',
    50→    // customize_conditions_options_schedule: 'Customize Your Workflow',
    51→    activate_workflow_and_test: 'Activate & Test'
    52→  };
    53→
    54→  // Handle dynamic target connection steps
    55→  if (isDynamicTargetStep(currentSubstep.value)) {
    56→    const targetIndex = getTargetIndexFromSubstep(currentSubstep.value);
    57→    const totalTargets = data.value?.platformTargets?.length || 0;
    58→    return `Connect Target Account ${targetIndex + 1} of ${totalTargets}`;
    59→  }
    60→
    61→  return titles[currentSubstep.value] || 'Accounts & Workflows';
    62→});
    63→
    64→const canProceed = computed(() => {
    65→  if (currentSubstep.value === 'accounts_workflow_start') return true;
    66→  if (currentSubstep.value === 'customize_conditions_options_schedule') return true; // Optional
    67→  if (currentSubstep.value === 'activate_workflow_and_test') return true; // Optional
    68→
    69→  return isSubstepComplete(currentSubstep.value, data.value);
    70→});
    71→
    72→const nextButtonText = computed(() => {
    73→  if (currentSubstep.value === 'activate_workflow_and_test') return 'Continue to Completion';
    74→  return 'Next';
    75→});
    76→
    77→const currentSubstepComponent = computed(() => {
    78→  const componentMap: Record<string, any> = {
    79→    accounts_workflow_start: defineAsyncComponent(() => import('./AccountsWorkflowStart.vue')),
    80→    post_from_here_to_here: defineAsyncComponent(() => import('./PostFromHereToHere.vue')),
    81→    connect_trigger_account: defineAsyncComponent(() => import('./ConnectTriggerAccount.vue')),
    82→    connect_target_accounts: defineAsyncComponent(() => import('./ConnectTargetAccounts.vue')),
    83→    generate_workflow: defineAsyncComponent(() => import('./GenerateWorkflow.vue')),
    84→    // customize_conditions_options_schedule: defineAsyncComponent(() => import('./CustomizeConditionsOptionsSchedule.vue')),
    85→    activate_workflow_and_test: defineAsyncComponent(() => import('./ActivateWorkflowAndTest.vue'))
    86→  };
    87→
    88→  // Handle dynamic target connection steps
    89→  if (isDynamicTargetStep(currentSubstep.value)) {
    90→    return defineAsyncComponent(() => import('./ConnectTargetAccounts.vue'));
    91→  }
    92→
    93→  return componentMap[currentSubstep.value] || null;
    94→});
    95→
    96→const handleNext = () => next();
    97→const handlePrevious = () => previous();
    98→const handleUpdate = (updates: any) => update(updates);
    99→
   100→defineEmits<{
   101→  next: [];
   102→  previous: [];
   103→  update: [updates: any];
   104→}>();
   105→</script>
