import { z } from "zod";

export const WorkflowNodeSchema = z.object({
  $id: z.string().optional(),
  $createdAt: z.string().optional(),
  $updatedAt: z.string().optional(),
  $permissions: z.array(z.string()).optional(),
  workflowId: z.string().max(36, "Maximum length of 36 characters exceeded"),
  nodeType: z.enum(["ACTION", "SWITCH", "FORK", "JOIN"]),
  action: z.enum(["switch", "fork", "join", "wait", "if_condition", "ai_rewrite", "ai_translate", "ai_summarize", "ai_gen_title", "ai_gen_tags", "ai_gen_content", "ai_add_title", "ai_add_tags", "ai_generate_content", "set_title", "set_content", "set_tags", "schedule_post", "publish_content", "add_image", "fetch_content", "reply_to_comment", "reply_to_message"]).nullish(),
  platform: z.string().max(50, "Maximum length of 50 characters exceeded").nullish(),
  config: z.string().max(17000, "Maximum length of 17000 characters exceeded").nullish(),
  connections: z.string().max(17000, "Maximum length of 17000 characters exceeded"),
});

export type WorkflowNode = z.infer<typeof WorkflowNodeSchema>;

export const POST_TYPES = [
    "text",
    "caption-image",
    "caption-video",
    "carousel",
    "story",
    // "file" TODO: Add this back in
] as const;

