/**
 * Workflow Recommendations Constants
 *
 * Provides AI-powered workflow action recommendations based on trigger and target platform combinations.
 * Each recommendation suggests how to optimize content when cross-posting between different social platforms.
 */

// Available workflow actions from WorkflowNodeSchema
export type WorkflowActionType =
  | "switch"
  | "fork"
  | "join"
  | "wait"
  | "if_condition"
  | "ai_rewrite"
  | "ai_translate"
  | "ai_summarize"
  | "ai_gen_title"
  | "ai_gen_tags"
  | "ai_gen_content"
  | "ai_add_title"
  | "ai_add_tags"
  | "ai_generate_content"
  | "set_title"
  | "set_content"
  | "set_tags"
  | "schedule_post"
  | "publish_content"
  | "add_image"
  | "fetch_content"
  | "reply_to_comment"
  | "reply_to_message";

// Platform types
export type PlatformType =
  | "instagram"
  | "facebook"
  | "tiktok"
  | "linkedIn"
  | "youtube"
  | "pinterest"
  | "threads"
  | "mastodon"
  | "bluesky";

// Single workflow recommendation
export interface WorkflowRecommendation {
  description: string;
  action: WorkflowActionType;
}

// Map of target platforms to their recommendation arrays
export type TargetPlatformRecommendations = {
  [key in PlatformType]?: WorkflowRecommendation[];
};

// Complete recommendations structure: trigger platform → target platforms → recommendations
