  }

  // First substep of next step
  const nextStep = ONBOARDING_STEPS[stepIndex + 1];
  if (nextStep) {
    return { step: nextStep.id as OnboardingStep, substep: nextStep.substeps[0] };
  }

  // No next step
  return null;
};

export const getPreviousSubstep = (currentStep: OnboardingStep, currentSubstep: string) => {
  const stepIndex = ONBOARDING_STEPS.findIndex(s => s.id === currentStep);
  const stepConfig = ONBOARDING_STEPS[stepIndex];
  const substepIndex = (stepConfig.substeps as readonly string[]).indexOf(currentSubstep);

  // Previous substep in current step
  if (substepIndex > 0) {
    return { step: currentStep, substep: stepConfig.substeps[substepIndex - 1] };
  }

  // Last substep of previous step
  const prevStep = ONBOARDING_STEPS[stepIndex - 1];
  if (prevStep) {
    const lastSubstep = prevStep.substeps[prevStep.substeps.length - 1];
    return { step: prevStep.id as OnboardingStep, substep: lastSubstep };
  }

