import json
import os

config_path = os.path.expanduser("~/.claude.json")
with open(config_path, 'r') as f:
    config = json.load(f)

# Find the socialaize project config
for project_path, project_config in config.get("projects", {}).items():
    if "socialaize" in project_path:
        if "mcpServers" not in project_config:
            project_config["mcpServers"] = {}

        project_config["mcpServers"]["appwrite-docs"] = {
            "command": "npx",
            "args": ["-y", "mcp-remote", "https://mcp-for-docs.appwrite.io"]
        }
        break

with open(config_path, 'w') as f:
    json.dump(config, f, indent=2)

print("Successfully added Appwrite docs MCP server")
PYTHON_EOF
