     1â†’<template>
     2â†’  <Transition :name="transitionName">
     3â†’    <div
     4â†’      v-if="isVisible"
     5â†’      ref="bubbleRef"
     6â†’      class="chat-bubble"
     7â†’      :style="bubbleStyles"
     8â†’    >
     9â†’      <div class="bubble-content">
    10â†’        <div class="bubble-text">
    11â†’          {{ currentMessage.conductorCommand }}
    12â†’        </div>
    13â†’        <div class="bubble-tail"></div>
    14â†’      </div>
    15â†’    </div>
    16â†’  </Transition>
    17â†’</template>
    18â†’
    19â†’<script setup lang="ts">
    20â†’import { ref, computed, useTemplateRef, onMounted, onUnmounted, watch } from 'vue'
    21â†’import { useAnimate } from '@vueuse/core'
    22â†’import { createAnimationBus, type ConductorAnimationEvents } from '@/components/composables/useAnimationBus'
    23â†’
    24â†’interface ChatMessage {
    25â†’  conductorCommand: string
    26â†’  providerResponse?: string
    27â†’}
    28â†’
    29â†’interface Props {
    30â†’  messages?: ChatMessage[]
    31â†’  cycleDuration?: number
    32â†’  currentIndex?: number
    33â†’}
    34â†’
    35â†’const props = withDefaults(defineProps<Props>(), {
    36â†’  messages: () => [
    37â†’    {
    38â†’      conductorCommand: "When I post to my Instagram account, post to all my other accounts",
    39â†’      providerResponse: "Posted!"
    40â†’    },
    41â†’    {
    42â†’      conductorCommand: "When I post to my TikTok, schedule a post to my other accounts",
    43â†’      providerResponse: "Scheduled!"
    44â†’    },
    45â†’    {
    46â†’      conductorCommand: "If my Facebook post has these keywords or hashtags, post to my other accounts",
    47â†’      providerResponse: "Checked and Posted!"
    48â†’    },
    49â†’    {
    50â†’      conductorCommand: "When I post to Threads, rewrite the title using AI to appeal to a more professional audience",
    51â†’      providerResponse: "Rewritten and Posted!"
    52â†’    },
    53â†’    {
    54â†’      conductorCommand: "Add the most trending hashtags to my post",
    55â†’      providerResponse: "Added!"
    56â†’    },
    57â†’    {
    58â†’      conductorCommand: "Post my content at the most optimal time for each platform",
    59â†’      providerResponse: "Scheduled!"
    60â†’    },
    61â†’    {
    62â†’      conductorCommand: "Based on my highest performing content, what should I post next?",
    63â†’      providerResponse: "Optimal Content Suggested!"
    64â†’    }
    65â†’  ],
    66â†’  cycleDuration: 5,
    67â†’  currentIndex: 0
    68â†’})
    69â†’
    70â†’const bubbleRef = useTemplateRef('bubbleRef')
    71â†’const bus = createAnimationBus<ConductorAnimationEvents>('conductor')
    72â†’
    73â†’// State management
    74â†’const isVisible = ref(false)
    75â†’const messageIndex = ref(props.currentIndex)
    76â†’const animationPhase = ref<'hidden' | 'entering' | 'visible' | 'exiting'>('hidden')
    77â†’const isCycling = ref(false)
    78â†’
    79â†’// Computed properties
    80â†’const currentMessage = computed(() =>
    81â†’  props.messages[messageIndex.value % props.messages.length]
    82â†’)
    83â†’
    84â†’const transitionName = computed(() => {
    85â†’  if (animationPhase.value === 'entering') return 'slide-up'
    86â†’  if (animationPhase.value === 'exiting') return 'slide-down'
    87â†’  return 'fade'
    88â†’})
    89â†’
    90â†’const bubbleStyles = computed(() => ({
    91â†’  '--bubble-opacity': animationPhase.value === 'visible' ? '1' : '0',
    92â†’  opacity: 'var(--bubble-opacity)'
    93â†’}))
    94â†’
    95â†’// Float animation for visible state with preserved centering
    96â†’const floatKeyframes = [
    97â†’  { transform: 'translateX(-50%) translateY(0)' },
    98â†’  { transform: 'translateX(-50%) translateY(-2px)' },
    99â†’  { transform: 'translateX(-50%) translateY(0)' }
   100â†’]
   101â†’
   102â†’let floatAnimation: ReturnType<typeof useAnimate> | null = null
   103â†’
   104â†’const playFloat = () => {
   105â†’  if (floatAnimation) {
   106â†’    floatAnimation.cancel()
   107â†’  }
   108â†’
   109â†’  floatAnimation = useAnimate(
   110â†’    bubbleRef,
   111â†’    floatKeyframes,
   112â†’    {
   113â†’      duration: 4000,
   114â†’      iterations: Infinity,
   115â†’      easing: 'cubic-bezier(0.25, 0.46, 0.45, 0.94)'
   116â†’    }
   117â†’  )
   118â†’
   119â†’  floatAnimation.play()
   120â†’}
   121â†’
   122â†’const cancelFloat = () => {
   123â†’  if (floatAnimation) {
   124â†’    floatAnimation.cancel()
   125â†’    floatAnimation = null
   126â†’  }
   127â†’}
   128â†’
   129â†’// Animation management
   130â†’let phaseTimeouts: ReturnType<typeof setTimeout>[] = []
   131â†’
   132â†’const clearTimeouts = () => {
   133â†’  phaseTimeouts.forEach(timeout => clearTimeout(timeout))
   134â†’  phaseTimeouts = []
   135â†’}
   136â†’
   137â†’const runBubbleSequence = () => {
   138â†’  const cycleDurationMs = props.cycleDuration * 1000
   139â†’
   140â†’  isCycling.value = true
   141â†’
   142â†’  animationPhase.value = 'entering'
   143â†’  isVisible.value = true
   144â†’
   145â†’  phaseTimeouts.push(
   146â†’    setTimeout(() => {
   147â†’      animationPhase.value = 'visible'
   148â†’      playFloat()
   149â†’    }, cycleDurationMs * 0.1)
   150â†’  )
   151â†’
   152â†’  phaseTimeouts.push(
   153â†’    setTimeout(() => {
   154â†’      animationPhase.value = 'exiting'
   155â†’      cancelFloat()
   156â†’    }, cycleDurationMs * 0.85)
   157â†’  )
   158â†’
   159â†’  phaseTimeouts.push(
   160â†’    setTimeout(() => {
   161â†’      isVisible.value = false
   162â†’      animationPhase.value = 'hidden'
   163â†’      messageIndex.value = (messageIndex.value + 1) % props.messages.length
   164â†’      isCycling.value = false
   165â†’    }, cycleDurationMs)
   166â†’  )
   167â†’}
   168â†’
   169â†’const startCycle = () => {
   170â†’  stopCycle()
   171â†’  runBubbleSequence()
   172â†’}
   173â†’
   174â†’const stopCycle = () => {
   175â†’  clearTimeouts()
   176â†’  cancelFloat()
   177â†’  isVisible.value = false
   178â†’  animationPhase.value = 'hidden'
   179â†’  isCycling.value = false
   180â†’}
   181â†’
   182â†’// Event bus listeners
   183â†’const unsubscribe = bus.on('bubble-cycle-start', () => {
   184â†’  if (isCycling.value) {
   185â†’    return
   186â†’  }
   187â†’  startCycle()
   188â†’})
   189â†’
   190â†’const unsubscribeStop = bus.on('stop-all', () => {
   191â†’  stopCycle()
   192â†’})
   193â†’
   194â†’watch(() => props.currentIndex, (newIndex) => {
   195â†’  if (newIndex !== undefined && newIndex !== messageIndex.value) {
   196â†’    messageIndex.value = newIndex
   197â†’  }
   198â†’}, { immediate: true })
   199â†’
   200â†’onUnmounted(() => {
   201â†’  stopCycle()
   202â†’  unsubscribe()
   203â†’  unsubscribeStop()
   204â†’})
   205â†’
   206â†’defineExpose({
   207â†’  startCycle,
   208â†’  stopCycle,
   209â†’  currentMessage,
   210â†’  isVisible
   211â†’})
   212â†’</script>
   213â†’
   214â†’<style scoped>
   215â†’.chat-bubble {
   216â†’  position: absolute;
   217â†’  top: 60%;
   218â†’  left: 50%;
   219â†’  transform: translateX(-50%);
   220â†’  z-index: 20;
   221â†’  transition: all 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
   222â†’  pointer-events: none;
   223â†’  will-change: transform, opacity;
   224â†’}
   225â†’
   226â†’.bubble-content {
   227â†’  background: linear-gradient(135deg,
   228â†’    rgba(102, 126, 234, 0.8) 0%,
   229â†’    rgba(118, 75, 162, 0.8) 100%);
   230â†’  border: 1px solid rgba(102, 126, 234, 0.4);
   231â†’  border-radius: 20px;
   232â†’  padding: 12px 20px;
   233â†’  backdrop-filter: blur(10px);
   234â†’  box-shadow:
   235â†’    0 8px 32px rgba(102, 126, 234, 0.25),
   236â†’    0 0 20px rgba(118, 75, 162, 0.2),
   237â†’    inset 0 1px 0 rgba(255, 255, 255, 0.15);
   238â†’  max-width: 300px;
   239â†’  position: relative;
   240â†’  transform: translate3d(0, 0, 0);
   241â†’}
   242â†’
   243â†’.bubble-text {
   244â†’  color: white;
   245â†’  font-size: 14px;
   246â†’  font-weight: 500;
   247â†’  text-align: center;
   248â†’  line-height: 1.4;
   249â†’  margin: 0;
   250â†’}
   251â†’
   252â†’
   253â†’.bubble-tail {
   254â†’  position: absolute;
   255â†’  top: -8px;
   256â†’  left: 50%;
   257â†’  transform: translateX(-50%) rotate(45deg);
   258â†’  width: 16px;
   259â†’  height: 16px;
   260â†’  background: linear-gradient(135deg,
   261â†’    rgba(102, 126, 234, 0.8) 0%,
   262â†’    rgba(118, 75, 162, 0.8) 100%);
   263â†’  border-top: 1px solid rgba(102, 126, 234, 0.4);
   264â†’  border-left: 1px solid rgba(102, 126, 234, 0.4);
   265â†’}
   266â†’
   267â†’/* Enhanced glow effect */
   268â†’.bubble-content::before {
   269â†’  content: '';
   270â†’  position: absolute;
   271â†’  inset: -2px;
   272â†’  border-radius: 22px;
   273â†’  padding: 2px;
   274â†’  background: linear-gradient(135deg, #667eea, #764ba2);
   275â†’  mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
   276â†’  mask-composite: exclude;
   277â†’  -webkit-mask-composite: xor;
   278â†’  opacity: 0.6;
   279â†’  animation: glow-pulse 2s ease-in-out infinite alternate;
   280â†’}
   281â†’
   282â†’@keyframes glow-pulse {
   283â†’  0% { opacity: 0.6; }
   284â†’  100% { opacity: 1; }
   285â†’}
   286â†’
   287â†’/* Particle effect */
   288â†’.bubble-content::after {
   289â†’  content: '';
   290â†’  position: absolute;
   291â†’  inset: -10px;
   292â†’  border-radius: 30px;
   293â†’  background: radial-gradient(circle at center,
   294â†’    rgba(102, 126, 234, 0.1) 0%,
   295â†’    transparent 70%);
   296â†’  animation: particle-glow 3s ease-in-out infinite;
   297â†’  pointer-events: none;
   298â†’}
   299â†’
   300â†’@keyframes particle-glow {
   301â†’  0%, 100% { opacity: 0; transform: scale(0.8); }
   302â†’  50% { opacity: 1; transform: scale(1.1); }
   303â†’}
   304â†’
   305â†’/* Vue transitions */
   306â†’.slide-up-enter-active,
   307â†’.slide-up-leave-active,
   308â†’.slide-down-enter-active,
   309â†’.slide-down-leave-active,
   310â†’.fade-enter-active,
   311â†’.fade-leave-active {
   312â†’  transition: all 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
   313â†’}
   314â†’
   315â†’.slide-up-enter-from {
   316â†’  opacity: 0;
   317â†’  transform: translateX(-50%) translateY(20px);
   318â†’}
   319â†’
   320â†’.slide-down-leave-to {
   321â†’  opacity: 0;
   322â†’  transform: translateX(-50%) translateY(-20px);
   323â†’}
   324â†’
   325â†’.fade-enter-from,
   326â†’.fade-leave-to {
   327â†’  opacity: 0;
   328â†’}
   329â†’
   330â†’/* Responsive adjustments */
   331â†’@media (max-width: 640px) {
   332â†’  .chat-bubble {
   333â†’    top: 65%;
   334â†’  }
   335â†’
   336â†’  .bubble-content {
   337â†’    max-width: 250px;
   338â†’    padding: 10px 16px;
   339â†’  }
   340â†’
   341â†’  .bubble-text {
   342â†’    font-size: 13px;
   343â†’  }
   344â†’}
   345â†’
   346â†’@media (max-width: 480px) {
   347â†’  .bubble-content {
   348â†’    max-width: 200px;
   349â†’    padding: 8px 14px;
   350â†’  }
   351â†’
   352â†’  .bubble-text {
   353â†’    font-size: 12px;
   354â†’  }
   355â†’}
   356â†’
   357â†’/* Reduced motion support */
   358â†’@media (prefers-reduced-motion: reduce) {
   359â†’  .chat-bubble,
   360â†’  .bubble-content::before,
   361â†’  .bubble-content::after {
   362â†’    animation: none !important;
   363â†’  }
   364â†’
   365â†’  .slide-up-enter-active,
   366â†’  .slide-up-leave-active,
   367â†’  .slide-down-enter-active,
   368â†’  .slide-down-leave-active {
   369â†’    transition: opacity 0.2s ease;
   370â†’  }
   371â†’}
   372â†’</style>

