    48→  if (currentSubstep.value === 'platform_use') return 'Continue to Account Setup';
    49→  return 'Next';
    50→});
    51→
    52→const currentSubstepComponent = computed(() => {
    53→  const componentMap: Record<string, any> = {
    54→    //personalization_start: defineAsyncComponent(() => import('./PersonalizationStart.vue')),
    55→    describes_you: defineAsyncComponent(() => import('./DescribesYou.vue')),
