    34→const { currentSubstep, data, next, previous, update } = useOnboardingFlow();
    35→
    36→const currentTargetIndex = computed(() => {
    37→  if (isDynamicTargetStep(currentSubstep.value)) {
    38→    return getTargetIndexFromSubstep(currentSubstep.value);
    39→  }
    40→  return data.value?.currentTargetIndex || 0;
    41→});
    42→
    43→const stepTitle = computed(() => {
    44→  const titles: Record<string, string> = {
    45→    accounts_workflow_start: 'Connect & Automate',
    46→    post_from_here_to_here: 'Choose Your Automation Flow',
    47→    connect_trigger_account: 'Connect Source Account',
    48→    connect_target_accounts: 'Connect Target Account',
    49→    generate_workflow: 'Generate Workflow',
    50→    ai_workflow_enhancements: 'AI Workflow Enhancements',
    51→    platform_customizations: 'Platform Customizations',
    52→    // customize_conditions_options_schedule: 'Customize Your Workflow',
    53→    activate_workflow_and_test: 'Activate & Test'
    54→  };
    55→
    56→  // Handle dynamic target connection steps
    57→  if (isDynamicTargetStep(currentSubstep.value)) {
    58→    const targetIndex = getTargetIndexFromSubstep(currentSubstep.value);
    59→    const totalTargets = data.value?.platformTargets?.length || 0;
    60→    return `Connect Target Account ${targetIndex + 1} of ${totalTargets}`;
    61→  }
    62→
    63→  return titles[currentSubstep.value] || 'Accounts & Workflows';
    64→});
