    70→  if (currentSubstep.value === 'platform_customizations') return true; // Optional
    71→  if (currentSubstep.value === 'activate_workflow_and_test') return true; // Optional
    72→
    73→  return isSubstepComplete(currentSubstep.value, data.value);
    74→});
    75→
    76→const nextButtonText = computed(() => {
    77→  if (currentSubstep.value === 'activate_workflow_and_test') return 'Continue to Completion';
    78→  return 'Next';
    79→});
    80→
