  const staggerContainer = document.querySelector('.grid.grid-cols-2.gap-3.mb-2');
  const cards = document.querySelectorAll('.stagger-item');

  // Get container info
  const containerComputed = window.getComputedStyle(staggerContainer);
  const containerInfo = {
    display: containerComputed.display,
    visibility: containerComputed.visibility,
    height: containerComputed.height,
    overflow: containerComputed.overflow,
    opacity: containerComputed.opacity,
    classList: Array.from(staggerContainer.classList),
    hasDataHide: staggerContainer.hasAttribute('data-hide'),
    vIfHidden: staggerContainer.style.display === 'none'
  };

  // Get detailed info for first 3 cards
  const cardsInfo = Array.from(cards).slice(0, 3).map((card, i) => {
    const computed = window.getComputedStyle(card);
    const rect = card.getBoundingClientRect();

    return {
      index: i,
      // Positioning
      position: {
        top: rect.top,
        left: rect.left,
        width: rect.width,
        height: rect.height,
        isOffScreen: rect.top < 0 || rect.left < 0 || rect.top > window.innerHeight
      },
      // Visibility styles
      visibility: {
        opacity: computed.opacity,
        visibility: computed.visibility,
        display: computed.display,
        transform: computed.transform,
        zIndex: computed.zIndex
      },
      // Inline styles
      inlineStyle: card.getAttribute('style'),
      // Parent chain visibility
      parentOpacity: window.getComputedStyle(card.parentElement).opacity,
      parentDisplay: window.getComputedStyle(card.parentElement).display
    };
  });

  return {
    containerInfo,
    cardsInfo,
    totalCards: cards.length
  };
}

