() => {
  // Find all elements with stagger-item class
  const cards = document.querySelectorAll('.stagger-item');

  if (cards.length === 0) {
    return { error: 'No .stagger-item elements found in DOM' };
  }

  // Get the parent container
  const firstCard = cards[0];
  const container = firstCard.parentElement;

  // Check if container exists and get its styles
  const containerStyles = container ? window.getComputedStyle(container) : null;

  return {
    totalCards: cards.length,
    containerExists: !!container,
    containerTag: container?.tagName,
    containerClasses: container ? Array.from(container.classList) : [],
    containerStyles: containerStyles ? {
      display: containerStyles.display,
      visibility: containerStyles.visibility,
      opacity: containerStyles.opacity,
      height: containerStyles.height,
      overflow: containerStyles.overflow
    } : null,
    // Check first card
    firstCardInfo: {
      rect: firstCard.getBoundingClientRect(),
      computed: {
        opacity: window.getComputedStyle(firstCard).opacity,
        display: window.getComputedStyle(firstCard).display,
        visibility: window.getComputedStyle(firstCard).visibility,
        height: window.getComputedStyle(firstCard).height
      },
      inlineStyle: firstCard.getAttribute('style')
    }
  };
}

