() => {
  const cards = document.querySelectorAll('.stagger-item');
  const container = cards[0]?.parentElement;

  if (!container) return { error: 'No container' };

  // Get ALL CSS rules that match this element
  const sheets = Array.from(document.styleSheets);
  const matchingRules = [];

  for (const sheet of sheets) {
    try {
      const rules = Array.from(sheet.cssRules || []);
      for (const rule of rules) {
        if (rule.selectorText && container.matches(rule.selectorText)) {
          matchingRules.push({
            selector: rule.selectorText,
            opacity: rule.style.opacity || 'not set',
            cssText: rule.cssText.substring(0, 200)
          });
        }
      }
    } catch (e) {
      // CORS or other errors
    }
  }

  return {
    matchingCSSRules: matchingRules,
    computedOpacity: window.getComputedStyle(container).opacity,
    hasOpacityInline: container.style.opacity,
    dataAttributes: Array.from(container.attributes).filter(a => a.name.startsWith('data-')).map(a => a.name)
  };
}

