input.new_string:
const theaterRef = ref<HTMLElement>()
const orbRef = ref<InstanceType<typeof AiOrb> | null>(null)
const bulbRefs = ref<HTMLElement[]>([])
const sparkleRefs = ref<HTMLElement[]>([])
const cardRefs = ref<HTMLElement[]>([])

// Clamp utility
const clamp = (value: number, min: number, max: number) => Math.min(Math.max(value, min), max)

// Base design tokens
const BASE_ORB_SIZE = 120
const BASE_ORB_PADDING = 16
const BASE_ORBIT_RADIUS = 120
const BASE_SPARKLE_OFFSET = 45
const BASE_LIGHT_BULB_SIZE = 32
const BASE_SPARKLE_SIZE = 20
const MAX_CONTENT_WIDTH = 800
const MIN_SCALE = 0.35
const MAX_SCALE = 1.4
const BASE_CONTAINER_HEIGHT = 384

// Responsive observation
const responsiveAnimation = useAnimation(theaterRef, {
  responsive: {
    enabled: true,
    baselineWidth: MAX_CONTENT_WIDTH,
    minScale: MIN_SCALE,
    maxScale: MAX_SCALE
  },
  respectEnvironment: true
})

const fallbackDimensions = computed(() => ({
  width: MAX_CONTENT_WIDTH,
  height: BASE_CONTAINER_HEIGHT,
  scale: 1,
  aspectRatio: MAX_CONTENT_WIDTH / BASE_CONTAINER_HEIGHT
}))

const dimensions = responsiveAnimation.responsive?.dimensions ?? fallbackDimensions

// Layout computation for responsive scaling
const brainstormLayout = computed(() => {
  const rawWidth = dimensions.value.width || MAX_CONTENT_WIDTH
  const contentWidth = Math.max(300, Math.min(rawWidth, MAX_CONTENT_WIDTH))
  const scale = clamp(contentWidth / MAX_CONTENT_WIDTH, MIN_SCALE, MAX_SCALE)

  return {
    scale,
    contentWidth,
    containerHeight: Math.round(BASE_CONTAINER_HEIGHT * scale),
    orbSize: Math.round(BASE_ORB_SIZE * scale),
    orbPadding: Math.round(BASE_ORB_PADDING * scale),
    orbitRadius: Math.round(BASE_ORBIT_RADIUS * scale),
    sparkleOffset: Math.round(BASE_SPARKLE_OFFSET * scale),
    lightBulbSize: Math.round(BASE_LIGHT_BULB_SIZE * scale),
    sparkleSize: Math.round(BASE_SPARKLE_SIZE * scale)
  }
})

// Light bulb positions (circular orbit) - now responsive
const lightBulbs = computed(() => [
  { angle: 0, radius: brainstormLayout.value.orbitRadius },    // Right
  { angle: 90, radius: brainstormLayout.value.orbitRadius },   // Top
  { angle: 180, radius: brainstormLayout.value.orbitRadius },  // Left
  { angle: 270, radius: brainstormLayout.value.orbitRadius }   // Bottom
])

// Sparkle positions (random around orb) - now responsive
const sparkles = computed(() => {
  const offset = brainstormLayout.value.sparkleOffset
  return [
    { x: `${offset}px`, y: `${-offset * 0.67}px` },
    { x: `${-offset}px`, y: `${offset * 0.56}px` },
    { x: `${offset * 1.11}px`, y: `${offset * 0.78}px` },
    { x: `${-offset}px`, y: `${-offset}px` }
  ]
})

// Idea cards floating up
const ideaCards = [
  { text: 'Summer Campaign' },
  { text: 'Product Launch' },
  { text: 'Behind Scenes' }
]

