const getPostOrbitStyle = (index: number) => {
  const totalPosts = scheduledPosts.length

  if (!totalPosts) {
    return {
      left: '50%',
      top: '50%',
      transform: 'translate(-50%, -50%)'
    }
  }

  // Position posts at their scheduled hour position
  const post = scheduledPosts[index]

  // Calculate angle in radians: hour 12 = -90deg (top), hour 3 = 0deg (right), hour 6 = 90deg (bottom), hour 9 = 180deg (left)
  const hourAngleDeg = ((post.time % 12) / 12) * 360 - 90
  const hourAngleRad = (hourAngleDeg * Math.PI) / 180

  // Orbit radius: clock max radius ~128px + gap ~72px = 200px
  const radius = 200

  // Calculate x,y coordinates from center
  const x = Math.round(Math.cos(hourAngleRad) * radius * 100) / 100
  const y = Math.round(Math.sin(hourAngleRad) * radius * 100) / 100

  return {
    left: `calc(50% + ${x}px)`,
    top: `calc(50% + ${y}px)`,
    '--animation-delay': `${index * 0.2}s`,
    '--initial-x': `${x * 0.5}px`,
    '--initial-y': `${y * 0.5}px`,
    '--final-x': `${x}px`,
    '--final-y': `${y}px`
  }
}

