import json
from pathlib import Path
from typing import Dict, Any, List, Optional


class Config:
    """Configuration manager"""

    def __init__(self, config_path: Path):
        self.config_path = config_path
        self._config = self._load_config()

    def _load_config(self) -> Dict[str, Any]:
        """Load configuration from JSON file"""
        if not self.config_path.exists():
            return self._default_config()

        with open(self.config_path, 'r') as f:
            return json.load(f)

    def _default_config(self) -> Dict[str, Any]:
        """Default configuration"""
        return {
            "version": "1.0.0",
            "projects": {},
            "topics": {},
            "settings": {
                "decay_threshold_days": 90,
                "auto_archive_stale": False,
                "max_insights_per_load": 20,
                "default_usefulness_score": 0.5
            },
            "git": {
                "auto_commit": True,
                "commit_message_prefix": "[Second Brain]"
            }
        }

    def save(self):
        """Save configuration to file"""
        with open(self.config_path, 'w') as f:
            json.dump(self._config, f, indent=2)

    def get_project(self, name: str) -> Optional[Dict[str, Any]]:
        """Get project configuration"""
        return self._config.get('projects', {}).get(name)

    def get_topic(self, name: str) -> Optional[Dict[str, Any]]:
        """Get topic configuration"""
        topics = self._config.get('topics', {})

        # Direct match
        if name in topics:
            return topics[name]

        # Check aliases
        for topic_name, topic_config in topics.items():
            aliases = topic_config.get('aliases', [])
            if name in aliases:
                return topic_config

        return None

    def resolve_topic(self, name: str) -> Optional[str]:
        """Resolve topic name from alias"""
        topics = self._config.get('topics', {})

        # Direct match
        if name in topics:
            return name

        # Check aliases
        for topic_name, topic_config in topics.items():
            aliases = topic_config.get('aliases', [])
            if name in aliases:
                return topic_name

        return None

    def get_all_topics(self) -> List[str]:
        """Get all topic names"""
        return list(self._config.get('topics', {}).keys())

    def get_setting(self, key: str, default: Any = None) -> Any:
        """Get a setting value"""
        return self._config.get('settings', {}).get(key, default)

    def get_git_config(self) -> Dict[str, Any]:
        """Get git configuration"""
        return self._config.get('git', {})

    def detect_project_from_path(self, file_path: str) -> Optional[str]:
        """Detect project name from file path"""
        for project_name, project_config in self._config.get('projects', {}).items():
            project_path = Path(project_config.get('path', '')).expanduser()
            file_path_obj = Path(file_path).expanduser()

            if str(file_path_obj).startswith(str(project_path)):
                return project_name

        return None

    @property
    def decay_threshold_days(self) -> int:
        """Get decay threshold in days"""
        return self.get_setting('decay_threshold_days', 90)

    @property
    def max_insights_per_load(self) -> int:
        """Get max insights per load"""
        return self.get_setting('max_insights_per_load', 20)

    @property
    def auto_commit(self) -> bool:
        """Get auto commit setting"""
        return self.get_git_config().get('auto_commit', True)

    @property
    def commit_message_prefix(self) -> str:
        """Get commit message prefix"""
        return self.get_git_config().get('commit_message_prefix', '[Second Brain]')

