#!/usr/bin/env python3
"""
NATE_SECOND_BRAIN MCP Server

Exposes knowledge management capabilities as MCP tools for any AI agent.
"""

import os
import sys
import asyncio
from pathlib import Path
from typing import Any, Optional

# Add parent directory to path for imports
sys.path.insert(0, str(Path(__file__).parent))

from mcp.server import Server
from mcp.server.stdio import stdio_server
from mcp.types import Tool, TextContent

from utils.database import Database
from utils.config import Config
from utils.git_ops import GitOps
from utils.gemini import GeminiExtractor


# Constants
BRAIN_DIR = Path(os.getenv('BRAIN_DIR', Path.home() / 'NATE_SECOND_BRAIN'))
DB_PATH = BRAIN_DIR / 'knowledge.db'
CONFIG_PATH = BRAIN_DIR / 'config.json'

# Initialize components
config = Config(CONFIG_PATH)
git = GitOps(BRAIN_DIR)
gemini = GeminiExtractor()

# Create MCP server
app = Server("nate-second-brain")


@app.list_tools()
async def list_tools() -> list[Tool]:
    """List available MCP tools"""
    return [
        Tool(
            name="extract_insights",
            description="Extract insights from planning documents using AI analysis",
            inputSchema={
                "type": "object",
                "properties": {
                    "file_path": {
                        "type": "string",
                        "description": "Path to file or folder to extract insights from"
                    },
                    "project": {
                        "type": "string",
                        "description": "Project name (auto-detected if not provided)"
                    },
                    "topic": {
                        "type": "string",
                        "description": "Topic to categorize insights under"
                    },
                    "auto_approve": {
                        "type": "boolean",
                        "default": False,
                        "description": "Auto-save all insights without review"
                    }
                },
                "required": ["file_path"]
            }
        ),
        Tool(
            name="load_insights",
            description="Load topic insights into agent's context",
            inputSchema={
                "type": "object",
                "properties": {
                    "topic": {
                        "type": "string",
                        "description": "Topic to load insights for (e.g., 'animation', 'workflows')"
                    },
                    "project": {
                        "type": "string",
                        "description": "Filter by project (optional)"
                    },
                    "max_results": {
                        "type": "number",
                        "default": 20,
                        "description": "Maximum number of insights to return"
                    }
                },
                "required": ["topic"]
            }
        ),
        Tool(
            name="search_insights",
            description="Full-text search across all insights",
            inputSchema={
                "type": "object",
                "properties": {
                    "query": {
                        "type": "string",
                        "description": "Search query"
                    },
                    "category": {
                        "type": "string",
                        "enum": ["project", "reference", "documentation"],
                        "description": "Filter by category (optional)"
                    },
                    "topic": {
                        "type": "string",
                        "description": "Filter by topic (optional)"
                    },
                    "max_results": {
                        "type": "number",
                        "default": 20,
                        "description": "Maximum number of results"
                    }
                },
                "required": ["query"]
            }
        ),
        Tool(
            name="archive_file",
            description="Archive planning documents to Second Brain",
            inputSchema={
                "type": "object",
                "properties": {
                    "file_path": {
                        "type": "string",
                        "description": "Path to file to archive"
                    },
                    "extract_first": {
                        "type": "boolean",
                        "default": True,
                        "description": "Extract insights before archiving"
                    },
                    "permanent_delete": {
                        "type": "boolean",
                        "default": False,
                        "description": "Permanently delete instead of archiving"
                    }
                },
                "required": ["file_path"]
            }
        ),
        Tool(
            name="get_stats",
            description="Get statistics about the knowledge base",
            inputSchema={
                "type": "object",
                "properties": {}
            }
        )
    ]


@app.call_tool()
async def call_tool(name: str, arguments: Any) -> list[TextContent]:
    """Handle tool calls"""

    if name == "extract_insights":
        return await handle_extract_insights(arguments)

    elif name == "load_insights":
        return await handle_load_insights(arguments)

    elif name == "search_insights":
        return await handle_search_insights(arguments)

    elif name == "archive_file":
        return await handle_archive_file(arguments)

    elif name == "get_stats":
        return await handle_get_stats(arguments)

    else:
        raise ValueError(f"Unknown tool: {name}")


async def handle_extract_insights(args: dict) -> list[TextContent]:
    """Extract insights from a file"""
    file_path = Path(args['file_path']).expanduser()
    project = args.get('project') or config.detect_project_from_path(str(file_path))
    topic = args.get('topic')
    auto_approve = args.get('auto_approve', False)

    if not file_path.exists():
        return [TextContent(
            type="text",
            text=f"Error: File not found: {file_path}"
        )]

    try:
        # Extract insights using Gemini
        insights = gemini.extract_from_file(file_path, project=project)

        if not insights:
            return [TextContent(
                type="text",
                text="No insights found in the file."
            )]

        # If auto-approve, save directly
        if auto_approve:
            with Database(DB_PATH) as db:
                saved_ids = []
                for insight in insights:
                    insight_id = db.insert_insight({
                        **insight,
                        'source_file': str(file_path),
                        'git_commit': git.get_current_commit()
                    })
                    saved_ids.append(insight_id)

            # Commit to git
            if config.auto_commit:
                git.commit_and_push(
                    f"Auto-extracted insights from {file_path.name}",
                    prefix=config.commit_message_prefix
                )

            return [TextContent(
                type="text",
                text=f"✅ Extracted and saved {len(insights)} insights automatically.\n\n" +
                     "\n".join([f"• {i['title']}" for i in insights])
            )]

        # Otherwise, return for review
        review_text = f"Found {len(insights)} insights for review:\n\n"
        for i, insight in enumerate(insights, 1):
            review_text += f"{i}. **{insight['title']}**\n"
            review_text += f"   Category: {insight.get('category', 'unknown')}\n"
            review_text += f"   Topic: {insight.get('topic', 'unknown')}\n"
            review_text += f"   Tags: {', '.join(insight.get('tags', []))}\n\n"

        review_text += "\nTo save these insights, use auto_approve=true or save manually via database."

        return [TextContent(type="text", text=review_text)]

    except Exception as e:
        return [TextContent(
            type="text",
            text=f"Error extracting insights: {str(e)}"
        )]


async def handle_load_insights(args: dict) -> list[TextContent]:
    """Load insights by topic"""
    topic = args['topic']
    project = args.get('project')
    max_results = args.get('max_results', 20)

    # Resolve topic alias
    resolved_topic = config.resolve_topic(topic) or topic

    with Database(DB_PATH) as db:
        insights = db.load_insights_by_topic(
            resolved_topic,
            project_context=project,
            limit=max_results
        )

        if not insights:
            return [TextContent(
                type="text",
                text=f"No insights found for topic: {topic}"
            )]

        # Update usage tracking
        db.update_last_used(
            [i['id'] for i in insights],
            project_context=project
        )

    # Format insights
    output = f"# {resolved_topic.title()} Insights ({len(insights)})\n\n"

    for insight in insights:
        output += f"## {insight['title']}\n\n"
        output += f"{insight['content']}\n\n"

        if insight.get('tags'):
            output += f"**Tags:** {insight['tags']}\n"
        output += f"**Source:** {insight['source_file']}\n"
        output += "\n---\n\n"

    return [TextContent(type="text", text=output)]


async def handle_search_insights(args: dict) -> list[TextContent]:
    """Search insights"""
    query = args['query']
    category = args.get('category')
    topic = args.get('topic')
    max_results = args.get('max_results', 20)

    with Database(DB_PATH) as db:
        results = db.search_insights(
            query,
            category=category,
            topic=topic,
            limit=max_results
        )

        if not results:
            return [TextContent(
                type="text",
                text=f"No results found for: {query}"
            )]

    # Format results
    output = f"# Search Results for '{query}' ({len(results)})\n\n"

    for result in results:
        output += f"## {result['title']}\n\n"
        output += f"{result['content']}\n\n"
        output += f"**Category:** {result['category']} | "
        output += f"**Topic:** {result.get('topic', 'N/A')} | "
        output += f"**Source:** {result['source_file']}\n"
        output += "\n---\n\n"

    return [TextContent(type="text", text=output)]


async def handle_archive_file(args: dict) -> list[TextContent]:
    """Archive a file"""
    file_path = Path(args['file_path']).expanduser()
    extract_first = args.get('extract_first', True)
    permanent_delete = args.get('permanent_delete', False)

    if not file_path.exists():
        return [TextContent(
            type="text",
            text=f"Error: File not found: {file_path}"
        )]

    messages = []

    # Extract insights first if requested
    if extract_first and not permanent_delete:
        extract_result = await handle_extract_insights({
            'file_path': str(file_path),
            'auto_approve': True
        })
        messages.extend(extract_result)

    # Archive or delete
    if permanent_delete:
        file_path.unlink()
        messages.append(TextContent(
            type="text",
            text=f"🗑️  Permanently deleted: {file_path.name}"
        ))
    else:
        # Move to archive
        project = config.detect_project_from_path(str(file_path))
        archive_dir = BRAIN_DIR / 'archive' / (project or 'general') / 'plans'
        archive_dir.mkdir(parents=True, exist_ok=True)

        archive_path = archive_dir / file_path.name
        file_path.rename(archive_path)

        # Git commit
        if config.auto_commit:
            git.commit_and_push(
                f"Archived {file_path.name}",
                prefix=config.commit_message_prefix
            )

        messages.append(TextContent(
            type="text",
            text=f"📦 Archived to: {archive_path.relative_to(BRAIN_DIR)}"
        ))

    return messages


async def handle_get_stats(args: dict) -> list[TextContent]:
    """Get knowledge base statistics"""
    with Database(DB_PATH) as db:
        stats = db.get_stats()

    output = "# NATE_SECOND_BRAIN Statistics\n\n"
    output += f"**Total Insights:** {stats['total_insights']}\n"
    output += f"**Total Relationships:** {stats['total_relationships']}\n"
    output += f"**Total Topics:** {stats['total_topics']}\n\n"

    if stats.get('by_category'):
        output += "## By Category\n\n"
        for category, count in stats['by_category'].items():
            output += f"- {category}: {count}\n"

    return [TextContent(type="text", text=output)]


async def main():
    """Run the MCP server"""
    async with stdio_server() as (read_stream, write_stream):
        await app.run(
            read_stream,
            write_stream,
            app.create_initialization_options()
        )


if __name__ == "__main__":
    asyncio.run(main())

