interface BusEmitOptions {
  force?: boolean
}

const busEmitSafe = (eventName: string, data?: unknown, options?: BusEmitOptions) => {
  const shouldForce = options?.force === true

  if (!isFloatingAnimating.value || shouldForce) {
    if (data !== undefined) {
      bus.emit(eventName, data)
    } else {
      bus.emit(eventName)
    }
  }
}

// Unified animation orchestration using useAnimation with sequence feature
const animation = useAnimation(conductorRef, {
  autoplay: {
    threshold: 0.1,
    enabled: computed(() => isVisible.value && !isFloatingAnimating.value),
    includeNavigation: true
  },
  respectEnvironment: true,
  events: {
    busName: 'conductor',
    emitOnStart: 'orchestrate-start',
    emitOnStop: 'stop-all'
  },
  sequence: {
    elements: computed(() => {
      const mode = props.mode
      if (mode === 'unison') {
        // Return all providers for unison mode (activate all at once)
        return validatedProviders.value
      } else {
        // Return one provider at a time for rotation mode
        return validatedProviders.value
      }
    }),
    interval: computed(() => props.cycleDuration * 1000),
    mode: computed(() => props.mode === 'rotation' ? 'loop' : 'unison'),
    onActivate: (provider, index, context) => {
      // Emit pathway activation event for child components
      busEmitSafe('pathway-activate', {
        index,
        provider,
        delay: 0,
        mode: props.mode
      })

      // Emit chat bubble cycle start at the beginning of each cycle
      if (props.showConductorCommand && index === 0 && context.elapsedInCycle < 50) {
        busEmitSafe('bubble-cycle-start')
      }
    },
    onCycleComplete: () => {
      emit('animationComplete', 'orchestration')
      emit('animationStart', 'orchestration')
    }
  },
  lifecycle: {
    afterPlay: () => {
      orbRef.value?.startPulse()
      emit('animationStart', 'orchestration')
      bus.emit('orchestrate-start', {
        mode: props.mode,
        cycleDuration: props.cycleDuration
      })
    },
    afterStop: () => {
      orbRef.value?.stopPulse()
      busEmitSafe('stop-all', undefined, { force: true })
    }
  }
})

// Expose animation controls for external use
const orchestrateAnimation = () => animation.play()
const stopAnimation = () => animation.stop()

