---
import Layout from "../layouts/Layout.astro";
import HeroSection from "@vue/views/home/HeroSection.vue";
import SocialProofBar from "@vue/views/home/SocialProofBar.vue";
import ValuePropositionSection from "@vue/views/home/ValuePropositionSection.vue";
import ProvidersSection from "@vue/views/home/ProvidersSection.vue";
import RepostingSection from "@vue/views/home/RepostingSection.vue";
import WorkflowsSection from "@vue/views/home/WorkflowsSection.vue";
import TestimonialsSection from "@vue/views/home/TestimonialsSection.vue";
import FaqSection from "@vue/views/home/FaqSection.vue";
import PricingSection from "@vue/views/home/PricingSection.vue";
import CTASection from "@vue/views/home/CTASection.vue";
import HowItWorksSection from "@vue/views/home/HowItWorksSection.vue";
import DocsModal from "@vue/modals/DocsModal.vue";
import TheaterPreview from "@vue/views/home/TheaterPreview.vue";
import { ENABLED_PROVIDERS } from "@/constants/providerConstants";
import { getCollection } from 'astro:content';
import { docCategories } from '@/content/docs/categories';
import type { DocSummary } from '@/types/docs';

const providers = ENABLED_PROVIDERS.filter((provider) => provider !== "facebook_pages" && provider !== "google");

// Fetch doc summaries for the modal without shipping full markdown bodies
const docsCollection = await getCollection('docs');
const docSummaries: DocSummary[] = docsCollection.map((doc) => ({
  slug: doc.slug,
  title: doc.data.title,
  description: doc.data.description,
  category: doc.data.category,
  order: doc.data.order ?? null,
  tags: doc.data.tags,
  relatedSections: doc.data.relatedSections,
  lastUpdated: doc.data.lastUpdated ? doc.data.lastUpdated.toISOString() : null,
  author: doc.data.author ?? null,
}));
---

<Layout title="Home" navVariant="landing">
    <div class="landing-bg">
      <!-- One consistent, page-wide background -->
      <div class="landing-bg-layer" aria-hidden="true"></div>

      <main class="relative z-10">
        <HeroSection client:load />
        <SocialProofBar client:idle />
        <ValuePropositionSection client:idle />
        <HowItWorksSection client:idle />
        <ProvidersSection client:idle providers={providers} />
        <RepostingSection client:idle />
        <TheaterPreview client:idle />
        <WorkflowsSection client:idle />
        <TestimonialsSection client:visible />
        <FaqSection client:visible />
        <PricingSection client:visible />
        <div id="cta">
          <CTASection client:visible />
        </div>
      </main>
    </div>

    <!-- Docs Modal -->
    <DocsModal client:idle docs={docSummaries} categories={docCategories} />


    <style>
      .landing-bg {
        position: relative;
      }

      /* Extend background behind header/footer while scrolling with content */
      .landing-bg .landing-bg-layer {
        position: absolute;
        /* Pull up behind header (navbar is ~64px tall) */
        top: -64px;
        left: 0;
        right: 0;
        /* Push down past footer to ensure coverage on long pages */
        bottom: -245px;
        z-index: 0;
        pointer-events: none;

        /* Using design token gradients - matches original layered radial design */
        background:
          var(--gradient-hero-radial-1),
          var(--gradient-hero-radial-2),
          var(--gradient-hero-radial-3),
          var(--gradient-hero-radial-4),
          var(--gradient-hero-radial-5),
          var(--gradient-hero-base);

        /* Static background - no animation for better performance */
        background-size: 100% 100%;
      }

      /* Noise texture overlay to prevent color banding */
      .landing-bg .landing-bg-layer::after {
        content: '';
        position: absolute;
        inset: 0;
        background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' /%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.03'/%3E%3C/svg%3E");
        opacity: 0.5;
        mix-blend-mode: overlay;
        pointer-events: none;
      }

      /* Animation removed for performance - was causing GPU overhead */

      /* Keep content above the background */
      .landing-bg main { position: relative; z-index: 1; }
      footer { position: relative; z-index: 20; }

      /* Make sections transparent and remove their full-bleed background overlays */
      .landing-bg section { background-color: transparent !important; }
      /* Only hide backgrounds inside landing content, not global header/footer */
      .landing-bg main > section > .absolute.inset-0 { display: none !important; }

      /* Specifically ensure CTA section has no extra absolute layers/shapes */
      .landing-bg main > #cta > .absolute { display: none !important; }
    </style>
  </Layout>

