   146→  name: string;
   147→  description: string;
   148→  monthlyPrice: number;
   149→  yearlyPrice: number;
   150→  features: string[];
   151→  limits: any;
   152→  billingKey: string;
   153→  buttonText: string;
   154→  isPopular: boolean;
   155→};
   156→
   157→// Billing state
   158→const isYearly = ref(false);
   159→
   160→// Contact modal state
   161→const showContactModal = ref(false);
   162→
   163→// Local loading/error state
   164→const isLoading = ref(true);
   165→const error = ref<string | null>(null);
   166→
   167→const yearlyDiscountPercentage = computed(() => {
   168→  return Math.round((1 - YEARLY_DISCOUNT) * 100);
   169→});
   170→
   171→// Transform unified pricing tier to the format expected by PricingCard
   172→const transformUnifiedTier = (tier: any): TransformedTier => {
   173→  console.log("Transforming tier:", tier);
   174→  // Generate features list from tier attributes
   175→  const features: string[] = [];
   176→  
   177→  // Connections
   178→  if (tier.connectionsMax === null) {
   179→    features.push("Unlimited Connected Accounts");
   180→  } else {
   181→    features.push(`Up to ${tier.connectionsMax} Connected Accounts`);
   182→  }
   183→  
   184→  // Teams
   185→  features.push(`${tier.teamsMax} Team${tier.teamsMax > 1 ? 's' : ''}`);
   186→  
   187→  // Users
   188→  features.push(`${tier.usersMax} User${tier.usersMax > 1 ? 's' : ''}`);
   189→  
   190→  // Storage
   191→  if (tier.storageMax >= 1024) {
   192→    const gb = Math.floor(tier.storageMax / 1024);
   193→    const remaining = tier.storageMax % 1024;
   194→    if (remaining === 0) {
   195→      features.push(`${gb} GB Storage Limit`);
   196→    } else {
   197→      features.push(`${gb}.${Math.round(remaining / 102.4)} GB Storage Limit`);
   198→    }
   199→  } else {
   200→    features.push(`${tier.storageMax} MB Storage Limit`);
   201→  }
   202→  
   203→  // AI Credits
   204→  if (tier.aiCreditsMax > 0) {
   205→    features.push(`${tier.aiCreditsMax} AI Credits`);
   206→  }
   207→  
   208→  // Executions
   209→  if (tier.unlimitedExecutions) {
   210→    features.push("Unlimited Workflow Executions");
   211→  }
   212→  
   213→  // Additional features for higher tiers
   214→  if (tier.billingKey !== 'free') {
   215→    features.push("Basic Analytics");
   216→    features.push("24/7 Automated Support");
   217→  }
   218→  
   219→  if (tier.billingKey === 'creator' || tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
   220→    features.push("Priority Support");
   221→  }
   222→  
   223→  if (tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
   224→    features.push("Advanced Analytics");
   225→  }
   226→  
   227→  // Generate limits object for backwards compatibility
   228→  const limits = {
   229→    connections: tier.connectionsMax,
   230→    teams: tier.teamsMax,
   231→    users: tier.usersMax,
   232→    storage: tier.storageMax,
   233→    aiCredits: tier.aiCreditsMax,
   234→    chatEnabled: tier.chatEnabled,
   235→    encodingEnabled: tier.encodingEnabled,
   236→    unlimited: tier.unlimitedExecutions,
   237→  };
   238→
   239→  return {
   240→    name: tier.name,
   241→    description: tier.description,
   242→    monthlyPrice: tier.monthlyPrice,
   243→    yearlyPrice: tier.yearlyPrice,
   244→    features: features,
   245→    limits: limits,
   246→    billingKey: tier.billingKey,
   247→    buttonText: tier.buttonText || "Get Started",
   248→    isPopular: tier.isPopular || false,
   249→  };
   250→};
   251→
   252→// Computed pricing data from unified pricing tiers
   253→const pricingData = computed(() => {
   254→  console.log("pricingData computed - unifiedPricingTiers.value:", unifiedPricingTiers.value);
   255→  
   256→  if (!unifiedPricingTiers.value || unifiedPricingTiers.value.length === 0) {
   257→    console.warn("No unified pricing tiers available");
   258→    return {
   259→      title: "Simple, Transparent Pricing",
   260→      subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
   261→      tiers: [] as TransformedTier[],
   262→    };
   263→  }
   264→  
   265→  const transformedTiers: TransformedTier[] = unifiedPricingTiers.value.map(transformUnifiedTier);
   266→  console.log("Transformed tiers:", transformedTiers);
   267→  
   268→  return {
   269→    title: "Simple, Transparent Pricing",
   270→    subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
   271→    tiers: transformedTiers,
   272→  };
   273→});
   274→
   275→// Fetch pricing tiers using the composable
   276→const fetchPricingTiers = async () => {
   277→  try {
   278→    isLoading.value = true;
   279→    error.value = null;
   280→    
   281→    console.log("Starting to load pricing tiers...");
   282→    // Explicitly request only visible tiers
   283→    const tiers = await loadPricingTiers(true);
   284→    console.log("Loaded pricing tiers (should be filtered):", tiers);
   285→    
   286→    // Client-side filtering as backup to ensure only visible tiers are shown
   287→    const visibleTiers = tiers.filter(tier => {
   288→      // Check if isVisibleOnHomepage is not explicitly false
   289→      const isVisible = tier.isVisibleOnHomepage !== false;
   290→      if (!isVisible) {
   291→        console.log(`Filtering out tier "${tier.name}" - isVisibleOnHomepage: ${tier.isVisibleOnHomepage}`);
   292→      }
   293→      return isVisible;
   294→    });
   295→    
   296→    console.log("Final visible tiers after client-side filtering:", visibleTiers);
   297→    console.log("unifiedPricingTiers.value:", unifiedPricingTiers.value);
   298→    
   299→    if (billingError.value) {
   300→      console.error("Billing error:", billingError.value);
   301→      error.value = billingError.value;
   302→    }
   303→    
   304→    if (!visibleTiers || visibleTiers.length === 0) {
   305→      console.warn("No visible pricing tiers available");
   306→      error.value = "No pricing plans available";
   307→    }
   308→  } catch (err) {
   309→    console.error("Failed to fetch pricing tiers:", err);
   310→    error.value = err instanceof Error ? err.message : "Failed to load pricing data";
   311→  } finally {
   312→    isLoading.value = false;
   313→  }
   314→};
   315→
   316→// Animation handling for pricing cards
   317→const hasBeenVisible = ref<boolean[]>(Array(4).fill(false)); // 3 main tiers + 1 enterprise
   318→
   319→// Set initial visibility after a short delay for a nice entrance effect
   320→setTimeout(() => {
   321→  hasBeenVisible.value = Array(4).fill(true);
   322→}, 100);
   323→
   324→// Helper functions to organize tiers for display
   325→const getMainTiers = (): TransformedTier[] => {
   326→  if (!pricingData.value.tiers) return [];
   327→  
   328→  // Filter out enterprise tier and get the 3 main tiers
   329→  const nonEnterpriseTiers = pricingData.value.tiers.filter(tier => 
   330→    tier.billingKey !== 'enterprise'
   331→  );
   332→  
   333→  // Sort by price (free first, then ascending by price)
   334→  return nonEnterpriseTiers.sort((a, b) => {
   335→    // Free tier always comes first
   336→    if (a.billingKey === 'free') return -1;
   337→    if (b.billingKey === 'free') return 1;
   338→    
   339→    // Sort others by monthly price
   340→    return a.monthlyPrice - b.monthlyPrice;
   341→  }).slice(0, 3); // Take only the first 3 tiers
   342→};
   343→
   344→// Fetch data on mount
   345→onMounted(() => {
   346→  fetchPricingTiers();
   347→});
   348→</script>
