"""Git operations for NATE_SECOND_BRAIN"""

import subprocess
from pathlib import Path
from typing import Optional, List


class GitOps:
    """Git operations wrapper"""

    def __init__(self, repo_path: Path):
        self.repo_path = repo_path

    def _run_git(self, args: List[str], check: bool = True) -> subprocess.CompletedProcess:
        """Run a git command"""
        return subprocess.run(
            ['git'] + args,
            cwd=self.repo_path,
            capture_output=True,
            text=True,
            check=check
        )

    def get_current_commit(self) -> str:
        """Get current git commit hash"""
        try:
            result = self._run_git(['rev-parse', '--short', 'HEAD'])
            return result.stdout.strip()
        except subprocess.CalledProcessError:
            return 'unknown'

    def get_full_commit(self) -> str:
        """Get full git commit hash"""
        try:
            result = self._run_git(['rev-parse', 'HEAD'])
            return result.stdout.strip()
        except subprocess.CalledProcessError:
            return 'unknown'

    def add(self, files: List[str] = None):
        """Add files to staging"""
        if files is None:
            files = ['.']
        self._run_git(['add'] + files)

    def commit(self, message: str, prefix: Optional[str] = None):
        """Create a commit"""
        full_message = f"{prefix} {message}" if prefix else message
        self._run_git(['commit', '-m', full_message])

    def push(self, remote: str = 'origin', branch: str = 'main'):
        """Push to remote"""
        self._run_git(['push', remote, branch])

    def get_status(self) -> str:
        """Get git status"""
        result = self._run_git(['status', '--short'])
        return result.stdout

    def has_changes(self) -> bool:
        """Check if there are uncommitted changes"""
        status = self.get_status()
        return bool(status.strip())

    def get_file_commit(self, file_path: str) -> str:
        """Get the commit hash for a specific file"""
        try:
            result = self._run_git(['log', '-n', '1', '--format=%h', '--', file_path])
            return result.stdout.strip() or 'unknown'
        except subprocess.CalledProcessError:
            return 'unknown'

    def commit_and_push(
        self,
        message: str,
        files: List[str] = None,
        prefix: Optional[str] = None,
        remote: str = 'origin',
        branch: str = 'main'
    ):
        """Add, commit, and push in one operation"""
        if not self.has_changes():
            return

        self.add(files)
        self.commit(message, prefix)
        self.push(remote, branch)

    def get_remote_url(self) -> Optional[str]:
        """Get remote repository URL"""
        try:
            result = self._run_git(['config', '--get', 'remote.origin.url'])
            return result.stdout.strip()
        except subprocess.CalledProcessError:
            return None

    def is_repo(self) -> bool:
        """Check if directory is a git repository"""
        try:
            self._run_git(['rev-parse', '--git-dir'])
            return True
        except subprocess.CalledProcessError:
            return False

