python3 -c "
import json
with open('/Users/natedamstra/.claude.json', 'r') as f:
    config = json.load(f)

# Find all paths to nate-second-brain
import json

def find_key(obj, target_key, path=''):
    if isinstance(obj, dict):
        for key, value in obj.items():
            new_path = f'{path}.{key}' if path else key
            if key == target_key:
                print(f'Found at: {new_path}')
                print(json.dumps(value, indent=2)[:500])
            find_key(value, target_key, new_path)
    elif isinstance(obj, list):
        for i, item in enumerate(obj):
            find_key(item, target_key, f'{path}[{i}]')

find_key(config, 'nate-second-brain')
" 2>&1 | head -30

