() => {
  // Find the hero section - typically the first main section after header
  const heroSection = document.querySelector('section') || document.querySelector('[class*="hero"]') || document.querySelector('main > div:first-child');

  if (!heroSection) return { error: 'Hero section not found' };

  // Get the outer HTML
  const html = heroSection.outerHTML;

  // Get computed styles for the section and its key children
  const getComputedStylesForElement = (el) => {
    const styles = window.getComputedStyle(el);
    const relevantProps = [
      'display', 'flex-direction', 'justify-content', 'align-items', 
      'padding', 'margin', 'background', 'background-color', 'background-image',
      'color', 'font-size', 'font-weight', 'line-height', 'text-align',
      'width', 'height', 'max-width', 'min-height', 'gap',
      'border', 'border-radius', 'box-shadow'
    ];

    const computedStyles = {};
    relevantProps.forEach(prop => {
      const value = styles.getPropertyValue(prop);
      if (value) computedStyles[prop] = value;
    });

    return computedStyles;
  };

  return {
    html: html.substring(0, 10000), // Limit to first 10k chars
    htmlLength: html.length,
    sectionStyles: getComputedStylesForElement(heroSection),
    className: heroSection.className,
    id: heroSection.id
  };
}

