input.function:
() => {
  // Try multiple selectors to find the hero section
  const selectors = [
    'div[class*="hero"]',
    'div[class*="Hero"]',
    '#hero',
    '.hero-section',
    'main > div:first-of-type',
    'body > div:first-of-type > div:first-of-type',
    '[data-elementor-type="wp-page"] > div:first-of-type section:first-of-type'
  ];

  let heroSection = null;

  for (const selector of selectors) {
    heroSection = document.querySelector(selector);
    if (heroSection) break;
  }

  // If still not found, look for a div containing the main heading
  if (!heroSection) {
    const h1 = document.querySelector('h1');
    if (h1) {
      heroSection = h1.closest('div[class]') || h1.closest('section');
    }
  }

  if (!heroSection) {
    return { 
      error: 'Could not find hero section',
      bodyClasses: document.body.className,
      firstDivClasses: document.querySelector('body > div')?.className
    };
  }

  return {
    success: true,
    className: heroSection.className,
    id: heroSection.id,
    tagName: heroSection.tagName,
    htmlPreview: heroSection.outerHTML.substring(0, 500)
  };
}

