input.function:
() => {
  // Find the hero section by looking for the main h1
  const h1 = Array.from(document.querySelectorAll('h1')).find(el => 
    el.textContent.includes('Save Time')
  );

  if (!h1) return { error: 'Hero heading not found' };

  // Get the parent section container
  const heroSection = h1.closest('[data-op3-element-type="section"]') || h1.closest('section');

  if (!heroSection) return { error: 'Hero section container not found' };

  // Extract all stylesheets
  const stylesheets = Array.from(document.styleSheets)
    .map(sheet => {
      try {
        return {
          href: sheet.href,
          rules: Array.from(sheet.cssRules || []).map(rule => rule.cssText).join('\n')
        };
      } catch (e) {
        return { href: sheet.href, error: 'CORS blocked' };
      }
    })
    .filter(s => s.rules);

  // Get inline styles
  const inlineStyles = Array.from(document.querySelectorAll('style'))
    .map(s => s.textContent);

  return {
    html: heroSection.outerHTML,
    dataAttributes: {
      uuid: heroSection.getAttribute('data-op3-uuid'),
      type: heroSection.getAttribute('data-op3-element-type'),
      gid: heroSection.getAttribute('data-op3-gid')
    },
    stylesheetCount: stylesheets.length,
    inlineStyleCount: inlineStyles.length,
    characterCount: heroSection.outerHTML.length
  };
}

