() => {
  const heading = Array.from(document.querySelectorAll('h2')).find(el => 
    el.textContent.includes('650,000+')
  );
  const section = heading.closest('[data-op3-element-type="section"]');

  const getStyles = (el, label) => {
    if (!el) return { label, styles: null };
    const computed = window.getComputedStyle(el);
    const key = ['font-family', 'font-size', 'font-weight', 'line-height', 'color', 'text-align', 'margin', 'padding', 'background-color', 'display'];
    const styles = {};
    key.forEach(prop => {
      const val = computed.getPropertyValue(prop);
      if (val) styles[prop] = val;
    });
    return { label, styles };
  };

  const h2all = section.querySelectorAll('h2');

  return {
    section: getStyles(section, 'Section Container'),
    heading650k: getStyles(h2all[0], 'Stat Number (650,000+)'),
    headingCreators: getStyles(h2all[1], 'Stat Label (Creators trust Repurpose)'),
    testimonialText: getStyles(section.querySelector('p'), 'Testimonial Quote'),
    button: getStyles(section.querySelector('a'), 'CTA Button'),
    h1: getStyles(section.querySelector('h1'), 'Main Heading')
  };
}

