input.function:
() => {
  const heading = Array.from(document.querySelectorAll('h2')).find(el => 
    el.textContent.includes('650,000+')
  );
  const section = heading.closest('[data-op3-element-type="section"]');

  // Find all row elements
  const rows = section.querySelectorAll('[data-op3-element-type="row"]');

  return {
    totalRows: rows.length,
    structure: Array.from(rows).slice(0, 5).map((row, idx) => {
      const columns = row.querySelectorAll('[data-op3-element-type="column"]');
      return {
        rowIndex: idx,
        columnCount: columns.length,
        content: Array.from(columns).map(col => {
          const h1 = col.querySelector('h1');
          const h2 = col.querySelector('h2');
          const testimonial = col.querySelector('[data-op3-element-type="testimonial"]');
          const button = col.querySelector('a');

          return {
            hasH1: !!h1,
            h1Text: h1?.textContent.substring(0, 50),
            hasH2: !!h2,
            h2Text: h2?.textContent.substring(0, 50),
            hasTestimonial: !!testimonial,
            hasButton: !!button,
            buttonText: button?.textContent.trim()
          };
        })
      };
    })
  };
}

