### Variant 3: Accessible & Optimized

**Philosophy**: Maximum accessibility, performance optimized, comprehensive ARIA support.

```vue
<script setup lang="ts">
import { ref, onMounted, computed } from 'vue'
import { Sun, Moon } from 'lucide-vue-next'

interface ThemeToggleProps {
  /** Position of the toggle button */
  position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left'
  /** Size variant */
  size?: 'sm' | 'md' | 'lg'
  /** Show visual label alongside icon */
  showLabel?: boolean
}

const props = withDefaults(defineProps<ThemeToggleProps>(), {
  position: 'top-right',
  size: 'md',
  showLabel: false
})

const theme = ref<'light' | 'dark'>('light')
const mounted = ref(false)
const isTransitioning = ref(false)

const positionClasses = computed(() => {
  const positions = {
    'top-right': 'top-[var(--spacing-4)] right-[var(--spacing-4)]',
    'top-left': 'top-[var(--spacing-4)] left-[var(--spacing-4)]',
    'bottom-right': 'bottom-[var(--spacing-4)] right-[var(--spacing-4)]',
    'bottom-left': 'bottom-[var(--spacing-4)] left-[var(--spacing-4)]'
  }
  return positions[props.position]
})

const buttonClasses = computed(() => {
  const sizeMap = {
    sm: 'p-[var(--spacing-2)]',
    md: 'p-[var(--spacing-3)]',
    lg: 'p-[var(--spacing-4)]'
  }
  return sizeMap[props.size]
})

const iconSize = computed(() => {
  const sizeMap = { sm: 16, md: 20, lg: 24 }
  return sizeMap[props.size]
})

const ariaLabel = computed(() => 
  theme.value === 'light' 
    ? 'Switch to dark mode' 
    : 'Switch to light mode'
)

const currentThemeLabel = computed(() =>
  theme.value === 'light' ? 'Light' : 'Dark'
)

onMounted(() => {
  mounted.value = true
  initializeTheme()
  listenToSystemThemeChanges()
})

const initializeTheme = () => {
  const savedTheme = localStorage.getItem('theme') as 'light' | 'dark' | null
  const systemPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches

  theme.value = savedTheme || (systemPrefersDark ? 'dark' : 'light')
  applyTheme(theme.value)
}

const listenToSystemThemeChanges = () => {
  const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)')

  mediaQuery.addEventListener('change', (e) => {
    // Only auto-update if user hasn't set a preference
    if (!localStorage.getItem('theme')) {
      theme.value = e.matches ? 'dark' : 'light'
      applyTheme(theme.value)
    }
  })
}

const applyTheme = (newTheme: 'light' | 'dark') => {
  const root = document.documentElement

  if (newTheme === 'dark') {
    root.classList.add('dark')
  } else {
    root.classList.remove('dark')
  }

  localStorage.setItem('theme', newTheme)

  // Announce to screen readers
  announceThemeChange(newTheme)
}

const announceThemeChange = (newTheme: 'light' | 'dark') => {
  const announcement = `Theme changed to ${newTheme} mode`
  const liveRegion = document.createElement('div')
  liveRegion.setAttribute('role', 'status')
  liveRegion.setAttribute('aria-live', 'polite')
  liveRegion.className = 'sr-only'
  liveRegion.textContent = announcement
  document.body.appendChild(liveRegion)

  setTimeout(() => {
    document.body.removeChild(liveRegion)
  }, 1000)
}

const toggleTheme = () => {
  if (isTransitioning.value) return

  isTransitioning.value = true
  theme.value = theme.value === 'light' ? 'dark' : 'light'
  applyTheme(theme.value)

  setTimeout(() => {
    isTransitioning.value = false
  }, 300)
}

const handleKeyDown = (event: KeyboardEvent) => {
  if (event.key === 'Enter' || event.key === ' ') {
    event.preventDefault()
    toggleTheme()
  }
}
</script>

<template>
  <button
    v-if="mounted"
    @click="toggleTheme"
    @keydown="handleKeyDown"
    type="button"
    :class="[
      'fixed z-50',
      positionClasses,
      buttonClasses,
      'rounded-[var(--radius-full)]',
      'bg-[var(--color-background)] dark:bg-[var(--color-card)]',
      'border-2 border-[var(--color-border)]',
      'text-[var(--color-foreground)]',
      'hover:bg-[var(--color-accent-50)] dark:hover:bg-[var(--color-accent-950)]',
      'transition-all duration-300 ease-in-out',
      'focus-ring-accent',
      'shadow-[var(--shadow-md)] hover:shadow-[var(--shadow-lg)]',
      'active:scale-95',
      'disabled:opacity-50 disabled:cursor-not-allowed',
      showLabel ? 'flex items-center gap-[var(--spacing-2)] px-[var(--spacing-4)]' : ''
    ]"
    :aria-label="ariaLabel"
    :aria-pressed="theme === 'dark'"
    :disabled="isTransitioning"
    role="switch"
  >
    <Transition
      enter-active-class="transition-all duration-300 ease-out"
      leave-active-class="transition-all duration-300 ease-in"
      enter-from-class="opacity-0 rotate-90 scale-50"
      leave-to-class="opacity-0 -rotate-90 scale-50"
      mode="out-in"
    >
      <Sun 
        v-if="theme === 'light'" 
        :size="iconSize" 
        class="text-[var(--color-accent-600)] flex-shrink-0"
        aria-hidden="true"
      />
      <Moon 
        v-else 
        :size="iconSize" 
        class="text-[var(--color-accent-500)] flex-shrink-0"
        aria-hidden="true"
      />
    </Transition>

    <span 
      v-if="showLabel" 
      class="text-body-sm font-medium"
      aria-hidden="true"
    >
      {{ currentThemeLabel }}
    </span>
  </button>
</template>

<style scoped>
/* Screen reader only utility */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}
</style>
```

