   174â†’
   175â†’  /* ===== TRANSITIONS ===== */
   176â†’  --transition-fast: 150ms ease;
   177â†’  --transition-base: 200ms ease;
   178â†’  --transition-medium: 300ms ease;
   179â†’  --transition-slow: 500ms ease;
   180â†’
   181â†’  /* Easing Functions */
   182â†’  --ease-linear: linear;
   183â†’  --ease-in: cubic-bezier(0.4, 0, 1, 1);
   184â†’  --ease-out: cubic-bezier(0, 0, 0.2, 1);
   185â†’  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
   186â†’  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   187â†’
   188â†’  /* ===== BACKGROUND GRADIENTS ===== */
   189â†’
   190â†’  /* Hero/Landing Gradients - Matching original multi-radial design */
   191â†’  --gradient-hero-base: linear-gradient(to bottom, #ffffff, #f8fafc);
   192â†’  --gradient-hero-radial-1: radial-gradient(1100px 1100px at 8% 8%, rgba(99, 102, 241, 0.12), transparent 60%);
   193â†’  --gradient-hero-radial-2: radial-gradient(1200px 1200px at 92% 92%, rgba(168, 85, 247, 0.11), transparent 60%);
   194â†’  --gradient-hero-radial-3: radial-gradient(1300px 900px at -10% 50%, rgba(45, 212, 191, 0.08), transparent 65%);
   195â†’  --gradient-hero-radial-4: radial-gradient(1300px 900px at 110% 45%, rgba(59, 130, 246, 0.08), transparent 65%);
   196â†’  --gradient-hero-radial-5: radial-gradient(900px 900px at 95% 10%, rgba(236, 72, 153, 0.06), transparent 65%);
   197â†’
   198â†’  /* Alternative simpler gradients */
   199â†’  --gradient-hero-vibrant: linear-gradient(135deg, var(--color-primary-100) 0%, var(--color-primary-200) 100%);
   200â†’  --gradient-hero-subtle: linear-gradient(180deg, var(--color-gray-50) 0%, var(--color-surface-primary) 100%);
   201â†’
   202â†’  /* Accent Gradients */
   203â†’  --gradient-accent-primary: linear-gradient(135deg, var(--color-primary-400) 0%, var(--color-primary-600) 100%);
   204â†’  --gradient-accent-purple: linear-gradient(135deg, #c084fc 0%, #7c3aed 100%);
   205â†’  --gradient-accent-warm: linear-gradient(135deg, #fb923c 0%, #f59e0b 100%);
   206â†’
   207â†’  /* Feature Section Gradients */
   208â†’  --gradient-feature-light: linear-gradient(to right, var(--color-primary-50), var(--color-gray-50));
   209â†’  --gradient-feature-card: linear-gradient(to bottom, var(--color-surface-primary), var(--color-surface-secondary));
   210â†’
   211â†’  /* CTA/Interactive Gradients */
   212â†’  --gradient-cta: linear-gradient(135deg, var(--color-primary-500) 0%, var(--color-primary-600) 100%);
   213â†’  --gradient-cta-hover: linear-gradient(135deg, var(--color-primary-600) 0%, var(--color-primary-700) 100%);
   214â†’
   215â†’  /* Optional Mesh Gradient Overlay for Extra Depth */
   216â†’  --gradient-mesh-overlay:
   217â†’    radial-gradient(at 20% 30%, rgba(124, 58, 237, 0.08) 0px, transparent 50%),
   218â†’    radial-gradient(at 80% 70%, rgba(59, 130, 246, 0.08) 0px, transparent 50%),
   219â†’    radial-gradient(at 40% 80%, rgba(236, 72, 153, 0.06) 0px, transparent 50%);
   220â†’
   221â†’  /* ===== TYPOGRAPHY ===== */
   222â†’  --font-family-sans: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
   223â†’  --font-family-mono: 'JetBrains Mono', 'SF Mono', Monaco, Inconsolata, 'Roboto Mono', 'Source Code Pro', monospace;
   224â†’  --font-family-emoji: 'Noto Color Emoji', 'Apple Color Emoji', 'Segoe UI Emoji', sans-serif;
   225â†’
   226â†’  /* Font Weights */
   227â†’  --font-weight-thin: 100;
   228â†’  --font-weight-extralight: 200;
   229â†’  --font-weight-light: 300;
   230â†’  --font-weight-normal: 400;
   231â†’  --font-weight-medium: 500;
   232â†’  --font-weight-semibold: 600;
   233â†’  --font-weight-bold: 700;
   234â†’  --font-weight-extrabold: 800;
   235â†’  --font-weight-black: 900;
   236â†’
   237â†’  /* Font Sizes */
   238â†’  --font-size-xs: 0.75rem;      /* 12px */
   239â†’  --font-size-sm: 0.875rem;     /* 14px */
   240â†’  --font-size-base: 1rem;       /* 16px */
   241â†’  --font-size-lg: 1.125rem;     /* 18px */
   242â†’  --font-size-xl: 1.25rem;      /* 20px */
   243â†’  --font-size-2xl: 1.5rem;      /* 24px */
   244â†’  --font-size-3xl: 1.875rem;    /* 30px */
   245â†’  --font-size-4xl: 2.25rem;     /* 36px */
   246â†’  --font-size-5xl: 3rem;        /* 48px */
   247â†’  --font-size-6xl: 3.75rem;     /* 60px */
   248â†’  --font-size-7xl: 4.5rem;      /* 72px */
   249â†’  --font-size-8xl: 6rem;        /* 96px */
   250â†’  --font-size-9xl: 8rem;        /* 128px */
   251â†’
   252â†’  /* Line Heights */
   253â†’  --line-height-none: 1;
   254â†’  --line-height-tight: 1.25;
   255â†’  --line-height-snug: 1.375;
   256â†’  --line-height-normal: 1.5;
   257â†’  --line-height-relaxed: 1.625;
   258â†’  --line-height-loose: 2;
   259â†’
   260â†’  /* ===== COMPONENT SIZES ===== */
   261â†’  --size-xs: 1.5rem;    /* 24px */
   262â†’  --size-sm: 2rem;      /* 32px */
   263â†’  --size-md: 2.5rem;    /* 40px */
   264â†’  --size-lg: 3rem;      /* 48px */
   265â†’  --size-xl: 3.5rem;    /* 56px */
   266â†’  --size-2xl: 4rem;     /* 64px */
   267â†’
   268â†’  /* Input Heights */
   269â†’  --input-height-sm: var(--size-sm);
   270â†’  --input-height-md: var(--size-md);
   271â†’  --input-height-lg: var(--size-lg);
   272â†’
   273â†’  /* ===== Z-INDEX SCALE ===== */
   274â†’  --z-index-behind: -1;
   275â†’  --z-index-base: 0;
   276â†’  --z-index-chat: 900;
   277â†’  --z-index-dropdown: 1000;
   278â†’  --z-index-notification: 1000;
   279â†’  --z-index-sticky: 1020;
   280â†’  --z-index-fixed: 1030;
   281â†’  --z-index-modal-backdrop: 1040;
   282â†’  --z-index-modal: 1050;
   283â†’  --z-index-popover: 1060;
   284â†’  --z-index-tooltip: 1070;
   285â†’  --z-index-toast: 1080;
   286â†’  --z-index-max: 2147483647;
   287â†’
   288â†’  /* ===== ANIMATION UTILITIES ===== */
   289â†’  --animation-pulse: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
   290â†’  --animation-bounce: bounce 1s infinite;
   291â†’  --animation-spin: spin 1s linear infinite;
   292â†’  --animation-ping: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
   293â†’  --animation-fade-in: fadeIn var(--transition-base);
   294â†’  --animation-fade-out: fadeOut var(--transition-base);
