   357â†’/* ===== KEYFRAME ANIMATIONS ===== */
   358â†’@keyframes fadeIn {
   359â†’  from {
   360â†’    opacity: 0;
   361â†’  }
   362â†’  to {
   363â†’    opacity: 1;
   364â†’  }
   365â†’}
   366â†’
   367â†’@keyframes fadeOut {
   368â†’  from {
   369â†’    opacity: 1;
   370â†’  }
   371â†’  to {
   372â†’    opacity: 0;
   373â†’  }
   374â†’}
   375â†’
   376â†’@keyframes slideInUp {
   377â†’  from {
   378â†’    opacity: 0;
   379â†’    transform: translateY(1rem);
   380â†’  }
   381â†’  to {
   382â†’    opacity: 1;
   383â†’    transform: translateY(0);
   384â†’  }
   385â†’}
   386â†’
   387â†’@keyframes slideInDown {
   388â†’  from {
   389â†’    opacity: 0;
   390â†’    transform: translateY(-1rem);
   391â†’  }
   392â†’  to {
   393â†’    opacity: 1;
   394â†’    transform: translateY(0);
   395â†’  }
   396â†’}
   397â†’
   398â†’@keyframes scaleIn {
   399â†’  from {
   400â†’    opacity: 0;
   401â†’    transform: scale(0.9);
   402â†’  }
   403â†’  to {
   404â†’    opacity: 1;
   405â†’    transform: scale(1);
   406â†’  }
   407â†’}
   408â†’
   409â†’@keyframes scaleOut {
   410â†’  from {
   411â†’    opacity: 1;
   412â†’    transform: scale(1);
   413â†’  }
   414â†’  to {
   415â†’    opacity: 0;
   416â†’    transform: scale(0.9);
   417â†’  }
   418â†’}
   419â†’
   420â†’@keyframes pulse {
   421â†’  0%, 100% {
   422â†’    opacity: 1;
   423â†’  }
   424â†’  50% {
   425â†’    opacity: 0.5;
   426â†’  }
   427â†’}
   428â†’
   429â†’@keyframes bounce {
   430â†’  0%, 100% {
   431â†’    transform: translateY(-25%);
   432â†’    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
   433â†’  }
   434â†’  50% {
   435â†’    transform: none;
   436â†’    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
   437â†’  }
   438â†’}
   439â†’
   440â†’@keyframes spin {
   441â†’  to {
   442â†’    transform: rotate(360deg);
   443â†’  }
   444â†’}
   445â†’
   446â†’@keyframes ping {
   447â†’  75%, 100% {
   448â†’    transform: scale(2);
   449â†’    opacity: 0;
   450â†’  }
   451â†’}
